vec3 trans(vec3 p){
    float m = 5.;
    vec3 q = mod(p, m) - m * .5;
    q.x += (rand(floor(p.yz * .2)) * 2. - 1.) * .4;
    q.y += (rand(floor(p.zx * .2)) * 2. - 1.) * .4;
    q.z += (rand((floor(p.xz * .2) + floor(p.yy * .2)) * .01) * 2. - 1.) * 3.9;
    // q.y += (rand(floor(p.xz * .2)) * 2. - 1.) * .4;
    return q;
}

float dist(vec3 p){
    return sdBox(trans(p), vec3(1.8, 1.8, .5));
}

vec3 getNormal(vec3 p){
    float d = 0.001;
    return normalize(vec3(
        dist(p + vec3(d, 0., 0.)) - dist(p + vec3(-d, 0., 0.)),
        dist(p + vec3(0., d, 0.)) - dist(p + vec3(0., -d, 0.)),
        dist(p + vec3(0., 0., d)) - dist(p + vec3(0., 0., -d))
    ));
}

vec3 rotateX(vec3 p, float th){
    return p * mat3(
        1, 0, 0,
        0, cos(th), -sin(th),
        0, sin(th), cos(th)
    );
}
vec3 rotateY(vec3 p, float th){
    return p * mat3(
        cos(th), 0, sin(th),
        0, 1, 0,
        -sin(th), 0, cos(th)
    );
}
vec3 rotateZ(vec3 p, float th){
    return p * mat3(
        cos(th), -sin(th), 0,
        sin(th), cos(th), 0,
        0, 0, 1
    );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (fragCoord * 2. - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec3 lightDir = normalize(vec3(.3, .5, -1.5));

    vec3 col = vec3(0.);

    vec3 screen = vec3(uv, 0.), cpos = vec3(0, 0, -.3);
    vec3 v = normalize(screen - cpos), p = cpos;

    p += vec3(0., 0., iTime);

    float d;

    for(int i = 0; i < 2000; i++){
        d = dist(p) * .2;
        p += v * d;
        if(d < 0.001) {
            vec3 normal = getNormal(p);
            col = vec3(1.) * max(0., dot(normal, lightDir));
            break;
        }
    }

    fragColor = vec4(col, 1.);
}

void mainImage( out vec4 fragData, in vec2 fragCoord ) {

  // Always initialize all variables
  fragData = vec4(0.);

  // Start all black
  vec3 color = vec3(0.);

  // A coordinate system for a 2D drawing, centered in frame, preserving aspect
  vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.y;

  // A soft circle
  color = mix(color, vec3(.2), clamp( 4.-5.*length(uv), 0., 1.));

  // Various sines
  for (float i=0.; i<8.; i++) {
    float h = i*.6;
    float k = exp2(i/8.);
    vec3 c = pow(.5+.5*cos(h-vec3(0,1,2)*2.094),vec3(2.));
    color += .2*c*max( 0., 1.-2.*abs( sin(k*uv.x+iTime/k)*k - uv.y )/k );
  }

  // Convert to Gamma=2.2 and output to screen
  fragData = vec4(pow(color, vec3(1./2.2)), 1.);
}

#define pi acos(-1.)

vec2 rand(vec2 uv){
    uv.x += 0.0004;
    uv.y += 0.0007;
    uv.x = floor(uv.x / 0.001) * 0.001;
    uv.y = floor(uv.y / 0.001) * 0.001;
    uv += vec2(13., 63.);
    uv.x = fract(sin(uv.x * 10. * uv.y * uv.y)  * 438.5453);
    uv.y = fract(cos(uv.x * uv.x * 10. * uv.y)  * 561.5453);
    return uv;
}

vec2 uvtransform(vec2 uv, float strength){
    float clsz = 0.01; // cellsize
    //vec2 nwuv = float(int(uv / clsz)) * clsz;
    int X = int(uv.x / clsz);
    int Y = int(uv.y / clsz);
    vec2 nwuv = vec2(float(X), float(Y)) * clsz;
    vec2 A = mix(rand(nwuv + vec2(0., 0.)  ), rand(nwuv + vec2(clsz, 0.)  ), (uv - nwuv).x / clsz);
    /*vec2 A = mix(
        rand(vec2(float(X), float(Y))), 
        rand(vec2(float(X + 1), float(Y))), 
        (uv - nwuv).x / clsz
    );*/
    vec2 B = mix(rand(nwuv + vec2(0., clsz)), rand(nwuv + vec2(clsz, clsz)), (uv - nwuv).x / clsz);
    vec2 ans = mix(A, B, (uv - nwuv).y / clsz);
    return uv + (ans - vec2(0.5)) * 2. * strength;
}

const float freq = 120.;

float rand(float x){
    return fract(sin(x) * 43758.5453);
}

float f(float x){
    x *= 100.;
    return (2. + sin(x / 10.)) / (2. + cos(x / 10.)) / 20.;
}

float g(float x){
    x *= 80.;
    return pow(
        (2. + sin(x / 10.)) / (2. + cos(x / 20.))
    	, 2.) /
        2. / 40.;
}

float strange_func(float x){
    float maxx = pow((exp(1.) - 1.), 0.1);
    x *= maxx;
    return (exp(1.) * log(pow(x, 10.) + 1.) / (pow(x, 10.) + 1.)) / 
           (exp(1.) * log(pow(maxx, 10.) + 1.) / (pow(maxx, 10.) + 1.));
}

float res(float x, float t){
    //x /= iResolution.x;
    return mix(f(x - 20. * t) + g(x + 30. * t), f(-20. * t) + g(30. * t), strange_func(abs(x - 0.5) * 2.));
}

vec4 grad(float x){
    //vec3 col = vec3(200., 0., 200.) / 256.;
    vec3 col = vec3(68., 27., 85.) / 256.;
    float cntrblur = 0.005;
    float bordblur = 0.05;
    if (abs(x) < cntrblur){
    	return vec4(mix(vec3(1.), col, abs(x) / cntrblur), 1.);
    } else {
        return vec4(mix(col, vec3(0.), (abs(x) - cntrblur) / bordblur), 1.);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    uv = uvtransform(uv, 0.01);

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float t = iTime / 100.;

    /*fragColor = vec4(
    	vec3(sign(mix(A, B, uv.x * 20. - floor(uv.x * 20.)) / 5. - uv.y)),
        1.
    );*/

    vec2 cent = vec2(iResolution.x / iResolution.y / 2., 0.5);
    float r = 0.1;
    vec2 cn = uv - cent;
    float ag = (atan(cn.y, cn.x) + pi) / pi / 2.;// + iTime / 1000.;
    float y = length(cn) - r;

    float outr = 0.3;

    float block = floor(ag * freq);
    float timeShift = floor(iTime * 8.) * 0.3;
    float A = rand(block + timeShift);
    float B = rand(mod(block + 1., freq) + timeShift);

    if (length(cn) < outr){
        fragColor = vec4(vec3(
            1. - smoothstep(-0.01, 0.01, y - res(ag, t))
        ), 1.);
    } else {
        if (res(ag, t) > 0.1){
            fragColor = grad(mix(A, B, ag * freq - block) / 20. - y + outr);
        }
    }

    // Output to screen
    //fragColor = vec4(col,1.0);
    /*fragColor = vec4(vec3(
        smoothstep(-0.01, 0.01, uv.y - res(uv.x, t))
    ), 1.);*/
}

float drawRectangle(vec2 uv, vec2 p, float width, float height) {
    float halfWidth = width / 2.0;
    float halfHeight = height/2.0;
    p.x -= halfWidth;
    p.y -= halfHeight;

	float horizontal = smoothstep(p.x, p.x+0.01, uv.x) - smoothstep(p.x + width, p.x + width + 0.01, uv.x); 
    float vertical = smoothstep(p.y, p.y+0.01, uv.y) - smoothstep(p.y + height, p.y + height + 0.01, uv.y); 


    return horizontal * vertical;

}

float drawCircle(vec2 uv, vec2 p, float radius, bool onHalf) {
    float dist = length(p - uv);

    float circle = smoothstep(radius, radius - 0.01, dist);
    if(onHalf) {
      p.y -= radius/2.0;
      return circle * drawRectangle(uv, p, radius * 2., radius);
    }

    return circle;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv -= 0.5;
    uv.x *= iResolution.x /iResolution.y;
    float eye1 = drawCircle(uv, vec2(0.1, 0.1), 0.05, false);
    float eye2 = drawCircle(uv, vec2(-0.1, 0.1), 0.05, false);
    float mouth = drawCircle(uv, vec2(0., -0.05), 0.1 + 0.1 * abs(cos(iTime)), true);
    float circle = drawCircle(uv, vec2(0, 0), 0.3, false);
    circle = max(0., circle - eye1 - eye2 - mouth);

    // Time varying pixel color
    vec3 col = vec3(1., 1., 0.) * circle;

    // Output to screen
    fragColor = vec4(col,1.0);
}

precision highp float;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 c = vec2((uv.x * 2.0 - 1.5) * iResolution.x/iResolution.y, (uv.y * 2.0 - 1.0));

    // Mandelbrot formula
    vec2 z = c;
    float iterations = 0.0;
    float maxIterations = 2000.0;
    const int maxii = 2000;

    for (int i = 0; i < maxii; i++) {
        float t = 2.0 * z.x * z.y + c.y;
        z.x = z.x * z.x - z.y * z.y + c.x;
        z.y = t;

        if (z.x * z.x + z.y * z.y > 4.0) {
            break;
        }

        iterations += 1.0;
    }

    if (iterations >= maxIterations) {
        fragColor = vec4(col,1.0);
    } else {
        fragColor = vec4(col*iterations,1.0);
    }
}

