vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float mandel(vec2 pos, vec2 c) {

    vec2 z = pos;
    for(int i = 0; i < 1000; i++) {
        z.xy = vec2(z.x*z.x-z.y*z.y,z.x*z.y*2.0f) + c;
    }

    return length(z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 u = fragCoord/iResolution.x;
	vec3 col = vec3(0.0f,0.0f,0.0f);
    vec2 uv = (fragCoord-vec2(iResolution.x/4.0f,iResolution.y/2.0f))/iResolution.x*8.0f;
    if(u.x < 0.5) {
        uv.x += -1.0f;
        col = hsv2rgb(vec3((mandel(uv,uv)+iTime)/10.0f,1.0f,1.0f));
    } else {
        uv.x += -4.0f;
        vec2 mouse = (iMouse.xy-vec2(iResolution.x/4.0f,iResolution.y/2.0f))/iResolution.x*8.0f;
        mouse.x += -1.0f;

        col = hsv2rgb(vec3((mandel(uv,mouse)+iTime)/10.0f,1.0f,1.0f));
    }

    fragColor = vec4(col,1.0);

}

// interp from triangle sides : see https://www.desmos.com/calculator/anchdwvynw

#define hue(v)  ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) ) // https://www.shadertoy.com/view/ll2cDc

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         V = u/R.y,
         M = iMouse.xy/R,
         U = V * (iMouse.x < 10. ? 5. : 20.*M.y),
         F = fract(U)*2.-1.,     // coords in tile in [-1,1]²
         I = mod(floor(U),2.);   // tile id in checker pattern

    float n = iMouse.x < 10. ? 10. : 20.*M.x,      // noise scale
          x = F.x, y = F.y, k=0., X,Y,Z, a,c, v, s = sqrt(2.);
                                                // --- settle phase at edges
#if 1                   // checker : 2pi turn around nodes
    a = I==vec2(0) ? abs(y) > abs(x) ? 4. : 0.  // phase along diagonals
                   : I.y==0. ? I.x : 3.-I.x;
    c = abs(y) < abs(x) ? I.y==0. ?  .5 : 2.5   // phase along tile border
                        : I.x==0. ? 3.5 : 1.5;
    a /= 4.; c /= 4.;
#else                   // no checker : 8pi turn around nodes
    float S = I.x==I.y ? 1. : 0.;
    a =  abs(y) > abs(x) ? S : 1.-S;            // phase along diagonals
    c = .5;                                     // phase along tile border
#endif
                                                // --- get triangle coordinates
    if ( abs(y) > abs(x) ) k=x,x=y,y=k;         // wrap to first quadrant (right triangle in tile)
    x = abs(x), 
    X = (x-y)/s, Y = (x+y)/s, Z = 1.-x;         // coords in triangle
    float A = Y*Z, B=X*Z, C=X*Y;                // --- interp within triangle
    v = ( a*A + a*B + c*C ) / (A+B+C) ;

    v += 1.*texture(iChannel0,(n*V+.5*0.)/iChannelResolution[0].x).x; // global perturbations
    O = hue(v); //return;
    // v = .5+.5*sin(30.*6.28*v);               // display isolines

    v = .5 + .5* sin( 150.* dot(V-.5, cos(.1*iTime+vec2(0,11))) + 6.28*v ); // phasor

    O *= vec4(v);

}

// Fork of "Length(gv)-rad and atan gv" by soundmasteraj. https://shadertoy.com/view/WtdXDf
// 2020-02-16 18:36:05

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y+.5;
	vec2 gv = vec2(uv.x * 5., -.25 + uv.y * 5.);
    float rad = .25 +.01 -cos(iTime)*.25+.25;
    float dist = length(gv*rad)-rad;

    vec3 col =atan(gv.x*uv.x*.5, gv.y*rad) + .618 *cos(iTime+dist+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
