float sceneSDF(vec3 samplePoint) {

    // we want to make the sphere bigger
    // we will do this by 
    //samplePoint *= vec3(sin(iTime), 1.0, cos(iTime));
    float result = heartShape(samplePoint, 0.2 * abs(sin(PI * iTime * 0.5 + samplePoint.y * 0.1)) + 0.6);
