vec2 MyFunc (vec3 stuff)
{
    mat3 sum = mat3(stuff.x + stuff, stuff.y + stuff, stuff.z + stuff);
    mat3 product = mat3(stuff.x * stuff, stuff.y * stuff, stuff.z * stuff);
    vec2 matchNum = vec2(0.0);
    for(int x = 0; x < 3; x++)
    {
		for(int y = 0; y < 3; y++)
    	{
            float match_s = sum[x][y];
            float match_p = product[x][y];
        	for(int x1 = 0; x1 < 3; x1++)
    		{
            	for(int y1 = 0; y1 < 3; y1++)
    			{
        			int myYead = 2;
                    if (abs(match_s - product[x1][y1]) < 0.1)
                    {
                        matchNum.x++;
                    }
                    if (abs(match_p - sum[x1][y1]) < 0.1)
                    {
                        matchNum.y++;
                    }
    }
    }
            matchNum--;
    }
    }
    return matchNum * matchNum;

}

vec3 spectrum(float x) {
    float pi = 3.14159;
    x = x * 2.1 - 0.555;
    vec4 v = vec4(clamp(x, -.6, 0.6), clamp(x, 0.05, 1.05), clamp(x, 0.65, 1.55), clamp(x, 1.16, 1.55));
    v += vec4(0.0, -0.55, -1.1, -1.35);
    v *= vec4(0.8, 1.0, 1.1, 2.5);
    v = (cos(v * v * pi * 4.) * 0.5 + 0.5);
    v.r += v.a * 0.5;
    return v.rgb;
}
