float smin( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*k*(1.0/4.0);
}

float GetDist(vec3 p) {
    float time = iTime*.4;
    float d;
    vec3 p2 = p;
    float floorDist = p2.y+(sin(p.z+sin(iTime)))*cos(p2.x)*.1+length(p)*.1;
    d = floorDist;
    for(int i = 0; i < 15; i++) {
        float size = float(i)*.1;
		float sphereDist= Sphere(p-vec3(sin(time*6.)*.4,cos(time*6.)*5.+2.,-80.+fract(-time*.15)*180.), size);
    	d = smin(sphereDist, d, .8);
        time +=.04;
    }
    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
	float d = 0.;
    for(int i = 0; i < 100; i++) {
    	float step = GetDist(ro + rd*d);
        d += step*.5;
        if(abs(d) < 0.0001 || d > 140.) break;
    }
    return d;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(0.001, 0.);
    vec3 normal = d - vec3(GetDist(p-e.xyy),
                           GetDist(p-e.yxy),
                           GetDist(p-e.yyx));
    normal = normalize(normal);
    return normal;
}

float GetLight(vec3 p){
	vec3 light = vec3(1.,7.,7.);
    vec3 normal = GetNormal(p);
    vec3 lightVec = normalize(light-p);
    float dif = max(0., dot(normal, lightVec))*(1.-max(0.,length(light-p)*.02));
    float shadow = RayMarch(p+normal*.002, lightVec);
    if(shadow < length(light-p)) dif *=clamp(shadow*.2, 0.,1.);
    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float time = iTime;
    vec2 uv = fragCoord/iResolution.xy;
	uv -= .5;
    uv.x *= iResolution.x/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.xy;
    mouse -= .5;
    vec3 col = vec3(0.);

    vec3 ro = vec3(4.*cos(mouse.x*6.),1.+mouse.y,4.*sin(mouse.x*6.));
    vec3 lookAt = vec3(0.,1.,3.);
    float zoom = 1.;
    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(forward, vec3(0.,1.,0.)));
    vec3 up = normalize(cross(right,forward));
    vec3 center = ro + forward * zoom;
    vec3 intersect = center + right*uv.x + up*uv.y;
    vec3 rd = intersect - ro;

    float d = RayMarch(ro, rd);
    float dif = GetLight(ro + rd *d);
    // Time varying pixel color
    col = vec3(dif);
	//col = vec3(st, 1.);
    // Output to screen
    fragColor = vec4(col,1.0);
}

//This parameter changes the magnification.
const float mag = 8.0;

const float GREEN = 0.0;
const float BLUE = 1.0;
const float GRAY = 2.0;
const float YELLOW = 3.0;
const float RED = 5.0;
const float WHITE = 6.0;
//this function is from https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p)
{
	float num_colors = 3.0;
    //set the number of colors to be randomly generated

    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33+iTime/100000.0);
    return floor(fract((p3.x + p3.y) * p3.z)*num_colors);
}


float magnify(vec2 fragCoord,float mag){
	return hash12(floor(fragCoord/pow(2.0,mag)));
}

bool is_above(vec2 fragCoord,float mag,float color){
	return magnify(fragCoord,mag) == color;
}
bool is_surrounded_by(vec2 fragCoord,float mag,float color){
	return magnify(fragCoord+vec2(0,1),mag) == color
        && magnify(fragCoord+vec2(1,0),mag) == color
        && magnify(fragCoord-vec2(0,1),mag) == color
        && magnify(fragCoord-vec2(1,0),mag) == color;
}

bool is_next_to(vec2 fragCoord,float mag,float color){
	return magnify(fragCoord+vec2(0,1),mag) == color
        || magnify(fragCoord+vec2(1,0),mag) == color
        || magnify(fragCoord-vec2(0,1),mag) == color
        || magnify(fragCoord-vec2(1,0),mag) == color;
}


vec3 get_color(float color){
    if(color == BLUE){
    	return vec3(0.149,0.141,0.912);
   	}
    else if(color == GREEN){
    	return vec3(0.000,0.833,0.224);
