    else if(color == RED){
    	return vec3(1.0,0.0,0.0);
   	}
    else if(color == WHITE){
    	return vec3(1.0,1.0,1.0);
   	}
    else if(color == GRAY){
    	return vec3(192.0,192.0,192.0)/255.0;
    }
    else if(color == YELLOW){
    	return vec3(1.0,1.0,0.0);
   	}
}

float iterations(float color1,in vec2 fragCoord,float mag){
    float mag1 = 0.0;
    while(mag1 < mag){
    bool modified = true;
    if(color1 == GREEN && is_next_to(fragCoord,mag1,BLUE)){
    	color1 = BLUE;
    }
    else if(color1 == BLUE && is_next_to(fragCoord,mag1,GRAY)){
        if(mag1 > 5.0){
        	color1 = YELLOW;
        }
        else{
        	color1 = GRAY;
        }
    }
    else if(color1 == GRAY && is_next_to(fragCoord,mag1,GREEN)){
        color1 = GREEN;
    }
    else if(color1 == YELLOW && is_next_to(fragCoord,mag1,GRAY)){
        color1 = GREEN;
    }
    else if(color1 == GRAY && is_next_to(fragCoord,mag1,BLUE) && is_next_to(fragCoord,mag1,GRAY)){
        color1 = WHITE;
    }

    mag1 += 1.0;
    }
    return color1;
}

