#define PI 3.141592
#define MAX_STEPS 250
#define MAX_DIST 250.0
#define SURF_DIST .001

#define WATER 0
#define SKY 1

#define LIGHT_POS vec3(0, 5, 25)

struct Obj {
	int type;
    float d;
    vec3 hitPos;
};

float rand(vec2 uv) {
    return fract(sin(dot(uv.xy, vec2(12.9898,78.233)))*
        43758.5453123);
}

// Value noise from the book of shader
float noise(in vec2 uv) {

    vec2 i = floor(uv);
    vec2 f = fract(uv);

    // Four corners in 2D of a tile
    float a = rand(i);
    float b = rand(i + vec2(1.0, 0.0));
    float c = rand(i + vec2(0.0, 1.0));
    float d = rand(i + vec2(1.0, 1.0));

    // Smooth
    vec2 u = f*f*(3.0-2.0*f);

    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

/*
 * Fractal Brownian Motion adds different iterations of noise (octaves) 
 * by incrementing the frequencies in regular steps and decrease the amplitude 
 * of the noise. It makes the noise more granular and add more fine details.
*/
float fbm(in vec2 uv, int octaves) {

    // Initial values
    float value = 0.0;
    float amplitude = .1;
    float frequency = 0.;

    // Loop of octaves
    for (int i = 0; i < octaves; i++) {
        value += amplitude * noise(uv);
        uv *= 2.0;
        amplitude *= 0.5;
    }
    return value;
}

// --- analytically triangle-filtered checkerboard by iq ---
vec3 pri(in vec3 x) {    
    vec3 h = fract(x/2.0)-0.5;
    return x*0.5 + h*(1.0-2.0*abs(h));
}

float checkersTextureGradTri(in vec3 p, in vec3 ddx, in vec3 ddy) {
    vec3 w = max(abs(ddx), abs(ddy)) + 0.01;       // filter kernel
    vec3 i = (pri(p+w)-2.0*pri(p)+pri(p-w))/(w*w); // analytical integral (box filter)
    return 0.1 - 0.5*i.x*i.y*i.z;                  // xor pattern
}

vec3 floorColor(in vec3 pos) {

	vec3 uvw = 1.2 * pos;

	// calc texture sampling footprint		
	vec3 ddx_uvw = dFdx( uvw ); 
    vec3 ddy_uvw = dFdy( uvw );
    vec3 color = vec3(checkersTextureGradTri(uvw, ddx_uvw, ddy_uvw));

    return color;
}

Obj getDistance(vec3 p) {	

    float height = fbm(vec2(p.x, p.z) + (iTime/3.), 2);
    float waterDist = p.y - height;

    Obj obj = Obj(WATER, waterDist, p);

    return obj;
}

// Basic raymarching loop
Obj rayMarch(vec3 ro, vec3 rd) {

    float t = 0.;

    Obj hitObj = Obj(SKY, t, ro + rd);
    for (int i = 0; i < MAX_STEPS; i++) {

        vec3 p = ro + rd*t;

        hitObj = getDistance(p);
        t += hitObj.d*0.35;

        // the ray has marched far enough but hit nothing. 
        // Render the pixel as a part of the sky.
        if (t > MAX_DIST) {
        	hitObj = Obj(SKY, t, p);
            break;
        }

        // the ray has marched close enough to an object
        if (abs(hitObj.d) < SURF_DIST) {
            hitObj = Obj(hitObj.type, t, p);
        	break;
        }

        hitObj.d = t;
    }

    return hitObj;
}

vec3 getWaterNormal(vec3 p) {
	float d = getDistance(p).d;

    // use offset samples to compute normal
    vec2 e = vec2(.5, 0);
    vec3 n = d - vec3(
        getDistance(p-e.xyy).d,
        getDistance(p-e.yxy).d,
        getDistance(p-e.yyx).d);

    return normalize(n);
}

// calculates the intersection of the given plane and ray
vec3 intersectPlane(vec3 ori, vec3 dir, vec3 planePos) {

    vec3 planeNormal = vec3(0, 1, 0);

	float a = dot(dir, planeNormal);

    float distToPlane = dot(ori-planePos, planeNormal);
    vec3 intersectPt = ori + dir*abs(distToPlane/a);

    return intersectPt;
}

// calculates specular lighting
float specular(vec3 n, vec3 l, vec3 rd, float s) {    
    float nrm = (s + 8.0) / (PI * 8.0);
    return pow(max(dot(reflect(rd, n), l),0.0), s) * nrm;
}

vec3 getSkyColor(vec3 rd, vec3 light) {

    vec3 col = vec3(0.4);
   	// sun
   	float sundot = clamp(dot(rd, normalize(light)),0.0,1.0);
   	// sun scatter
	col += 0.6*vec3(1.0,0.7,0.3) * pow(sundot, 8.0);
   	col += 0.25*vec3(1.0,0.7,0.4) * pow(sundot, 5.0);
	col += 0.25*vec3(1.0,0.8,0.6) * pow(sundot, 64.0);
	col += 0.2*vec3(1.0,0.8,0.6) * pow(sundot, 512.0);

    return col;
}

vec3 getWaterColor(vec3 p, vec3 n, vec3 rd, vec3 distToP) {  

    // Fresnel is an exponential that gets bigger when the angle between the water
    // surface normal and camera ray is smaller. It is used to calculate 
    // the amount of reflected vs. refracted light.
    float fresnel = 1.0 - max(dot(n, -rd), 0.0);
    fresnel = fresnel*fresnel*fresnel * 0.25;                        

    // Bounces the camera ray towards the sky and gets its color
    vec3 reflected = getSkyColor(reflect(rd, n), normalize(LIGHT_POS));    

    // Calculates refraction effect based on angle between the light and surface normal 
     vec3 refractDir = refract(rd, n, 1.0/1.3);
     vec3 refractedPos = intersectPlane(p, refractDir, vec3(0.0, -1.0, 0.0));
    // floor color at the position hit by the refracted ray
    vec3 refracted = floorColor(refractedPos);

    // Blend the refracted color with the reflected color based on our fresnel term
    vec3 color = mix(refracted, reflected, fresnel);

  	// Add specular hilight
    color += vec3(specular(n, normalize(LIGHT_POS), rd, 100.0));    

    return color;
}

float caustics(vec3 waterHitPos, vec3 lightPos) {

    vec3 ray = normalize(waterHitPos - lightPos);

    // Calculate the intersection of the light and the non-deformed water plane
    vec3 surfacePlaneNormal = vec3(0.0, 1.0, 0.0);    
    vec3 planePos = vec3(0, 0, 0);

    vec3 surfacePlaneHitPos = rayMarch(lightPos, ray).hitPos;
    float height = fbm(vec2(surfacePlaneHitPos.x, surfacePlaneHitPos.z) + (iTime/3.), 2);
    surfacePlaneHitPos += vec3(0., height, 0.);

    // Calculate the intersection of the refracted ray and bottom plane   
    vec3 refractDir = refract(ray, surfacePlaneNormal, 1.0/1.3);   
    vec3 bottomPlanePos = vec3(0.0, 1.0, 0.0);
    vec3 beforePos = intersectPlane(surfacePlaneHitPos, refractDir, bottomPlanePos);
  	beforePos += surfacePlaneHitPos;

  	// Calculate the intersection of the light and deformed water surface
    vec3 deformedPlaneHitPos = rayMarch(lightPos, ray).hitPos;
   	vec3 normal = getWaterNormal(deformedPlaneHitPos);

    // Calculate the intersection of the refracted ray and bottom plane   
    refractDir = refract(ray, normal, 1.0/1.3);
    vec3 afterPos = intersectPlane(deformedPlaneHitPos, refractDir, bottomPlanePos);
    afterPos += deformedPlaneHitPos;

    float beforeArea = length(dFdx(beforePos)) * length(dFdy(beforePos));
    float afterArea = length(dFdx(afterPos)) * length(dFdy(afterPos));
    return max(beforeArea / afterArea, .001);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    // normalize coordinates ranging from -0.5 to 0.5 and fix aspect ratio
    vec2 uv = (fragCoord - iResolution.xy*0.5)/iResolution.y;

    vec3 col = vec3(.05, .25, 0.5);   

    // camera
    vec3 ro = vec3(.5, 2, -8);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    Obj hitObj = rayMarch(ro, rd);   

    if (hitObj.type == SKY) {

		col = getSkyColor(rd, LIGHT_POS-vec3(0., 3, 0.));

    } else if (hitObj.type == WATER) {

   		vec3 normal = getWaterNormal(hitObj.hitPos);       
        col += getWaterColor(hitObj.hitPos, normal, rd, hitObj.hitPos - ro)*0.6;
    	col *= caustics(hitObj.hitPos, LIGHT_POS);
        float fogAmount = pow(1.0 - pow(rd.y,2.0), 200.0);
        col = mix(col, getSkyColor(rd, LIGHT_POS-vec3(0., 3, 0.)), fogAmount);     
    }

    col = pow(col,vec3(.454545));
 	fragColor = vec4(col,1.0);
}

