}

float remap(float v, float l0, float h0, float l1, float h1)
{
    return l0+(v-l1)*(h1-l1)/(h0-l0);
}

float lineRand(vec2 uv, float r)
{
    uv.x *= r;

    vec2 mouse = iMouse.xy/iResolution.xy;

    float rr = remap(r, 0., 20., 0., 1.);

    float frac = fract(rr*456123.);
    //float tm = remap(frac, 0., 1., 0., 2.);
    if(frac < 0.5)
    {
        uv.x -= iTime*(-frac*2.);
    }
    else
    {
        uv.x -= iTime*(frac*10.);
    }


    vec2 stuv = uv*vec2(20., 1.);

    vec2 pos = floor(stuv);

    float mult = round(pow(random(pos), 10.));

    //mult = tm;

    return mult;
}

float lineReveal(vec2 uv, float ca, float num)
{
    float rd = mix(0., 1., step(1.-uv.y, num/ca));
    float n = mix(0., 1., step(1.-uv.y, num-1./ca));

    return rd-n;
}
float rowReveal(vec2 uv, float ca, float num)
{
    float line = lineReveal(uv, ca, num);
    float line2 = lineReveal(uv, ca, num-1.);


    line-= line2;
    line *= 1.-uv.x*fract(1.-iTime*10.);

    return line;
}
float fill(vec2 uv, float ca, float num)
{
    float fill = 0.;
    fill += step(1.-uv.y, num/ca);

    return fill;
}

