#define CLOSE_D 0.01
#define MAX_D 100.
#define MAX_ITER 100
#define REFL_BOUNCES 4

mat2 rot(float a) {
    float s=sin(a),c=cos(a);
    return mat2(c,-s,s,c);
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sphere(vec3 p, float r) {
    return length(p) - r;
}

float hex( vec3 p, vec2 h )
{
  const vec3 k = vec3(-0.8660254, 0.5, 0.57735);
  p = abs(p);
  p.xz -= 2.0*min(dot(k.xy, p.xz), 0.0)*k.xy;
  vec2 d = vec2(
       length(p.xz-vec2(clamp(p.x,-k.z*h.x,k.z*h.x), h.x))*sign(p.z-h.x),
       p.y-h.y );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float room(vec3 p) {
    p.y -= 12.1;
    return 0.1-sdBox(p, vec3(12));      
}

//returns the vec2 with the lower x component
//used to handle distance in x and object id in y
vec2 xmin(vec2 a, vec2 b) {
    return a.x < b.x ? a : b;
}

float hit = 0.;
float objectNearCount=0.;
float sdf(vec3 p) {
    vec2 room = vec2(room(p), 0);
    float hexagon = hex(p-vec3(0,1.5,0),vec2(1.5,1.5));
    float spheres = min(
        min(
            sphere(p-vec3(4,2.+sin(iTime),0),1.),
            sphere(p-vec3(-4,2.+sin(iTime+3.14),0),1.)
        ),
        min(
            sphere(p-vec3(0,2.+sin(iTime+3.14/2.),4),1.),
            sphere(p-vec3(0,2.+sin(-iTime+3.14*1.5),-4),1.)
        )
    );
    vec2 objects = vec2(
        min(hexagon,spheres),
    	1	//object id
    );

    vec2 res = xmin(
        room,
        objects
    );
    hit = res.y;
    objectNearCount += hit;
    return res.x;
}

float rm(vec3 ro, vec3 rd, float max_d) {
    float td=0.;
    for(int i=0;i<MAX_ITER;i++) {
        float d = sdf(ro+rd*td);
        td += d;
        if (abs(d) < CLOSE_D || td > max_d) break;
    }
    return td;
}

vec3 norm(vec3 p) {
    vec2 e = vec2(.001,0);
    return normalize(sdf(p) - vec3(
        sdf(p - e.xyy),
        sdf(p - e.yxy),
        sdf(p - e.yyx)));
}

float light(vec3 p, vec3 rd) {
    float originalONC = objectNearCount;
    vec3 n = norm(p);

    int b=0;
    //If we hit something reflective (object id 1) and up to REFL_BOUNCES bounces:
    while (hit == 1. && b++<REFL_BOUNCES) {	
        rd = reflect(rd, n); //get a new ray direction
        p+=n*CLOSE_D*2.;     //pull out of the surface a bit so we're no longer intersecting
        p = p+rd*rm(p, rd, MAX_D);  //raymarch from the old position to the next hit
        n = norm(p);
    }

    vec3 lp = vec3(5,10,-5);
    vec3 ld = normalize(lp-p);
    float l = dot(n,ld);

    float baseColor = .1+.9*smoothstep(-.05,.05,sin((p.y+p.x)*2.));
    //reset the counter for how many iterations we are closer to the objects than the room
    objectNearCount = 0.;
    float max_d = length(lp-p);
    float lm = rm(p+n*CLOSE_D*2.,ld, max_d);
    if (lm < max_d) {
        l *= 0.1;
    }
   	l *= 1. * smoothstep(50.,10.,objectNearCount); //smooth shadows
    //l += (1.-l)*smoothstep(20.,30.,originalONC); //glow around the objects
    return l * baseColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.) / iResolution.y;

    vec3 ro = vec3(0,4.,-8);
    vec3 rd = normalize(vec3(uv,1.));

    rd.yz *= rot(0.25);
    if (iMouse.w < .5) {
        ro.zx *= rot(iTime*0.1);
        rd.zx *= rot(iTime*0.1);
    } else {
        ro.yz *= rot(-.5+(iMouse.y/iResolution.y) * 3.14/2.);
        rd.yz *= rot(-.5+(iMouse.y/iResolution.y) * 3.14/2.);
        ro.zx *= rot(iMouse.x/iResolution.x * 6.2);
        rd.zx *= rot(iMouse.x/iResolution.x * 6.2);
    }
    objectNearCount = 0.;
	float d = rm(ro,rd, MAX_D);

    vec3 col = vec3(light(ro+rd*d, rd));

    col *= smoothstep(100.,50.,d);

    fragColor = vec4(col,1.0);
}

#define RESOLUTION 64.
#define NOISESPEED 1.
#define NOISEBLEND 0.4
#define WAVESPEED 10.
#define WAVELENGTH 5.

float random(vec2 v) {
	return fract(sin(dot(v.xy, vec2(37.8479, 84.1047))) * 23479.34593785 + fract(iTime*NOISESPEED));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float t = iTime;

    vec2 uv = fragCoord.xy/iResolution.y;
    vec2 noiseUV = uv*RESOLUTION;
    noiseUV = floor(noiseUV);

    vec3 waveCol = vec3(sin(noiseUV.y/WAVELENGTH+t*WAVESPEED)*.5+.5);
    vec3 noiseCol = vec3(random(noiseUV));

    vec3 col = mix(waveCol, noiseCol, NOISEBLEND);
    fragColor = vec4(col, 1.);
}

/**
* @source SDF functions from http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
* @source 2D Texture Mapping method from https://www.shadertoy.com/view/MssGRl (user : https://www.shadertoy.com/user/asalga)
*/

#define PI 3.141592658
#define MAX_STEPS 200
#define MAX_DIST 10.
#define SURF_DIST 0.1

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdBox(vec3 p, vec3 b)
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float opUnion( float d1, float d2 )
{
	return min(d1,d2);
}

float opSubtraction( float d1, float d2 ) { 
    return max(-d1,d2); 
}

float distanceTree(vec3 pos) {
    float res = MAX_DIST;

    float skullhead = sdSphere(pos-vec3(  0.,   0., 5.), 2. );
    float leftEye 	= sdSphere(pos-vec3(-0.45, 0.15, 1.04), 2.+cos(iTime-130.)/300.);
    float rightEye 	= sdSphere(pos-vec3( 0.45, 0.15, 1.04), 2.+cos(iTime+130.)/300.);
    res = opSubtraction(leftEye, skullhead);
    res = opSubtraction(rightEye, res);
    float skullheadErase = sdSphere(pos-vec3(0., -10, 5.), 10. );
    res = opSubtraction(skullheadErase, res);

    float tooth = MAX_DIST;
    tooth = opUnion( tooth, sdBox(pos-vec3(-0.20, -0.4, 3.1), vec3(0.05)) );
    tooth = opUnion( tooth, sdBox(pos-vec3(-0.05, -0.4, 3.1), vec3(0.05)) );
    tooth = opUnion( tooth, sdBox(pos-vec3( 0.05, -0.4, 3.1), vec3(0.05)) );
    tooth = opUnion( tooth, sdBox(pos-vec3( 0.20, -0.4, 3.1), vec3(0.05)) );
    res = opUnion(res, tooth);

    return res;
}

vec3 GetNormal (vec3 p) {
    float epsilon = 0.00001;

    float dpx = (distanceTree(vec3(p.x+epsilon,p.y,p.z)) - distanceTree(vec3(p.x-epsilon,p.y,p.z))) / (2.*epsilon);
    float dpy = (distanceTree(vec3(p.x,p.y+epsilon,p.z)) - distanceTree(vec3(p.x,p.y-epsilon,p.z))) / (2.*epsilon);
    float dpz = (distanceTree(vec3(p.x,p.y,p.z+epsilon)) - distanceTree(vec3(p.x,p.y,p.z-epsilon))) / (2.*epsilon);

    return normalize(vec3(dpx, dpy, dpz));
}

vec4 TextureMapping(vec3 n) {
    vec3 northVector = vec3(0, 1, 0);
	vec3 eastVector  = vec3(1, 0, 0);

	float lat = acos(dot(northVector, n));
	float v = lat / PI;
	float u;

	float lon = (acos( dot( n, eastVector) / sin(lat))) / (2.0 * PI);
	if(dot(cross(northVector, eastVector), n) > 0.0){
		u = lon;
	}
	else{
		u = 1.0 - lon;
	}

	u -= iTime / 10.0;

    return texture(iChannel0, vec2(u, v));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // Init
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y; // put the origin in the middle
    vec3 finalColor = vec3(0.);

    // Setup Sphere Tracing
    vec3 ro = vec3(0., 0., -2.); // ray origin
    ro.xz += vec2(sin(iTime) / 3., cos(iTime) / 3.);
    vec3 rd = normalize(vec3(uv, 1.) - ro); // ray direction

    // Main loop of the sphere tracing
  	float d = 0.;
    vec3 p;
    int i;
    for(i=0; i < MAX_STEPS; i++) {
      p = ro + rd*d;
      d = distanceTree(p);
      if(d>MAX_DIST || d<SURF_DIST) break;
    }

    // If we hit something
    if (i < MAX_STEPS) {
        vec3 normal = GetNormal(p);

    	// Diffuse Lighting
        vec3 diffuseLightPos = vec3(3., 3., 1.);
        vec3 diffuseLightDir = normalize(diffuseLightPos - p);
        float diffuseLightFalloff = dot(diffuseLightDir, normal);

        // Specular Lighting
        vec3 specularLightPos = vec3(0., 50., 1.);
        // specularLightPos.xz += vec2(sin(iTime), cos(iTime)) * 20.;
        vec3 specularLightDir = normalize(specularLightPos - p);
        vec3 viewDir = normalize(ro - p);
        vec3 viewReflec = reflect(-viewDir, normal);
        float specularLightFalloff = dot(specularLightDir, normal);

        // Color
        vec3 ambientColor = vec3(0.2);
        vec3 diffuseColor = vec3(0.5, 0.7, 0.3);
        vec3 specularColor = vec3(1., 1.0, 1.0);

        // Texture
        vec4 t = TextureMapping(normal);

        finalColor = ambientColor
            + max(diffuseLightFalloff, 0.) * diffuseColor
            + max(specularLightFalloff, 0.) * specularColor
            * t.xyz;
    } else {
        // Manage background color here
        vec4 flag = texture(iChannel1, (uv+1.)*0.5);  

        vec3 mask = vec3(sin(2.*uv.x - 0.5*uv.y - iTime) + 1.) * 0.5;

        vec3 normal = vec3(0., 0., -1.);

    	// Diffuse Lighting
        vec3 diffuseLightPos = vec3(3.,1., -2.);
        vec3 diffuseLightDir = normalize(diffuseLightPos - vec3(uv, 0.));
        float diffuseLightFalloff = dot(diffuseLightDir, normal);

        finalColor = vec3(flag) * diffuseLightFalloff * mask;
    }

    fragColor = vec4(finalColor, 1.);
}

/**
 *Shader written by Joseph Adrian
 *2/18/2020
 *
 *My objective here was just to learn more about ray marching, GLSL, and implicit geometry
 *As it turns out, this method of rendering can do some nifty stuff.
 *
 */

const int MAX_STEPS = 250;
const float MAX_DIST = 100.0;
const float MIN_DIST = 0.0;
const float EPSILON = 0.0001;


//performs modulus division on a global 3D position vector and returns its position relative to the center of it's local subspace
//(the effect is similar to subdividing all of 3D space into regular chunks and making them all copies of the chunk centered about
//the origin, however the actual operation that takes place below just translates a vector based on the remainder when it is 
//divided bt the nearest multiple of a given displacement vector) ****i think**** i might be totally thinking about it wrong
vec3 repeat( vec3 pos, float offset )
{
    return mod(pos + offset * 0.5, offset) - offset * 0.5;
}

//signed distance function for a sphere
float sphereSDF(vec3 pos, float rad)
{
    return length(repeat(pos, 3.0)) - rad;
}

//signed distance function for a box
float boxSDF(vec3 pos, vec3 lwh)
{
    vec3 x = abs(pos) - lwh;
    return length(max(x,0.0)) + min(max(x.x,max(x.y,x.z)),0.0);
}

//signed distance function for the combination of all objects in the scene
float sceneSDF(vec3 pos)
{
    //vec3 lwh = vec3(0.20, 0.15, 0.45);
    return sphereSDF(pos, 0.5);
}

//returns the shortest distance from the camera to the closest surface
float sd2s(vec3 cam, vec3 marchDirection, float start, float end)
{
    float depth = start;
    for(int i = 0; i < MAX_STEPS; i++)
    {
        float dist = sceneSDF(cam + depth * marchDirection);
        if(dist < EPSILON){
            return depth;
        }
        depth+=dist;
        if(depth >= end){
            return end;
        }
    }
    return end;
}

//returns the direction vector for the ray that extends from this pixel
vec3 rayDirection(float fov, vec2 size, vec2 fragCoord)
{
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan( radians(fov) / 2.0);
    return normalize(vec3(xy,-z));
}

//estimate surface normal using the gradient of the signed distance function
vec3 approxNormal(vec3 p)
{
    return normalize(vec3(
        			sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
                    sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
                    sceneSDF(vec3(p.x, p.y, p.z + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
        			));

}
/**
 * cDiff = diffuse color
 * cSpec = specular color
 * cAmb = ambient color
 * a = alpha
 * lPos = light position
 * nct = intensity of light
 *
 *returns the shade of color that a light contributes to the surface at a given position
 *
 */
vec3 phongLightContrib(vec3 cDiff, vec3 cSpec, float a, vec3 pos, 
                       vec3 camPos, vec3 lPos, vec3 nct) {

    vec3 normal = approxNormal(pos);
    vec3 light = normalize(lPos - pos);
    vec3 view = normalize(camPos - pos);
    vec3 reflection = normalize(reflect(-light, normal));

    float dotLN = dot(light, normal);
    float dotRV = dot(reflection, view);

    //if dotLN is less than 0 then there is no light on this part of the surface
    if(dotLN < 0.0){
        return vec3(0.0, 0.0, 0.0);
    }

    //light reflecting away from camera, so only apply diffuse lighting
    if(dotRV < 0.0){
        return nct * (cDiff * dotLN);
    }
    return nct * (cDiff * dotLN + cSpec * pow(dotRV, a));


}

//calculates frag color after light sources are accounted for
vec3 phongLighting(vec3 cAmb, vec3 cDiff, vec3 cSpec, float a, vec3 pos, vec3 camPos){

    const vec3 AL = 0.5 * vec3(1.0, 1.0, 1.0);//AL = Ambient Light
    vec3 color = AL * cAmb;

    vec3 pL1 = vec3(4.0 * sin(iTime),//light 1 position
                    2.0,
                    4.0 * cos(iTime));
    vec3 nctL1 = vec3(0.4, 0.4, 0.4);//light 1 intensity

    color += phongLightContrib(cDiff, cSpec, a, pos, camPos, pL1, nctL1);


    vec3 pL2 = vec3(2.0 * sin(0.37 * iTime),//light 2 position
                    2.0 * cos(0.37 * iTime),
                    2.0);
    vec3 nctL2 = vec3(0.4, 0.4, 0.4); //light 2 intensity

    color += phongLightContrib(cDiff, cSpec, a, pos, camPos, pL2, nctL2);

    return color;
}


//main function
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float fov = 90.0;

    vec3 dir = rayDirection(fov, iResolution.xy, fragCoord);
	vec3 cam = vec3(0.0, 0.0, 5.0);
    float dist = sd2s(cam, dir, MIN_DIST, MAX_DIST);

    //case if ray does not hit a surface
    if(dist > MAX_DIST - EPSILON)
    {
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
        return;
    }
    vec3 point = cam + dist * dir;
    vec3 cAmb = vec3(0.3, 0.3, 0.5);
    vec3 cDiff = vec3(0.2, 0.2, 0.8);
    vec3 cSpec = vec3(1.0, 1.0, 1.0);
    float alpha = 10.0;

    vec3 color = phongLighting(cAmb, cDiff, cSpec, alpha, point, cam);

    fragColor = vec4(color, 1.0);
}

// Maximum number of iterations of the Mandelbrot sequence to check before deciding if a point is in the set.
// Has a high impact on both performance and accuracy.
const int MAX_ITER = 666;

// Point on which the zoom is centered
const vec2 REGION_MID = vec2(-0.774472, 0.124979);

// Zoom extremes
const float ZOOM_MIN  = 0.2;
const float ZOOM_MAX  = 50000.0;

// Total time in seconds for the entire zoom loop
const float ZOOM_TIME = 50.0;

// Colors used for the iteration count color cycle. Can use arbitrarily many.
const int NUM_COLORS = 3;
vec4 COLORS[NUM_COLORS] = vec4[](
    vec4(255.0, 237.0, 130.0, 255.0) / 255.0,
    vec4(253.0, 143.0, 19.4,  255.0) / 255.0,
    vec4(179.0, 29.0,  0.43,  255.0) / 255.0);

// Colors used for the interior of the set
vec4 INTERIOR_COLOR = vec4(0.1, 0.1, 0.1, 1.0);

// Supersampling offsets used when rendering. This 16 sample pattern taken from:
// http://docs.microsoft.com/en-us/windows/win32/api/d3d11/ne-d3d11-d3d11_standard_multisample_quality_levels
const int NUM_SAMPLES = 16;
vec2 SAMPLE_OFFSETS[NUM_SAMPLES] = vec2[](
    vec2( 1.0, 1.0) / 16.0,
    vec2(-1.0,-3.0) / 16.0,
    vec2(-3.0, 2.0) / 16.0,
    vec2( 4.0,-1.0) / 16.0,

    vec2(-5.0,-2.0) / 16.0,
    vec2( 2.0, 5.0) / 16.0,
    vec2( 5.0, 3.0) / 16.0,
    vec2( 3.0,-5.0) / 16.0,

    vec2(-2.0, 6.0) / 16.0,
    vec2( 0.0,-7.0) / 16.0,
    vec2(-4.0,-6.0) / 16.0,
    vec2(-6.0, 4.0) / 16.0,

    vec2(-8.0, 0.0) / 16.0,
    vec2( 7.0,-4.0) / 16.0,
    vec2( 6.0, 7.0) / 16.0,
    vec2(-7.0,-8.0) / 16.0);


// compute c0 * c1 for complex numbers c0, c1
vec2 cmul(in vec2 c0, in vec2 c1) {
    float r = c0.x * c1.x - c0.y * c1.y;
    float i = c0.x * c1.y + c0.y * c1.x;
    return vec2(r, i);
}

// compute c * c for a complex number c
vec2 csqr(in vec2 c) {
    float r = c.x * c.x - c.y * c.y;
    float i = 2.0 * c.x * c.y;
    return vec2(r, i);
}

// compute x * x for a float x
float sqr(in float x) {
	return x * x;   
}

// compute abs(c) * abs(c) for a complex number c
float lengthsq(in vec2 c) {
    return dot(c, c);
}

// Compute the next iteration of a Mandelbrot sequence from the previous value and the constant c
vec2 iteration(in vec2 prev, in vec2 c) {
    return csqr(prev) + c;
}

// True if a point c is in the main cardioid, else false
bool inCardioid(in vec2 c) {
    float tmp = c.x - 0.25;
    float q = sqr(tmp) + sqr(c.y);

    return q * (q + tmp) < 0.25 * sqr(c.y);
}


// True if a point c is in the 2nd order bulb, else false
bool in2Bulb(vec2 c) {    
    return sqr(c.x + 1.0) + sqr(c.y) < (1.0 / 16.0);
}


// Compute the Mandelbrot normalized escape iteration count for a point on the complex plane.
// Returns 0 for points that are estimated to be in the set
float normIterCount(vec2 c) {
    // If the point is in the main cardioid or 2nd order bulb then we can exit early.
    if (inCardioid(c) || in2Bulb(c)) {
        return 0.0;
    }

    // Otherwise, see if and when the Mandelbrot sequence escapes for c.
    vec2 z = c;
    for (int i = 1 ; i < MAX_ITER ; ++i) {
        float zsq = lengthsq(z);

		// Escape is guaranteed if abs(z) > threshold on an iteration. A threshold of 2 is 
        // sufficient for testing if a point escapes, but with normalized iteration count the
        // interpolation between iterations can only be estimated well with a larger threshold.
        const float THRESHOLD = 256.0;
        if (zsq > THRESHOLD * THRESHOLD) {
            // The normalized iteration count formula following iq's fast version for
            // a threshold of 256.
            //
            // Generally, given a THRESHOLD = 2^p, the fast version is
            // return float(i) - log2(log2(zsq)) + log2(p) + 1
        	return float(i) - log2(log2(zsq)) + 4.0;
        }
        z = iteration(z, c);
    }

    // The sequence did not escape for our iteration max. Treat as part of the set.
    return 0.0;
}

// Convert from screen space fragment coordinates to a complex number.
vec2 screenToComplex(vec2 fragCoord, float time) {    
    // Weight is two smoothstep splines that blend from 0 to 1 and back over the specified time period.
    float normTime = 2.0 * fract(time / ZOOM_TIME);
    float weight = normTime > 1.0 ? 2.0 - normTime : normTime;
    weight = smoothstep(0.0, 1.0, weight);

    // Do the interpolation in log space so the zoom rate appears constant.
    // Blend between the log min/max zoom values, then transform back to real zoom coefficient.
	const float LOG_ZOOM_MIN = log(ZOOM_MIN);
    const float LOG_ZOOM_MAX = log(ZOOM_MAX);
    float logZoom = mix(LOG_ZOOM_MIN, LOG_ZOOM_MAX, weight);    
    float zoom = exp(logZoom);

    // Scale the render region by the zoom value and transform fragCoord to a complex number.
	float regionSize = 1.0 / zoom;
    float scale = regionSize / iResolution.y;
    vec2 c = fragCoord.xy * scale;    
    c = c + REGION_MID - 0.5 * regionSize;

    return c;
}

// Compute a color value from a normalized iteration count.
vec4 computeColor(float iter) {
    // If a part of the set, use the interior color.
    if (iter == 0.0) {
    	return INTERIOR_COLOR;   
    }

    // Else use a blend between the colors of the cycle. I take the log of the iteration count because I
    // like how it makes the areas closer to the set where iterations change rapidly look less busy and
    // aliased. Most renderers do not do this.
    float ifloor;
    float imod = modf(log2(iter), ifloor);    
    int ix = int(ifloor);
    vec4 color = mix(COLORS[ix % NUM_COLORS], COLORS[(ix + 1) % NUM_COLORS], imod);

    return color;
}

// Sample the mandelbrot colorization at a point in screen space.
vec4 sampleMandelbrot(vec2 fragCoord, float time) {
    // Convert screen position to a complex number c
	vec2 c = screenToComplex(fragCoord, time);

    // Compute the normalized iteration count for the Mandelbrot sequence for c 
    float iter = normIterCount(c);

    // Color according to the normalized iteration count.
	return computeColor(iter);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 color = vec4(0.0);

    // Sample N points around the pixel according to the hardcoded supersample offsets.
    for (int i = 0; i < NUM_SAMPLES; ++i) {
        color += sampleMandelbrot(fragCoord + SAMPLE_OFFSETS[i], iTime);
    }

    // Average the color samples.
	fragColor = color / float(NUM_SAMPLES);
}
