const float PI = 3.1415926535897932384626433832795;
const float scale = 200.;
const float radius = 10.;
const int   count = 50;
const float delta = 2.*PI/float(count);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy;
	vec2 center = iResolution.xy/2.;

    fragColor = vec4(1.);
    for (int i=0; i<count; ++i) {
        float x = float(i)*delta + iTime;
        vec2 point = center + scale*vec2(cos(x), sin(x)*cos(x));
        if (distance(xy, point) <  radius)
        	fragColor = vec4(0.);
    }
}

void mainImage(out vec4 e, in vec2 v)
{
    vec3 d=.5-vec3(gl_FragCoord.xy,1)/iResolution.y,p,o;
    for(int i=0;i<32;i++)
    {
        o=p;
        o.z-=iTime*9.;
        float a=o.z*.1;
        o.xy*=mat2(cos(a),sin(a),-sin(a),cos(a));
        p+=(.1-length(cos(o.xy)+sin(o.yz)))*d;
    }
    e=vec4((sin(p)+vec3(2,5,9))/length(p)*vec3(1), 1);
}

//fireworks in heart shape
//np = no. of particles
//snp = no. of spawned particles

#define rad(x) radians(x)
#define np 20.
#define snp 40.
#define spawn 1
#define trail 1

//random value
vec2 N22(vec2 p){
    vec3 a = fract(p.xyx*vec3(123.34, 234.34, 345.65));
    a += dot(a, a+34.45);
	return fract(vec2(a.x*a.y, a.y*a.z));
}

//particle
vec3 particle(vec2 st, vec2 p, float r, vec3 col){
 	float d = length(st-p);
    d = smoothstep(r, r-2.0/iResolution.y, d);//d<r?1.0:0.0;
    return d*col;
}
//particle with light
vec3 burst(vec2 st, vec2 pos, float r, vec3 col, int heart) {
	st -= pos;
    if (heart==1) st.y -= sqrt(abs(st.x))*0.1;
    r *=0.6*r;
    return (r/dot(st, st))*col*0.6;
}

//displacement with s = p0 + ut + 0.5at^2
vec2 get_pos(vec2 u, vec2 a, vec2 p0, float t, float ang){
    ang = rad(ang);
    vec2 d = p0 + vec2(u.x*cos(ang), u.y*sin(ang))*t + 0.5*a*t*t;
    return d;
}
//velocity at time t
vec2 get_velocity(vec2 u, vec2 a, float t, float ang){
    ang = rad(ang);
    return vec2(u.x*cos(ang), u.y*sin(ang)) + a*t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    float aspect = iResolution.x/iResolution.y;
    vec3 col = vec3(0.0);
    float t = mod(iTime, 5.0);

    //particle
    float r = 0.04;
    vec2 u = vec2(5.); //init velocity
    vec2 a = vec2(0.0, -9.8); //acc due to gravity
    float ang = 75.0; //angle of projection

    vec3 p1 = vec3(0.0); //particle

    for (float i=0.; i<np; i++)
    {
        vec2 rand = N22(vec2(i));
        vec2 ip = vec2(sin(15.*rand.x)*aspect, -1.+r); //initial pos
        u = vec2(sin(5.*rand.x), 5.+sin(4.*rand.y)); //initial velocity
        float t1 = t-i/5.;
    	vec2 s = get_pos(u, a, ip, t1, ang); // displacement
    	vec2 v = get_velocity(u, a, t1, ang);
        float Tf = 2.0*u.y*sin(rad(ang))/abs(a.y); //time of flight
		vec2 H = get_pos(u, a, ip, Tf/2.0, ang); //max height

        vec3 pcol = vec3(sin(22.*rand.x), sin(5.*rand.y), sin(1.*rand.x)); //color per particle

        if (v.y<-0.5){
            //p1+= burst(uv, H, max(0.0, 0.1-(t1*t1-Tf*0.5)), pcol*0.5, 1 );
        	r=0.0; //die
        }
        p1 += burst(uv, s, r, pcol, 0); //main particle

        //trail particle
        if (trail==1){
        	for (float k=4.0; k>0.0; k--){
                vec2 strail = get_pos(u, a, ip, t1-(k*0.02), ang); //delay for trail particles
        		p1 += burst(uv, strail, v.y<-0.5?0.0:r-(k*0.006), pcol, 0);
        	}
        }

        //spawn
        if (v.y<=0.0 && t1>=Tf/2.0 && spawn==1)
        {
            for (float j=0.0; j<snp; j++)
            {
                vec2 rand2 = N22(vec2(j));
                float ang2 = (j*(360./snp));
                r = 0.035; // radius of spawned particles
                r -= (t1-Tf*0.5)*0.04;
                //r/=(t-(Tf/2.)+0.2);

                float x = cos(rad(ang2)); //coords of unit circle 
                float y = sin(rad(ang2));

                y = y + abs(x) * sqrt( (8.- abs(x))/50.0 ); // heart shape as a function of j

                vec2 heart = vec2(x*x + y*y)*(0.4/(t1*sqrt(t1))); //velocity vector
                vec2 S = get_pos(heart, a*0.03, H, t1-(Tf/2.), ang2);
                pcol = vec3(sin(8.*rand2.x), sin(6.*rand2.y), sin(2.*rand2.x)); // color per spawn particle
                p1 += burst(uv, S, max(0.0,r), pcol, 0);

            }
        } 

    }

    //bg
    col = p1;
    vec3 night = vec3(0.06, 0.02, 0.18)*vec3(uv.y*0.5+0.5);
    col += night*(1.0-p1);
    //col = vec3(light(uv, vec2(0.0), 0.1));
    fragColor = vec4(col,1.0);
}

float wave(vec2 uv, vec2 coords, float height, float frequency)
{
    float argument = coords.x + frequency * uv.x;
    float normalizedSine = height * sin(argument);

	float col = 1.0 - smoothstep(-2.0,2.0, ((uv.y - coords.y) -normalizedSine)*iResolution.y);
    col *= uv.y + 2.0;

    return col;
}

float sun(vec2 uv, vec2 center, float radius, float bloom)
{
    float dist = distance(uv, center);
	float circle = 1.0 - smoothstep(-3.0,3.0, (dist-radius)*iResolution.y);

    uv.y -= 0.;
    float visible = 1.0 - step(sin(uv.y * 40.0 + 5.0 * iTime) + 2.7 * uv.y - 0.3, 0.5);
    visible *= (uv.y + 0.5);
    return visible * circle + (1.0 - dist + bloom);
}

vec4 draw(vec4 inputImage, float mask, vec4 color)
{
	inputImage += mask * color;
    return inputImage;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec4 image = vec4(0.0, 0.0, 0.0, 0.0);

    float animatedValue = iTime + 0.3 * sin(iTime);
    float speed = 2.0;
    image = draw(image, wave(uv, vec2(animatedValue * speed ,-0.2), 0.04, 7.0), vec4(0.1, 0.2, 0.3, 0.0));
    image = draw(image, wave(uv, vec2(animatedValue * speed * 1.3 ,-0.4), 0.07, 6.0), vec4(0.1, 0.2, 0.3, 0.0));
    image = draw(image, wave(uv, vec2(animatedValue * speed * 1.5 ,-0.7), 0.1, 5.0), vec4(0.1, 0.2, 0.3, 0.0));
    image = draw(image, sun(uv, vec2(0.0, 0.4), 0.55, 0.3), vec4(1.0, 85.0/255.0, 0.0, 0.0));

    // Output to screen
    fragColor = vec4(image);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.x*1.1;

    const int p=17;
    int k=int(floor(iTime))%(p-2)+2;
    const float r=0.8/float(p);

    float t=fract(iTime);
    t=t*t*(3.0-2.0*t);
    t=t*t*(3.0-2.0*t);
    float mindist=1e9;
    for(int i=1;i<p;i++){
    	float x=mix(float(i),float((i*k)%p),t)/float(p);
		float y=float((i*k)%p-i)*(t-t*t)*0.1;
        vec2 c=vec2(2.*x-1., y);
        mindist=min(mindist, length(uv-c)/r);
    }
    fragColor = vec4(smoothstep(1.2, 0.8, mindist));
}

