float hash(float a, float b) {
  return fract(sin(dot(vec2(a,b), vec2(12.9898, 78.233))) * 43758.5453)*2.-1.;
}

float id;
float scene(vec3 p) {
  float xscale = 1.;
  id = floor(p.x*xscale);
  p.x = (fract(p.x*xscale)-0.5)/xscale;

  float m1 = hash(id, 69.);
  float m2 = hash(id, 38.);
  float m3 = hash(id, 41.);
  float myTime = iTime + m3*10.;
  p.yz += vec2(m1,m2)*0.2 * sin(myTime*2.);

  return 0.8*(length(vec2(length(p.yz)-0.5, p.x))-0.1);
}

vec3 norm(vec3 p) {
  mat3 k = mat3(p,p,p)-mat3(0.001);
  return normalize(scene(p) - vec3(scene(k[0]), scene(k[1]), scene(k[2])));
}

vec3 srgb(float a, float b, float c) {
  return pow(vec3(a,b,c), vec3(2.));
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax,p)*ax, p, cos(ro)) + sin(ro)*cross(ax,p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;

  float n1 = hash(hash(uv.x, uv.y), iTime * 11.);
  float n2 = hash(hash(uv.x, uv.y), iTime * 23.);
  uv += vec2(n1,n2)*0.002;

  vec3 cam = normalize(vec3(1, uv));
  float rot = iTime;
  float shift = iTime;
  float outside = smoothstep(0.0,0.5, asin(sin(iTime*acos(-1.)/4.)));
  float look = mix(0.05, 0.4, outside);
  vec3 init = vec3(shift,0,0) + vec3(0, sin(rot), cos(rot))*mix(0.15, 1., outside);
  cam = erot(cam, vec3(0,1,0), cos(rot)*look);
  cam = erot(cam, vec3(0,0,1), -sin(rot)*look);
  vec3 p = init;
  bool hit = false;
  float mini = 100000.;
  int i;
  for (i = 0 ; i < 100 && !hit; i++) {
    float dist = scene(p);
    mini = min(dist, mini);
    hit = dist*dist < 1e-6;
    p+=dist*cam;
  }
  float glow = pow(exp(-mini*20.), 2.);
  float idloc = id;
  float fog = (float(i)/100.);
  vec3 n = norm(p);
  vec3 r = reflect(cam, n);
  float diff = length(sin(n*2.)*0.5+0.5)/sqrt(3.);
  float spec = length(sin(r*2.)*0.5+0.5)/sqrt(3.);
  vec3 col = mix(srgb(0.4,0.1,0.2), srgb(0.9,0.7,0.4), spec);
  col = abs(erot(col, normalize(vec3(1,7,1)), idloc)) + pow(spec, 15.)*2.;
  vec3 bg = mix(srgb(0.5, 0.2, 0.3), srgb(0.2, 0.1, 0.3), sqrt(length(uv))) + glow*0.2;
  fragColor.xyz = hit ? mix(col, bg, fog) : bg;
  fragColor.xyz = sqrt(fragColor.xyz) + hash(hash(uv.x, uv.y), iTime)*0.04;

  fragColor.xyz = smoothstep(vec3(-0.1), vec3(1.1), fragColor.xyz);
}

#define MAX_STEPS 64
#define MAX_DIST 20.
#define EPS 0.001
const float PI = acos(-1.0);

mat3 rotX(float angle)
{
    return mat3(1,	0,			0,
               0,	cos(angle), -sin(angle),
               0,	sin(angle), cos(angle));
}

mat3 rotY(float angle)
{
	return mat3(cos(angle), 0, sin(angle),
               0,			1, 0,
               -sin(angle), 0, cos(angle));
}

mat3 rotZ(float angle)
{
	return mat3(cos(angle), -sin(angle), 0,
               sin(angle), 	cos(angle),	 0,
               0,		 	0,			 1);
}

vec2 opRepLim( in vec2 p, in float s, in vec2 lima, in vec2 limb )
{
    return p-s*clamp(round(p/s),lima,limb);
}


float sdCube(vec2 pos, float r)
{
	vec2 q = abs(pos) - r;
	return length(max(q,0.0)) + min(max(q.x,q.y),0.0);
}

float sdCube(vec3 pos, vec3 r)
{
	vec3 q = abs(pos) - r;
	return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float plane(vec3 pos)
{
    pos.z = mod((pos.z + iTime*0.2), 0.5);
    pos.xz = opRepLim(pos.xz, 0.5, vec2(-1., -1), vec2(1., 1.0));

    return sdCube(pos, vec3(0.125,0.02,0.125));
}

float map(vec3 pos)
{
    vec3 p = vec3(0);
    vec2 rot = vec2(PI/4., PI/2.);

    p = pos;
    float d1 = max(p.z-0.65, plane(p));

    p = pos;
    p *= rotY(rot.x);
    p *= rotZ(rot.y);
    p *= rotY(rot.x);
    float d2 = max(p.z-0.65, plane(p));

    p = pos;
    p *= rotY(-rot.x);
    p *= rotZ(-rot.y);
    p *= rotY(-rot.x);
    float d3 = max(p.z-0.65, plane(p));

    float res = MAX_DIST;
    res = min(res, d1);
    res = min(res, d2);
    res = min(res, d3);
    return res;
}

vec3 normals(vec3 pos)
{
    vec2 e = vec2(0, 0.01);
    return normalize(vec3(
    	map(pos+e.yxx)-map(pos-e.yxx),
        map(pos+e.xyx)-map(pos-e.xyx),
        map(pos+e.xxy)-map(pos-e.xxy)
    ));
}

const vec4 miss = vec4(MAX_DIST, vec3(0));
vec4 raymarch(vec3 ro, vec3 rd)
{
    float t = 0.0;
    for(int i = 0; i<MAX_STEPS; i++)
    {
        vec3 pos = ro + rd*t;
        float d = map(pos);

        if(abs(d)<=EPS) return vec4(length(pos-ro), normals(pos));
        if(d >= MAX_DIST) return miss;

        t+=d;
    }


    return miss;
}

const vec3 BACK_GROUND = vec3(0.2);
vec3 GetScene(vec3 ro, vec3 rd, vec3 lightDir)
{

    vec4 a = raymarch(ro, rd);
    float d = a.x;
    vec3 nor = a.yzw;

    vec3 col = BACK_GROUND;

    if(d < MAX_DIST)
    {
        float li = dot(lightDir, nor);

        vec3 pos = ro+rd*d;
        pos *= rotX(0.9);

        float lines = sin(pos.y * 300.) * 0.5+0.5;
        lines = smoothstep(li+0.25, li-0.25, lines);
        lines = lines*0.7+0.3;

        col = vec3(1, 0.94, 0.8) * lines;
        col = mix(col, BACK_GROUND, smoothstep(1., 10., d)); // fog
    }

    return col;

}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.x;

    vec2 mouse = iMouse.xy / iResolution.xy;
    float camAngle = PI/2.;

    vec2 p = 1.3 * vec2(cos(camAngle), sin(camAngle));


    vec3 ro = vec3(p.x,0.915,p.y);
    mat3 ca = setCamera(ro, vec3(0,0.2,0), 0.0);
    vec3 rd = ca*normalize(vec3(uv,0.9));

    vec3 ld = normalize(vec3(1, 0.5, -0.5));

    vec3 col = GetScene(ro, rd, ld);

    float v = dot(uv,uv);
    col = mix(col, vec3(0.4), pow(v,2.));

    // Output to screen
    fragColor = vec4(col,1.0);
}

struct Vertex
{
    vec2 pos;
    float radius;
};

float segment(in vec2 p, vec2 a, vec2 b, out float ratio)
{
	vec2 ap = p - a;
	vec2 ab = b - a;
	ratio = clamp(dot(ap,ab)/dot(ab,ab), 0.0, 1.0); // Project ap onto ab. Clamp to edges of line if we project beyond it.
	return length( ap - ab*ratio ); // finds length of vec from p to the projection of p onto ab
}

// returns the distance of p from the taped thicc boi line. negative length indicates p is within the shape
float taperedSegment(vec2 p, vec2 ap, float ar, vec2 bp, float br)
{
    float ratio; // Where we are along the line [0,1]. 0 means at a and 1 means b
    float len = segment(p, ap, bp, ratio); // Compute len from the center line running from point a to b

    float v = mix(ar, br, ratio);
    float dist = len - v;

	return dist;
}


// Shape /////////////////////////////////////////////////////////////////////////////////////////////////
vec3 strokeCol = vec3(0.8,0.2,0.4);
vec3 fillCol = vec3(0.7,0.3,0.6)*0.6;
const int numVerts = 7;
Vertex[numVerts] shape = Vertex[numVerts] (
    Vertex(vec2(0.25,-0.1), 2.0), // bot left 2
    Vertex(vec2( 0.0, 0.0), 4.0), // bot left
    Vertex(vec2( 0.0, 1.0), 2.5), // top left
    Vertex(vec2( 0.5, 0.5), 5.0), // pivot
    Vertex(vec2( 1.0, 1.0), 2.5), // top right
    Vertex(vec2( 1.0, 0.0), 4.0), // bot right
    Vertex(vec2(0.75,-0.1), 2.0)  // bot right 2
);

// Finds the distance of P from the line A to B
vec3 drawShape(vec2 p, mat3 tform, vec3 col, bool stroke, bool fill)
{
    float thicc = 0.01;
    float strokeThicc = 0.008;

    for(int i = 1; i < numVerts; i++)
    {
        Vertex a = shape[i-1];
        Vertex b = shape[i];
    	vec2 ap = (tform * vec3(a.pos, 1)).xy;
    	vec2 bp = (tform * vec3(b.pos, 1)).xy;

        float dist = taperedSegment(p, ap, thicc*a.radius, bp, thicc*b.radius);
        if (dist < strokeThicc) 
        {	
            // stroke
            col = mix(col, strokeCol, vec3(stroke));

            // fill
            if (dist < 0.0) 
            {
            	col = mix(col, fillCol, vec3(fill));
            }
        }
    }

    return col;
}

// Move some verts around and scale radii for funsies
void updateShape()
{
    shape[2].pos += 0.7 * vec2(0.2*sin(iTime), 0.1*cos(iTime));
    shape[4].pos += 0.7 * vec2(0.2*-cos(iTime), 0.1*sin(iTime));

    for (int i = 0; i < numVerts; i++)
    {
        shape[i].radius += sin(float(i) + iTime * (1.0 + 0.5*shape[i].radius));
    }
}


