/** 
	Matrix notes
 	- These are column major.
	- Although written as rows, think of them transposed as columns.
	- Concatenate transformations in reverse order. Eg. M=T*R*S; Will evaluate left to right (duh) 
	  but the transformation order will be Scale, Rotate then Translate.
**/
mat3 createTranslationMat(vec2 translation)
{
    return mat3(vec3(1,0,0), 
                vec3(0,1,0), 
                vec3(translation,1));
}
mat3 createScaleMat(vec2 scale)
{
    return mat3(scale.x, 0, 0,
                0, scale.y, 0,
                0, 0,       1);
}
mat3 createRotationMat(float rad)
{
    return mat3(cos(rad), sin(rad), 0,
               -sin(rad), cos(rad), 0,
                0,        0,        1);
}
mat3 createAffineMat(vec2 translation, float rotation, vec2 scale)
{
    mat3 S = mat3(createScaleMat(scale));
    mat3 R = mat3(createRotationMat(rotation));
    mat3 T = createTranslationMat(translation);
    return T*R*S;
}

mat3 matTranslate(mat3 m, vec2 translation)
{
    return m * createTranslationMat(translation);
}
mat3 matRotate(mat3 m, float rad)
{
    return m * createRotationMat(rad);
}
mat3 matScale(mat3 m, float scale)
{
    return m * createScaleMat(vec2(scale));
}
mat3 matScale(mat3 m, vec2 scale)
{
    return m * createScaleMat(scale);
}


