void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspectRatio = iResolution.x / iResolution.y;
    vec2 uv = fragCoord/iResolution.yy;
    vec2 uvNorm = fragCoord/iResolution.xy;

    updateShape();

    // Shape tform - moves pivot to the middle of our shape
    mat3 shapeMat = mat3(1);
    shapeMat = matTranslate(shapeMat, vec2(-.5,-.5));

    // Model tform - moves/rotates and squishes our model
    mat3 modelMat = mat3(1);
	modelMat = matTranslate(modelMat, vec2(aspectRatio/2., 0.5));
    modelMat = matRotate(modelMat, sin(iTime*0.7)*0.7);
   	modelMat = matScale(modelMat, 0.5 + 0.08 * vec2(sin((iTime+3.0)*3.0), cos(iTime*3.0)));

    // View tform - pan the view about
    mat3 viewMat = mat3(1);
    viewMat = matTranslate(viewMat, 0.05 * vec2(2.0*sin(1.2*iTime+3.0), cos(iTime+5.0)) );

    // Draw
    vec3 col = vec3(1);
    col = viewMat * vec3(uvNorm,1)*0.2; // Draw background
    col = drawShape(uv, viewMat*modelMat*shapeMat, col, true, false); // fill
    col = drawShape(uv, viewMat*modelMat*shapeMat, col, false, true); // stroke. Done in 2 passes to hide interior strokes. Could be nicer :)

    fragColor = vec4(col,1.0);
}

