// Time measured with Shadertoy unofficial plugin by Patu

// 1.00ms
float sdLine(vec3 p, vec3 a, vec3 b){
    vec3 pa = p-a, ba = b-a;
    float h = clamp(dot(pa,ba)/dot(ba,ba),0.,1.);
    return length(pa-ba*h);
}

// 1.15ms
float sdLine4(vec3 p, vec3 a, vec3 b){
    vec3 pa = p-a, ba = b-a;
    float h = dot(pa,ba)/dot(ba,ba);
    pa -= ba*h;
    h = max(abs(h-.5)-.5,0.);
    return sqrt(length(vec2(dot(pa,pa),h*h*dot(ba,ba))));
}

// 1.25ms
float sdLine3(vec3 p, vec3 a, vec3 b){
    vec3 pa = p-a, ba = b-a;
    float h = dot(pa,ba)/dot(ba,ba);
    float u = length(pa-ba*h), v = max(abs(h-.5)-.5,0.)*length(ba);
    return pow(u*u*u+v*v*v,1./3.);
}

// 1.5ms-1.6ms
float sdLineN(vec3 p, vec3 a, vec3 b, float n){
    vec3 pa = p-a, ba = b-a;
    float h = dot(pa,ba)/dot(ba,ba);
    float u = length(pa-ba*h), v = max(abs(h-.5)-.5,0.)*length(ba);
    return pow(pow(u,n)+pow(v,n),1./n);
}
// n=2:    G1
// 2<n<3:  G2 with divergent 3rd derivative, looks like G1
// n=3:    G2, looks like G2
// 3<n<4:  G3 with divergent 4th derivative
// nâ‰¥4:    G3+



vec3 a, b;
float map(vec3 p){
    return sdLineN(p,a,b,3.-cos(iTime))-1.0;
}
vec3 grad(vec3 p){
    const float e = .001;
	float a = map(p+vec3(e,e,e));
	float b = map(p+vec3(e,-e,-e));
	float c = map(p+vec3(-e,e,-e));
	float d = map(p+vec3(-e,-e,e));
	return normalize(vec3(a+b-c-d,a-b+c-d,a-b-c+d));
}


