// Returns the inverse trasnformation matrix for the given t,r and s components 
mat4 inverseTransform(vec3 scale, vec3 rDeg, vec3 trans) {

    // convert from degrees to radians
    vec3 rotate = vec3(radians(rDeg.x), radians(rDeg.y), radians(rDeg.z));

    mat4 r_x;
    r_x[0] = vec4(1., 0., 0., 0.);
    r_x[1] = vec4(0., cos(rotate.x), sin(rotate.x), 0.);
    r_x[2] = vec4(0., -sin(rotate.x), cos(rotate.x), 0.);
    r_x[3] = vec4(0., 0., 0., 1.);


    mat4 r_y;
    r_y[0] = vec4(cos(rotate.y), 0., -sin(rotate.y), 0.);
    r_y[1] = vec4(0., 1, 0., 0.);
    r_y[2] = vec4(sin(rotate.y), 0., cos(rotate.y), 0.);
    r_y[3] = vec4(0., 0., 0., 1.);

    mat4 r_z;
    r_z[0] = vec4(cos(rotate.z), sin(rotate.z), 0., 0.);
    r_z[1] = vec4(-sin(rotate.z), cos(rotate.z), 0., 0.);
    r_z[2] = vec4(0., 0., 1., 0.);
    r_z[3] = vec4(0., 0., 0., 1.);

    mat4 r = r_x * r_y * r_z;

    // creating worldTransform matrix
    mat4 t;
    t[0] = vec4(1., 0., 0., 0.);
    t[1] = vec4(0., 1., 0., 0.);
    t[2] = vec4(0., 0., 1., 0.);
    t[3] = vec4(trans.x, trans.y, trans.z, 1.);

    mat4 s;
    s[0] = vec4(scale.x, 0., 0., 0.);
    s[1] = vec4(0., scale.y, 0., 0.);
    s[2] = vec4(0., 0., scale.z, 0.);
    s[3] = vec4(0., 0., 0., 1.);

    mat4 worldTrans = t * r * s;

    return inverse(worldTrans);
}

