#define SPHERE_SDF sphere(pos, 2.0, vec3(0.0, 0.0, 6.0))
#define CUBE_SDF cube(pos, vec3(3., 4., 3.), vec3(0., 0., 0.), vec3(-5., 0., 8.))
#define TRI_PRISM_SDF sdTriPrism( pos-vec3(-5.0, 3., 8.0), vec2(2.25,2.05) )
#define TORUS_SDF sdTorus(pos-vec3( 4.5,0.25, 4.0), vec2(1.60, .5))
#define OCTA_SDF sdOctahedron( pos-vec3(8., 0., 2.), 2.35 )

///-----------------///
/// Scene Functions ///
///-----------------///

void sceneMap3D(vec3 pos, out float t, out int obj) {
    t = SPHERE_SDF;
    vec2 res = vec2( 1e10, 0.0 );
    float t2;
    obj = 0; // 0 is center sphere
    if((t2 = TRI_PRISM_SDF ) < t) {
        t = t2;
        obj = 1;
    }
    if((t2 = CUBE_SDF) < t) {
        t = t2;
        obj = 2;
    }
    if((t2 = TORUS_SDF) < t) {
        t = t2;
        obj = 4;
    }
   if((t2 = OCTA_SDF) < t) {
        t = t2;
        obj = 5;
    }
    if((t2 = box(pos + vec3(0.0, 3.0, 0.0), vec3(50.0, 1.0, 50.0))) < t) {
        t = t2;
        obj = 3;
    }
}


float sceneMap3D(vec3 pos) {
    float t = SPHERE_SDF;
    t = min(t, TRI_PRISM_SDF);
    t = min(t, CUBE_SDF);
    t = min(t, TORUS_SDF);
    t = min(t, OCTA_SDF);
    t = min(t, box(pos + vec3(0.0, 3.0, 0.0), vec3(50.0, 1.0, 50.0)));
    return t;
}

vec3 computeNormal(vec3 pos) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                            sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                            sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

void rayMarch(vec3 origin, vec3 dir, out float t, out int hitObj) {
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i) {
        vec3 pos = origin + t * dir;
    	float m;
        sceneMap3D(pos, m, hitObj);
        if(m < 0.01) {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}


vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 lightDir, vec3 viewVec) 
{
    float t;
    switch(hitObj) {
        case 0:
        // Sphere
        return vec3(1.0, 0.67, 0.67);
        break;
        case 1:
        // TriPrism
        return vec3(0.67, 1.0, 0.67);
        break;
        case 2:
        // Cube
        return vec3(0.67, 0.67, 1.0);
        break;
        case 3:
        // Floor
        t = floor(mod(0.5 * (sin(p.x) + sin(p.z)), 3.0));
        return mix(vec3(0.1), vec3(0.5), t);
        break;
        //Torus 
        case 4: 
        return vec3(0.37, 0.67, 0.5);
        //Octa 
        case 5: 
        return vec3(0.7, 0.07, 0.2);
        case -1:
        // Background
        return vec3(0.99, 0.88, 0.69);
        break;
    }
    return vec3(0.99, 0.88, 0.69);
}

///------------------SHADOWS----------------------///

float hardShadow(vec3 dir, vec3 origin, float min_t) {
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) {
        float m = sceneMap3D(origin + t * dir);
        if(m < 0.0001) {
            return 0.0;
        }
        t += m;
    }
    return 1.0;
}

float softShadow(vec3 dir, vec3 origin, float min_t, float k) {
    float res = 1.0;
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) {
        float m = sceneMap3D(origin + t * dir);
        if(m < 0.0001) {
            return 0.0;
        }
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}

float shadow(vec3 dir, vec3 origin, float min_t) {
    #if HARD_SHADOW
    return hardShadow(dir, origin, min_t);
    #else
    return softShadow(dir, origin, min_t, SHADOW_HARDNESS);
    #endif
}

//Takes a ray in the scene and returns an intersection 
Intersection sceneIntersection(Ray ray) 
{
    //To be set 
    float t;
    int hitObj;

    //RayMarch will set t and object hit by raymarching using SDFs 
    rayMarch(ray.origin, ray.dir, t, hitObj);

    //Intersection point in our scene 
    vec3 isect_point = ray.origin + t * ray.dir;

    //Normal at POI  
    vec3 nor = computeNormal(isect_point);
    vec3 lightDir = normalize(lightPos - isect_point);

    //Compute Material returns color at the POI 
    vec3 material = computeMaterial(hitObj, isect_point, nor, lightDir, 
                                        normalize(ray.origin - isect_point));

    vec3 warmDir = rotateY(normalize(vec3(1.0, 1.0, 1.0)), sin(iTime * 0.5));
    vec3 coolDir = rotateY(normalize(vec3(-1.0, 0.0, -1.0)), sin(iTime * 0.5));

    float warmDot = max(0.0, dot(nor, warmDir));
    float coolDot = max(0.0, dot(nor, coolDir));

    vec3 overallColor = warmDot * vec3(1.0, 0.88, 0.7) * shadow(warmDir, isect_point, 0.1);
    overallColor += coolDot * vec3(0.05, 0.2, 0.5);
    overallColor *= material;
    overallColor = clamp(overallColor + vec3(0.05, 0.1, 0.15), 0.0, 1.0);

    vec3 sdfColor = overallColor;

    return Intersection(t, sdfColor, isect_point, hitObj);
}

// Returns direction of ray
Ray rayCast(vec3 eye, vec3 ref, vec2 ndc) 
{
    vec3 F = ref - eye;
    vec3 R = normalize(cross(F, vec3(0,1,0)));
    vec3 U = normalize(cross(R, F));

    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / iResolution.y;

    vec3 p = ref + ndc.x * H + ndc.y * V;

    return Ray(eye, normalize(p - eye));
}

///---------------///
/// Main Function ///
///---------------///

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    //Anti aliasing    
    if(ANTIALIASING == 1)
    {  
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // [-1, 1]
    vec2 uv2 = 2.0 * uv - vec2(1.0);

    Intersection aaIsects[4];
    //Camera's eye and reference points 

    vec3 eye = vec3(-4, 9., -15.);
    vec3 ref = vec3(0, 3.5, 0);
    int idx = 0;
    for(float i = 0.0; i < 1.0; i += 0.5) {
        for(float j = 0.0; j < 1.0; j += 0.5) {
            vec2 ndc = uv2 + vec2(i, j) / iResolution.xy;
            Ray ray = rayCast(eye, ref, ndc);
            aaIsects[idx++] = sceneIntersection(ray);
        }
    }

    vec3 avgColor = vec3(0.0);
    for(int i = 0; i < 4; ++i) {
        avgColor += aaIsects[i].color;
    }

    avgColor *= 0.25;

    #ifdef DOF
    float distAlongCamZ = abs(dot(normalize(ref - eye), (aaIsects[0].p - eye)));
    float dofZ = min(1.0, abs(distAlongCamZ - FOCAL_LENGTH) / FOCAL_RANGE);
    dofZ = pow(dofZ, 0.5);

    fragColor = vec4(avgColor * step(0.0, aaIsects[0].t), dofZ);
    float fogT = smoothstep(50.0, 100.0, distance(aaIsects[0].p, eye));
    fragColor.rgb = mix(fragColor.rgb, vec3(0.0), fogT);
    #endif 

   #ifdef NO_DOF 
        fragColor = vec4(avgColor.rgb, 1.);
    #endif

    }
    else //No Anti aliasing
    {
        // Normalized pixel coordinates (from 0 to 1)
        vec2 uv = fragCoord / iResolution.xy;
        // Convert to range [-1, 1]
        uv = uv * 2.0 - vec2(1.0, 1.0);

        //Camera's eye and reference points 
        vec3 eye = vec3(0, 3.5, -15.);
        vec3 ref = vec3(0, 2.5, 0);


        //Ray cast returns a ray in the camera's direction to the reference point 
        Ray ray = rayCast(eye, ref, uv);

        //Scene Intersection 
        Intersection isect = sceneIntersection(ray);
        vec3 avgColor = isect.color;

        #ifdef DOF
            float distAlongCamZ = abs(dot(normalize(ref - eye), (isect.p - eye)));
            float dofZ = min(1.0, abs(distAlongCamZ - FOCAL_LENGTH) / FOCAL_RANGE);
            dofZ = pow(dofZ, 0.5);
            fragColor = vec4(avgColor * step(0.0, isect.t), dofZ);
            float fogT = smoothstep(50.0, 100.0, distance(isect.p , eye));
            fragColor.rgb = mix(fragColor.rgb, vec3(0.0), fogT);
        #endif 

       #ifdef NO_DOF 
            fragColor = vec4(avgColor.rgb, 1.);
        #endif
    }
}

