#define time iTime
#define PI 3.141592
#define anim(t) (abs(sin(t))*0.3-1.2);

mat2 rot (float a)
{return mat2 (cos(a),sin(a),-sin(a),cos(a));}

float hash21(vec2 x)
{return fract(sin(dot(x,vec2(12.1,23.4)))*1245.5);}

float hash11(float x)
{return fract(sin(x)*124.4);}

float vcaps( vec3 p, float h, float r )
{
    p.y -= clamp( p.y, 0.0, h );
    return length( p ) - r;
}

// french for jar
float pot (vec3 p)
{
    float radius = (p.y>0.7) ? 0.6: 0.5+sin((p.y+0.8)*2.)*0.3;
    return max(p.y-.9,abs(max(length(p.xz)-radius, abs(p.y)-1.))-0.05);
}

float yeux;
float g1 = 0.;
// french for cat
float chat (vec3 p)
{ 
    p.y -= 1.1;
    float sy = anim((time+p.y*0.07)*6.);
    float sxz = 1./sy;
    vec3 psize = vec3(sxz,sy,sxz);
    p *= -psize;

    vec3 pp = p;
    float sphe = length(p)-.5;

    p = pp;
    p.z += 0.45;
    p.y -= 0.2;
    p.x = abs(p.x)-0.15;  
    // french for eyes
    yeux = length(p)-0.05;  
    g1 += 0.01/(0.01+yeux*yeux);

    p = pp;
    p.y -= 0.35;
    p.z += 0.2;
    p.x = abs(p.x)-0.3;  
    p.xy *= rot(-PI/4.);
    float or =  max(-(p.z-0.04),abs(max(-p.y,dot(p,normalize(sign(p)))-0.12))-0.002);

    return min(sphe, min(yeux,or));
}

// french for paws
float pattes (vec3 p)
{  
    p.z += 0.7;
    p.y -= 0.8;
    p.x = abs(p.x)-0.2;
    return vcaps(p,0.15, 0.08);
}

float papat, chacha, popo;
float idchat;
float SDF (vec3 p)
{

    float per = 4.;
    float dt = time*0.5;
    p.x += per*(floor(dt)+pow(fract(dt),12.));
    idchat = floor((p.x-per*0.5)/per);
    p.x = mod(p.x-per*0.5, per)-per*0.5;

    papat = pattes(p);
    chacha = chat(p);
    popo = pot(p);
    return min(min(papat,chacha),popo);
}

vec3 palette (float t, vec3 c, vec3 d)
{return vec3(0.5)+vec3(0.5)*cos(2.*PI*(c*t+d));}

vec3 text_pot (vec2 uv, float detail, float cid)
{
    uv.y *= detail;
    float id = floor(uv.y);
    vec3 col1 = vec3(1.,.9,hash11(idchat*0.1));
    vec3 col2 = palette(id, vec3(0.1), vec3(cid*0.1,cid*0.8,cid*0.5));
    return clamp((abs(uv.y) > 10.) ? col1 : col2,0.,1.);
}

float background(vec2 uv)
{
    uv *= 5.;
    vec2 id = floor(uv);
    uv = fract(uv)-0.5;
    uv.x = abs(uv.x);
    uv *= rot(PI/4.);
    float r = 0.03+sin(length(id*2.)-time)*0.05+0.08;
    return smoothstep(r,r*1.1, length(uv*vec2(0.5,1.)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    float dither = hash21(uv);

    vec3 ro = vec3(0.01,1.6,-4.),
        rd = normalize(vec3(uv+vec2(0.,-0.3),1.)),
        p = ro,
        col = clamp(mix(vec3(1.,0.,0.),vec3(0.95),background(uv)),0.,1.);

    float shad,d;
    bool hit = false;

    for (float i=0.; i<64.; i++)
    {
        d = SDF(p);
        if (d<0.01)
        {
            hit = true;
            shad = i/64.;
            break;
        }
        d *= 0.7+dither*0.1;
        p+=d*rd;
    }
    if (hit)
    {
        vec2 cuv = vec2(atan(p.z,p.x), p.y);
        if (d == papat || d == chacha) col = palette(abs(idchat), vec3(0.1),vec3(0.,0.,0.1));
        if (d == popo) col = text_pot(cuv,15., abs(idchat));
        if (d == yeux) col = vec3(0.9);

        col *= (1.-shad); 
    }
    col += g1*0.05;

    // vignetting (from iq)
    vec2 q = fragCoord.xy / iResolution.xy;
    col *= .5 + 0.5 * pow(16. * q.x * q.y * (1. - q.x) * (1. - q.y), 0.1);

    fragColor = vec4(sqrt(col),1.);
}

//This parameter changes the magnification.
const float mag = 9.0;
const float distortion_scale = mag*mag;

const int GREEN = 0;
const int BLUE = 1;
const int GRAY = 2;
const int YELLOW = 3;
const int WHITE = 4;
const int FOREST_GREEN = 5;
const int LIGHTBLUE = 6;
const int SKYBLUE = 7;
const int SNOW = 8;
const int WHITESMOKE = 9;
const int LIGHTGRAY = 10;
const int LIME = 11;
const int LIGHTYELLOW = 12;
const int BEIGE = 13;
const int SAND = 14;
const int TAN = 15;
const int ORANGE = 16;
const int RED = 17;
const int BLACK = 18;
const int DARKGRAY = 19;
const int ORANGERED = 20;

const int FOREST_BIOME = 5;

//this function is from https://www.shadertoy.com/view/4djSRW
int hash12(vec2 p,float num_colors)
{
    //set the number of colors to be randomly generated

    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return int(floor(fract((p3.x + p3.y) * p3.z)*num_colors));
}

int magnify(vec2 fragCoord,float mag,float num_colors){
    return hash12(floor(fragCoord/pow(3.0,mag)),num_colors);
}

ivec4 get_neighbors(vec2 fragCoord,float mag,float colors){
	return ivec4(magnify(fragCoord+vec2(0,1),mag,colors),
        magnify(fragCoord+vec2(1,0),mag,colors),
        magnify(fragCoord-vec2(0,1),mag,colors),
        magnify(fragCoord-vec2(1,0),mag,colors));
}


bool is_next_to(int color,ivec4 neighbors){
	return (neighbors.x == color)
        || (neighbors.y == color)
        || (neighbors.z == color)
        || (neighbors.w == color);
}

vec3 rgb(float r,float g,float b){
	return vec3(r,g,b)/255.0;
}

