}


int forest_biome(inout int color1, ivec4 neighbors){
	int BLUE = 0;
    int GREEN = 1;
    int YELLOW = 2;
    int GRAY = 3;
    int FOREST_GREEN = 4;
    int WHITE = 5;
    return (color1 == BLUE && is_next_to(GREEN,neighbors))
        ? GREEN
    :(color1 == YELLOW)
        ? GREEN
    :(color1 == WHITE && is_next_to(FOREST_GREEN,neighbors))
        ? FOREST_GREEN
    :(color1 == GRAY && is_next_to(GREEN,neighbors))
        ? WHITE
    :(color1 == GREEN && is_next_to(YELLOW,neighbors))
        ? WHITE
    :(color1 == GREEN && is_next_to(WHITE,neighbors))
        ? FOREST_GREEN
    :(color1 == FOREST_GREEN && is_next_to(GREEN,neighbors))
        ? GREEN
    :color1;

}


void biome(inout int color1, ivec4 neighbors,int biome){
    color1 = forest_biome(color1,neighbors);
}

int biome(in vec2 fragCoord,float mag,float zoom,int colors[6],int biome1){
    fragCoord *= zoom;


    float num_colors = float(colors.length());
    float warp = 50.0;
    float avg = (warp*(mag));
    fragCoord += vec2(sin(fragCoord.y/avg),sin(fragCoord.x/avg))*mag*mag;

    int color1 = magnify(fragCoord,mag,num_colors);

    ivec4 neighbors;

    while(mag > 1.0){
        avg =(warp*(mag));
        fragCoord += vec2(sin((fragCoord.y)/avg+iTime)*(3.0+sin(fragCoord.y/warp)*2.0),sin(fragCoord.x/avg+iTime)*(3.0+sin(fragCoord.x/warp)*2.0))*mag*mag;

        neighbors = get_neighbors(fragCoord,mag,num_colors);
    biome(color1, neighbors,biome1);
    mag -= 1.0;
    }
    return colors[int(color1)];;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = mag*(((iMouse.x-iMouse.y)*2.0+iResolution.x)/iResolution.x);
    fragCoord *= zoom;
    fragCoord = fragCoord + floor(iTime*50.0)*vec2(mag,0.0);

    //choose the biome here
	int biome = biome(fragCoord,mag,1.0,int[](BLUE,GREEN,YELLOW,GRAY,FOREST_GREEN,WHITE),FOREST_BIOME);

    fragColor = vec4(get_color(biome),1.0);
}

void mainImage(out vec4 C,vec2 U ){
