    U/=iResolution.xy;U-=0.5;f j,i=0.;for(;i <7.;i++){j=f(int(U.y*0.5)^int(U.x*10.));U=abs(U)-2.-j*0.15;U*=mat2(cos(i+t),-sin(i+t),sin(i+t),cos(i+t));}C=vec4(max(U.y,U.x)*40.);
}

// HYPERBOLIC CODE FROM http://roy.red/generating-spherical-and-hyperbolic-tilings-in-glsl-.html#generating-spherical-and-hyperbolic-tilings-in-glsl
// Super interesting read, I am still in the process of understanding all of it, so I copied the UV generation
// and added some textures on top to make it shiny


#define rot(x) mat2(cos(x),-sin(x),sin(x),cos(x))

#define PI acos(-1.)

#define pi acos(-1.)
#define tau (1.*pi)

// Adjust p, q, r
const float p = 3.; const float q=4.; const float r=3.;
float space = float(sign(q*r+p*r+p*q-p*q*r));
vec3 fold( vec3 p, in vec3 dir, inout int n) {
// Reflect if we're outside the fundamental region
    float dt = dot(p,dir);
    if (dt < 0.) {
        n = n + 1;
        return p-2.*dt*dir*vec3(1,1,space);
    }
    return p;
}
float sdBox(vec2 p, vec2 s){
	float d = 10e8;
    p = abs(p) - s;
    d = max(p.x,p.y);
    return d;
}
float xor(float a, float b){
	return float(int(a)^ int(b));
}
vec4 noise(float t){return texture(iChannel0,vec2(floor(t), floor(t))/256.);}

vec3 get(vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    vec2 z = uv;
    z *= 2.5;

    // - HYPERBOLIC UV - //

    if(length(z) > 1.)
    	z /= dot(z,z);

    // Stereographically project
    vec3 w=vec3(2.*z,1.-space*dot(z,z))*1./(1.+space*dot(z,z));

    // Hyperbolic rotation
    float amt = mod(iTime, 2.45*2.);

    float envA = smoothstep(0.,1.,amt/2.45)*0.2;
    float envB = smoothstep(0.,1.,amt/2.45 - 1.)*0.2;

    if(amt > 2.45)
        amt -= 2.45*2.;
    w.yz *= mat2(cosh(amt), sinh(amt), sinh(amt),cosh(amt));
    //w.yz *= mat2(cosh(amt), sinh(amt), sinh(amt),cosh(amt));
    // Calculate cutting planes
    float ab = -cos(PI/p);
    float bc = -cos(PI/q);
    float ac = -cos(PI/r);
