        c[2] = .5;

    // Fold across the planes a few times
    int n=0; int m = 0;
    for (int i=0;i<10;i++){
        w = fold(w,a,m);
        w = fold(w,b,m);
        w = fold(w,c,n);
    }


    // - COLORING - //
