    float d = 10e7;
    vec4 no = noise(float(n + m));
    vec2 p = w.xy;
    //p = sin(p*7.);
    #define pmod(p,x) (mod(p,x) - 0.5*x)
    //vec2 j = pmod(p, 20.);
    vec2 j = p;
