// final
const vec4 colorBalance = vec4(0.3,0.7,0.5,0.3);
const vec3 uColor = vec3(0.5,1,0);
const vec3 uFog = vec3(0.005,0.001,0.0035);
const vec3 uLightDir = vec3(-1,-1,1);

// shape
const vec3 uRatio = vec3(-0.37762,0.36364,0);
const float uRot = 0.1;
const vec2 uvOffset = vec2(2.69231,1.71329);

float camZ = 0.0;

vec2 path(float t)
{
	return vec2(cos(t * uRot) * 0.7, 
                sin(t * uRot) * 0.5) * uvOffset;	
}

float map(vec3 p)
{
	float a = p.z * uRot;
    p.xy *= mat2(cos(a),sin(a),-sin(a),cos(a));
    float th = mix(0.001, 0.06, cos(camZ - p.z)*0.5+0.5);
    return th-length(
        sin(p.xyz)*uRatio.x+
        sin(p.zxy)*uRatio.y+
        sin(p.yzx)*uRatio.z);
}

vec3 nor(vec3 p, float prec)
{
	vec3 e = vec3(prec,0,0);
	return normalize(vec3(
		map(p+e)-map(p-e),
		map(p+e.yxz)-map(p-e.yxz),
		map(p+e.zyx)-map(p-e.zyx)));
}

void mainImage(out vec4 e, in vec2 v)
{
    vec3 rd = normalize(.5 - vec3(v,1)/iResolution.y);
    camZ = iTime * -5.0;
	vec3 ro = vec3(uvOffset + path(camZ),camZ);
	vec3 p = vec3(0);

	float d = 0.0, s = d;
    for(int i=0;i<250;i++)
    {
		if (abs(s)<d*d*1e-6) break;
		d += s = map(p);
		p = ro + rd * d;
    }

	vec3 ld = normalize(uLightDir);
	vec3 n = nor(p, 0.01);
	float diff = max(dot(n,-ld),0.0);
	float spec = max(dot(reflect(ld,n), rd),0.0);
	vec3 col = sin(d*uColor)*0.5+0.5;
	vec3 fog = exp(-uFog*d*d);
    vec3 c = 
		diff * colorBalance.x * fog.x + 
		col * colorBalance.y * fog.y + 
		sqrt(spec * colorBalance.z * 10.0) * colorBalance.w * fog.z;

	e = vec4(c, 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float iterations = 20.0;
    vec3 col;
    col = vec3(1.0,1.0,1.0);
    float iterated = 0.0;

    for (float i = 0.0; i <= iterations; i++)
    {

        int x = int(i);
        float colony = sin(uv.x+( iTime*0.5*(float(x)*0.05))+i)+(((sin(iTime))*0.5*(float(x)*0.05))+0.5);
        if (colony >= uv.y-0.05 && colony <= uv.y+0.05 && iterated == 0.0)
    	{
            if (x % 4 == 1)
            {
                col *= 0.5;
    			col.r = 0.5;
            	col.b = 0.8;
                iterated = 1.0;
            }
            else if (x % 4 < 1)
            {
                col *= 0.5;
    			col.b = 0.5;
           		col.g = 0.8;
                iterated = 1.0;
            }
            else if (x % 4 > 1)
            {
                col *= 0.5;
    			col.g = 0.5;
                col.r = 0.8;
                iterated = 1.0;
            }
    	}

    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

const float cells = 40.;
const float subtile = 1.;
const bool boxed = true;

vec2 N22(vec2 p) {
    vec3 a = fract(p.xyx*vec3(123.34, 234.34, 345.65));
    a += dot(a, a+34.45);
    return fract(vec2(a.x*a.y, a.y*a.z));
}

vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;
	float t = iTime * 0.2+200.;

    vec3 col;

    float id = 0.;
    float minD = 1000.;

    if (boxed) {
        vec2 guv = fract(uv*subtile)-.5;
        vec2 gid = floor(uv*subtile);
		vec2 cid;

        for (float x = -1.; x < 2.; x++) {
            for (float y = -1.; y < 2.; y++) {
                vec2 off = vec2(x, y);
                vec2 n = N22(gid+off);
                vec2 p = off + sin(n * t*4.)*0.5;
                vec2 dVec = guv-p;
                float d = abs(dVec.x)+abs(dVec.y);
                if (d < minD) {
                    minD = d;
                    cid = off+gid;
                }
            }
        }
        float h = N22(cid).x*0.2;
        float s = smoothstep(1., -0.6, minD);
        float b = 0.3+N22(cid).x*(1.-minD);

        col = hsb2rgb(vec3(h, s, b));
    } else {
        for (float i = 0.; i < cells; i++) {
            vec2 n = N22(vec2(i));
            vec2 p = sin(n * t);
            p.x *= iResolution.x / iResolution.y;
            vec2 dVec = uv-p;
            float d = abs(dVec.x)+abs(dVec.y);
            if (d < minD) {
                minD = d;
                id = i / cells;
            }
            float h = id*0.2;
            float s = smoothstep(0.7, -0.6, minD);
            float b = 0.3+id*(1.-minD);

            col = hsb2rgb(vec3(h, s, b));
        }
    }
    fragColor = vec4(col,1.);
}

