        col+=f*textureLod(VidTex,uv,.5+float(i)*1.2);
        sum+=f;
    }
    col/=sum;
    float l=length(c/Res-.5);
	//col*=1.1;
    //col-=.1;
    col=mix(col,vec4(1),clamp(WhiteVign*l*l,0.,1.));
    return clamp(col,0.,.9);
}

