/*
 Looking inside the Mandelbox and Mandalay fractals.
 Step-through showing slices overlaid on full view.
 Mouse-x rotates, -y zooms
*/

#define M_TYPE   0  // = 0/1 for Mandelbox / Mandalay fractals

#define VAR_ZERO min (iFrame, 0)

float PrBoxDf (vec3 p, vec3 b);
vec3 HsvToRgb (vec3 c);
float Minv3 (vec3 p);
float Maxv3 (vec3 p);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);

vec3 ltDir, pFold;
float tCur, dstFar, mScale, bxLim, slPos, slWid;
int mType;
const int itMax = 12;
bool isTrans;
const float pi = 3.14159;

float PPFoldD (vec3 p)
{
  vec3 s;
  p.y = max (p.y, p.z);
  s = vec3 (p.x, max (abs (p.x - pFold.x) - pFold.x, p.y - 4. * pFold.x),
     max (p.x - 2. * pFold.x - pFold.y, p.y - pFold.z));
  return Minv3 (s);
}

vec3 PPFold (vec3 p)
{
  return vec3 (PPFoldD (p), PPFoldD (p.yzx), PPFoldD (p.zxy));
}

float ObjDf (vec3 p)
{
  vec4 p4;
  float d;
  p4 = vec4 (p, 1.);
  for (int j = 0; j < itMax; j ++) {
    p4.xyz = 2. * clamp (p4.xyz, -1., 1.) - p4.xyz;
    if (mType == 1) p4.xyz = - sign (p4.xyz) * PPFold (abs (p4.xyz));
    p4 = mScale * p4 / clamp (dot (p4.xyz, p4.xyz), 0.25, 1.) + vec4 (p, 1.);
  }
  d = max (length (p4.xyz) / p4.w, PrBoxDf (p, vec3 (bxLim)));
  if (! isTrans) d = max (d, abs (p.z - slPos) - slWid);
  return d;
}

vec3 ObjTDist (vec3 p)
{
  vec4 p4;
  vec3 pMin;
  pMin = vec3 (1.);
  p4 = vec4 (p, 1.);
  for (int j = 0; j < itMax; j ++) {
    p4.xyz = 2. * clamp (p4.xyz, -1., 1.) - p4.xyz;
    if (mType == 1) p4.xyz = - sign (p4.xyz) * PPFold (abs (p4.xyz));
    pMin = min (pMin, abs (p4.xyz));
    p4 = mScale * p4 / clamp (dot (p4.xyz, p4.xyz), 0.25, 1.) + vec4 (p, 1.);
  }
  return pMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, h, s, sLo, sHi, eps;
  eps = 0.0005;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = VAR_ZERO; j < 160; j ++) {
    h = ObjDf (ro + s * rd);
    if (h < eps || s > dstFar) {
      sHi = s;
      break;
    }
    sLo = s;
    s += h;
  }
  if (h < eps) {
    for (int j = VAR_ZERO; j < 5; j ++) {
      s = 0.5 * (sLo + sHi);
      if (ObjDf (ro + s * rd) > eps) sLo = s;
      else sHi = s;
    }
    dHit = sHi;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0002, -0.0002);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, colT, vn, roo;
  float dstObj, dstObjT;
  bool isBg;
  mScale = (mType == 1) ? 3. : 2.5;
  roo = ro;
  isTrans = false;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    isBg = false;
    ro += dstObj * rd;
    vn = ObjNf (ro);
    col = HsvToRgb (vec3 (mod (0.2 + 0.15 * Maxv3 (abs (ObjTDist (ro))), 1.), 0.5, 1.)) *
       (0.3 + 0.7 * max (dot (vn, ltDir), 0.)) +
       0.1 * pow (max (0., dot (ltDir, reflect (rd, vn))), 16.);
   } else {
    isBg = true;
    col = vec3 (0.3, 0.3, 0.3);
  }
  isTrans = true;
  ro = roo;
  dstObjT = ObjRay (ro, rd);
  if (dstObjT < min (dstObj, dstFar)) {
    ro += dstObjT * rd;
    vn = ObjNf (ro);
    colT = HsvToRgb (vec3 (mod (0.2 + 0.15 * Maxv3 (abs (ObjTDist (ro))), 1.), 0.5, 1.)) *
       (0.3 + 0.7 * max (dot (vn, ltDir), 0.));
    colT = mix (colT, vec3 (0.6, 0.65, 0.6) * smoothstep (0., 1., Maxv3 (colT)), 0.7);
    col = mix (col, colT, (isBg ? 0.35 : 0.15));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col[2];
  vec2 canvas, uv;
  float el, az, zmFac, tCyc;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  mType = M_TYPE;
  bxLim = (mType == 1) ? 5. : 2.8;
  if (mType == 1) pFold = HsvToRgb (vec3 (0.5, 0.2, 1.));
  dstFar = 80.;
  az = -0.25 * pi;
  el = 0.;
  zmFac = (mType == 1) ? 5.6 : 5.2;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    zmFac += (mType == 1) ? 15. * (mPtr.y + 0.5) : 30. * (mPtr.y + 0.5);
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -6.6 * bxLim);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  ltDir = vuMat * normalize (vec3 (1., 2., -1.));
  slWid = 0.05;
  tCyc = 100.;
  tCur *= 1.5;
  for (int k = VAR_ZERO; k <= 1; k ++) {
    slPos = (bxLim - slWid) * (4. * (0.5 - abs (mod ((float (k) +
       floor (tCur)), tCyc) / tCyc - 0.5)) - 1.);
    col[k] = ShowScene (ro, rd);
  }
  fragColor = vec4 (mix (col[0], col[1], smoothstep (0.1, 0.9, fract (tCur))), 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float random(vec2 v, float seed) {
    return fract(sin(dot(v, vec2(25.2332, 19.399)))*seed);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    float randomSeed = 98183.232;

    float uvScale = 15.;
    vec2 scaleLayer = uv * uvScale;

    // Snow = finer resolution than the grid
    float snowScale = uvScale * 6.;
    vec2 snowLayer = uv * snowScale;
    vec2 snowGrid = floor(snowLayer);
    // Only update snow ever 1s
    float snowUpdate = floor(iTime);
    float snow = random(snowGrid * snowUpdate, randomSeed);
    // gridSquares fall at random speed by row
    float gridX = floor(scaleLayer.x);
    float gridY = floor(scaleLayer.y);
    float gridYSpeed = random(vec2(gridX+1.), randomSeed) * 20.;
    float minSpeed = 5.;
    vec2 gridIdx = floor(scaleLayer + vec2(0.0, (gridYSpeed + minSpeed) * iTime));

    float gridValue = random(gridIdx, randomSeed);
    gridValue = step(0.756, gridValue);


    vec3 color = vec3(0.);
    color = mix(color, vec3(snow), gridValue);

    fragColor = vec4(color,1.0);
}

const vec4 FLOOR_VEC       = vec4(0.0, 1.0, 0.0, 3.0);
const vec3 LONG_BOX_POS    = vec3(2.1, -0.5, -2);
const vec3 LONG_BOX_SCALE  = vec3(1.5, 3.0, 1.5);
const vec3 SPHERE_POS      = vec3(2.2, -1.0, 1.7);
const vec3 TORUS_POS       = vec3(0.5, 2.0, 1);
const vec2 TORUS_RADIUS    = vec2(1.0, 0.45);

const vec3 EYE_POS         = vec3(0.0, 2.5, -14.5);
const vec3 REF_POS         = vec3(0.0, 2.5, 0.0);

const int RAY_STEPS     = 256;
const int FLOOR_ID      = 0;
const int LONG_BOX_ID   = 1;
const int SPHERE_ID     = 2;
const int TORUS_ID      = 3;
const float PI_OVER_180 = 3.1415926 / 180.0;

// The higher the value, the smaller the penumbra
const float SHADOW_HARDNESS = 7.0;

// 0 for penumbra shadows, 1 for hard shadows
#define HARD_SHADOW 0

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

float plane(vec3 p, vec4 n)
{
  // n must be normalized
  return dot(p, n.xyz) + n.w;
}

// Box with side lengths b
float box(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}


float sphere(vec3 p, float r, vec3 c)
{
    return distance(p, c) - r;
}

float torus(vec3 p, vec2 t)
{
    vec2 q = vec2(length(p.xz) - t.x, p.y);
    return length(q) - t.y;
}

vec3 rotateY(vec3 p, float a) 
{
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

vec3 rotateX(vec3 p, float amt) 
{
    return vec3(p.x, cos(amt) * p.y - sin(amt) * p.z, sin(p.y) + cos(p.z));
}


void sceneMap3D(vec3 pos, out float t, out int objId)
{
    // floor
    t = plane(pos, FLOOR_VEC);  
    objId = FLOOR_ID;  

    float t2;
    if((t2 = box(rotateY(pos + LONG_BOX_POS, 27.5 * PI_OVER_180), LONG_BOX_SCALE)) < t)  // long bix
    {
        t= t2;
        objId = LONG_BOX_ID;
    }
    if((t2 = sphere(pos, 2.0, SPHERE_POS)) < t)
    {
        t= t2;
        objId = SPHERE_ID;
    }
    if((t2 = torus(pos + TORUS_POS, TORUS_RADIUS)) < t)
    {
        t= t2;
        objId = TORUS_ID;
    }
}

float sceneMap3D(vec3 pos)
{
    float t = plane(pos, FLOOR_VEC);
    t = min(t, box(rotateY(pos + LONG_BOX_POS, 27.5 * PI_OVER_180), LONG_BOX_SCALE));
    t = min(t, sphere(pos, 2.0, SPHERE_POS));
    t = min(t, torus(pos + TORUS_POS, TORUS_RADIUS));
    return t;
}

void march(vec3 origin, vec3 dir, out float t, out int objId)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, objId);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }

    t = -1.0;
    objId = -1;
}

vec3 computeMaterial(int objId, vec3 p, vec3 n)
{
    switch(objId) 
    {
    case FLOOR_ID:  // floor
    {
        return vec3(1.0, 1.0, 1.0);
        break;
    }
    case LONG_BOX_ID:  // Cuboid, the long box
    {
        return vec3(1.0, 0.4, 0.0);
        break;
    }
    case SPHERE_ID:  // Sphere
    {
        return vec3(0.0, 1.0, 1.0);
        break;
    }   
    case TORUS_ID:  // Torus
    {
        return vec3(0.9, 0.2, 0.9);
        break;
    }
    default:
        return vec3(0.0);
        break;
    }

    return vec3(1.0);
}

vec3 computeNormal(vec3 pos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize(vec3(sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                          sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                          sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

float hardShadow(vec3 dir, vec3 origin, float min_t)
{
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        float m = sceneMap3D(origin + t * dir);
        if(m < 0.0001) 
        {
            return 0.0;
        }
        t += m;
    }

    return 1.0;
}

float softShadow(vec3 dir, vec3 origin, float min_t, float k) 
{
    float res = 1.0;
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) 
    {
        float m = sceneMap3D(origin + t * dir);
        if(m < 0.0001) 
        {
            return 0.0;
        }

        res = min(res, k * m / t);
        t += m;
    }

    return res;
}

float shadow(vec3 dir, vec3 origin, float min_t) 
{
    #if HARD_SHADOW
    return hardShadow(dir, origin, min_t);
    #else
    return softShadow(dir, origin, min_t, SHADOW_HARDNESS);
    #endif
}


Intersection sdf3D(vec3 dir, vec3 eye)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect);

    vec3 material = computeMaterial(hitObj, isect, nor);

    vec3 warmDir = rotateY(normalize(vec3(1.0, 1.0, 1.0)), sin(iTime * 0.5));
    vec3 coolDir = rotateY(normalize(vec3(-1.0, 0.0, -1.0)), sin(iTime * 0.5));

    float warmDot = max(0.0, dot(nor, warmDir));
    float coolDot = max(0.0, dot(nor, coolDir));

    vec3 overallColor = warmDot * vec3(1.0, 0.88, 0.7) * shadow(warmDir, isect, 0.1);
    overallColor += coolDot * vec3(0.05, 0.2, 0.5);
    overallColor *= material;
    overallColor = clamp(overallColor + vec3(0.05, 0.1, 0.15), 0.0, 1.0);

    vec3 sdfColor = overallColor;

    return Intersection(t, sdfColor, isect, hitObj);
}

void raycast(vec2 uv, out vec3 dir, out vec3 eye, out vec3 ref)
{
    eye = EYE_POS;
    ref = REF_POS;

    float len = tan(3.14159 * 0.125) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * float(iResolution.x) / float(iResolution.y);
    vec3 p = ref + uv.x * H + uv.y * V;
    dir = normalize(p - eye);
} 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // [-1, 1]
    vec2 uv2 = 2.0 * uv - vec2(1.0);

    vec3 dir, eye, ref;
    raycast(uv2, dir, eye, ref);
    Intersection intersection = sdf3D(dir, eye);

    // Output to screen    
    fragColor = vec4(intersection.color * step(0.0, intersection.t), 1.0);
    float fogT = smoothstep(30.0, 50.0, distance(intersection.p, eye));
    fragColor.rgb = mix(fragColor.rgb, vec3(0.0), fogT);
}

//adapted from https://gist.github.com/thomaswilburn/b9f049875c251496b8143bd86ab587eb
precision highp float;

const vec3 top = vec3(.8, .7, .7);
const vec3 bottom = vec3(.9, .7, .7);
const vec3 plastic = vec3(.8, .2, .1);
float rand(float seed) {
  return fract(sin(dot(vec2(12.87042, 48.382097), vec2(seed, seed + 40238.842))) * 479283.48293);
}

