const float pi=acos(-1.);


//convert distance to smoothly produce line color if within a
//certain threshold
vec3 line(float dist){ 

    return 1.0-vec3(smoothstep(0.,0.05,abs(dist)));
}

//Homotopic function that transtions from circle contour when s=0 
//to square contour when s=1 on the 0 to pi/8 region (for variable t) 
vec2 h(float s,float t){ 
    return vec2(s+(1.-s)*cos(2.*pi*t),s*t/(pi/8.)+(1.-s)*sin(2.*pi*t));
} 
//Ternary search for point and an s defining the current contour
//to find the minimum distance to the contour.
//Assuming distance to contour for a fixed point is convex within the region
//It should be for this specific case.
float ternary(float s,vec2 z){
    //map z to within the pi/8 region
    z=abs(z);
    if(z.y>z.x){ 
        z=z.yx;
    } 
    float l=0.;
    float r=pi/8.;
    float m1,m2;
    vec2 contour1,contour2;
    float mindist; 
    for(int i=0;i<30;i++){
        m1=l+(r-l)/3.;
        m2=r-(r-l)/3.;
        contour1=h(s,m1);
        contour2=h(s,m2); 
        float f1=length(z-contour1); 
        float f2=length(z-contour2);
        if(f1<f2){ 
            r=m2;
            mindist=f1;
        }else{
            l=m1; 
            mindist=f2;
        } 
    } 
    return mindist;
}






