#define PI 3.14159265358979323846

float circle(vec2 st, float r, float i, float aa)
{
    r = r+i*abs(sin(6.0
                    *atan(st.y, st.x)
                    +iTime*1.));
    float d = length(st);
    float d1 = smoothstep(r, r-aa, d);//d<r?1.0:0.0;
    float d2 = smoothstep(r, r-aa, d+0.02);//d<r-0.01?1.0:0.0;
    return d1-d2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(uv, 0.0);
    float mus = texelFetch(iChannel0, ivec2(1, 0), 0).x;
    mus *= .5;

    //circle
    float amp = 0.01;
    float aa = 0.1+
        		pow( (0.5+0.5*sin(PI*iTime+uv.y)), 4.)
        		*0.1;
    float cir1 = 0.;//circle(uv, 0.3, amp, aa);
    for (float i=0.1; i<0.27; i+=0.04)
    	cir1 += circle(uv, i, amp, aa);

    float cir2 = circle(uv, 0.4, amp+0.1, aa+0.02);
    cir1 += cir2;
    // Output to screen
    vec3 A = vec3(0.8, 0.6, 0.1)*(1.0-length(uv));
    vec3 B = vec3(0.1, 0.1, 0.5);
    col = mix(B, A, (1.0-cir1)*(2.3));
    col = clamp(col, 0.0, 1.0);

    //gamma correction
    col *= 0.8+mus*0.4;
    //col = pow(col, vec3(1./2.4));
    fragColor = vec4(col,1.0);
}

//rotate a vector... Not very happy with this huge function...
mat3 rotateXYZ(vec3 t)
{
      float cx = cos(t.x);
      float sx = sin(t.x);
      float cy = cos(t.y);
      float sy = sin(t.y);
      float cz = cos(t.z);
      float sz = sin(t.z);
      mat3 m=mat3(
        vec3(1, 0, 0),
        vec3(0, cx, -sx),
        vec3(0, sx, cx));

      m*=mat3(
        vec3(cy, 0, sy),
        vec3(0, 1, 0),
        vec3(-sy, 0, cy));

      return m*mat3(
        vec3(cz, -sz, 0),
        vec3(sz, cz, 0),
        vec3(0, 0, 1));
}

//SDF-Functions
float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}


float softmin(float f1, float f2, float val)
{
      float e = max(val - abs(f1 - f2), 0.0);
      return min(f1, f2) - e*e*0.25 / val;     
}

float map(vec3 p)
{
      vec3 rotPlane=(rotateXYZ(vec3(1.5,.0,.0))*p);
      vec3 rotCube=(rotateXYZ(vec3(1,iTime,sin(iTime*.5)*.5))*p);
      float myplane=sdRoundBox(rotPlane-vec3(.0,.0,1.5),vec3(20.,20,.01),.1);
      float mycube=sdRoundBox(rotCube+vec3(0.,.5,0.),vec3(.75/2.),.1);
      return(softmin(myplane,mycube,1.));
}


vec3 normal(vec3 p)
{
      vec2 eps=vec2(.005,0);
      return normalize(vec3(map(p+eps.xyy)-map(p-eps.xyy),
                            map(p+eps.yxy)-map(p-eps.yxy),
                            map(p+eps.yyx)-map(p-eps.yyx)));
}

// LIGHT
float diffuse_directional(vec3 n,vec3 l, float strength)
{
      return (dot(n,normalize(l))*.5+.5)*strength;
}

float specular_directional(vec3 n, vec3 l, vec3 v, float strength)
{
      vec3 r=reflect(normalize(l),n);
      return pow(max(dot(v,r),.0),128.)*strength;
}

float ambient_omni(vec3 p, vec3 l)
{
      float d=1.-abs(length(p-l))/100.;
      return pow(d,32.)*1.5;
}



//SHADOW
float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    float ph = 1e20;
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t);
        if( h<0.0001 )
            return .0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return res;
}

// MAINLOOP
void mainImage(out vec4 fragColor, in vec2 fragCoord )
{  
    vec2 uv= fragCoord.xy/iResolution.xy-.5;
    uv.x/=iResolution.y/iResolution.x;
    vec3 ro=vec3(.0,.0,-3.5); 
    vec3 p=ro;
    vec3 rd=normalize(vec3(uv,1.));
    float shading=.0;
    bool hit=false;

    vec3 color;
    while(p.z<20.)
    {
        float d=map(p);
        if(d<.0001)
        {
            hit=true;
            break;
        }
        p += rd*d;
    }

    float t=length(ro-p);
    if (hit)
    {
        shading=length(p*10.);
        vec3 n=normal(p);
        vec3 l1=vec3(1,.5,-.25);
        float rl=ambient_omni(p,l1)*diffuse_directional(n,l1,.5)+specular_directional(n,l1,rd,.9);
        color=vec3(rl)+vec3(.1,.4,.1);
        color*=color;
        vec3 pos = ro + t*rd;
        color=mix(vec3(.0),color,softshadow(pos,normalize(l1),.01,10.0,20.)*.25+.75);
    }
    color*=mix(color,vec3(1.,1.,1.),1.-exp(-.1*pow(t,128.)));
    color-=t*.05;
    color=sqrt(color);

    fragColor=vec4(color,1.);
}

// Source Material:
// Great source for all kinds of computer graphics stuff: http://iquilezles.org/
// Fractals: http://blog.hvidtfeldts.net/index.php/2011/06/distance-estimated-3d-fractals-part-i/

// Videos:
// Ray marching setup and HDR colors: https://www.youtube.com/watch?v=Cfe5UQ-1L9Q
// SDF maths: https://www.youtube.com/watch?v=sl9x19EnKng

float boxSDF(in vec3 p, in vec3 r)
{
    return length(max(abs(p) - r, 0.0));
}

vec3 fold3(in vec3 p, in vec3 n)
{
    return p - 2.0 * min(0.0, dot(p, n)) * n;
}

// Signed distance function that describes the scene
float sceneSDF(in vec3 pos)
{    
    for (int i = 0; i < 15; ++i)
    {
        float t = float(i) * 1.0 + cos(iTime) * 0.18;
        vec3 n = normalize(vec3(cos(t), sin(t), sin(t)));

        pos = fold3(pos, n);

        if (mod(float(i), 2.0) < 1.0)
        {
            float x = n.x;
            n.x = n.z;
            n.z = x;
            pos = fold3(pos, n);
        }
    }

    vec3 off = vec3(1.8, 0, 0);
    return boxSDF(pos - off, vec3(2.7, 0.2, 0.3)) - 0.3;
}

// Approximates the normal of the surface at the given position
// by calculating the gradient of the scene SDF
vec3 calcNormal(in vec3 pos)
{
    vec2 e = vec2(0.0001, 0.0);

    return normalize(vec3(
        sceneSDF(pos + e.xyy) - sceneSDF(pos - e.xyy),
        sceneSDF(pos + e.yxy) - sceneSDF(pos - e.yxy),
        sceneSDF(pos + e.yyx) - sceneSDF(pos - e.yyx)));
}

// Returns the distance to scene surface.
// If the ray hit nothing this returns -1.0
float rayMarchScene(in vec3 rayOrigin, in vec3 rayDir)
{
    float t = 0.0;
    for (int i = 0; i < 100; ++i)
    {
        vec3 pos = rayOrigin + rayDir * t;

        float dist = sceneSDF(pos);
        t += dist;
        if (dist < 0.00001)
            break;
        if (t > 100.0)
            return -1.0;
    }

    return t;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec2 mouse = iMouse.xy / iResolution.xy;
    //vec2 mouse = vec2(0.5, 1.0);
    float time = mouse.x * 6.28;//iTime * 0.2;
    float cameraDist = 2.6;
    float upDownAngle = mouse.y-0.6;

    vec3 rayOrigin = vec3(cos(time), sin(upDownAngle)*1.2, sin(time)) * cameraDist;
    vec3 lookAtPos = vec3(0.0, 0.6, 0.0);

    vec3 forward = normalize(lookAtPos - rayOrigin);
    vec3 right = cross(forward, vec3(0.0, 1.0, 0.0));
    vec3 up = cross(right, forward);

    vec3 rayDir = normalize(forward + right * uv.x + up * uv.y);

    float t = rayMarchScene(rayOrigin, rayDir);

    // Skybox color
    vec3 skyCol = vec3(0.5, 0.2, 0.1) + rayDir.y * 0.1;
    vec3 col = skyCol;
    if (t > 0.0)
    {
        vec3 surfacePos = rayOrigin + rayDir * t;
        vec3 normal = calcNormal(surfacePos);

        col = normal;
        col.x = abs(col.x);
        col.z = -abs(col.z);
        col *= 0.5;
        col += 0.5;
    }

    // Gamma correction
    col = pow(col, vec3(0.4545));    
    fragColor = vec4(col, 1.0);
}

