vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec4 getColor(int n, vec2 zn)
{
    //https://stackoverflow.com/questions/369438/smooth-spectrum-for-mandelbrot-set-rendering
    float sn = (float(n) + 1.0 - log(log(length(zn)))/log(2.0))/float(N);
	return vec4(hsv2rgb(vec3(sn,1,1)), 1);
}

vec4 mandel(in vec2 c)
{
    vec2 z = c;
    int i;
    vec4 color;
    for (int j = 0; j < N; j++) {
        i = j;
		if (length(z) > 2.2)
            break;
        z = vec2(z.x*z.x - z.y*z.y + c.x, 
                 2.0*z.x*z.y + c.y);   	    
    }

    if (i >= N)
        color = vec4(0,0,0,1);
   	else
        color = getColor(i, z);
    return color;
}

