    // final colour
    fragColor.xyz = colour * strength;
}

#define MAX_STEPS 200
#define MAX_DIST 100.
#define SURF_DIST .001

mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}


float intersectSDF(float distA, float distB) {
    return max(distA, distB);
}

float unionSDF(float distA, float distB) {
    return min(distA, distB);
}

float differenceSDF(float distA, float distB) {
    return max(distA, -distB);
}

float sphereSDF(vec3 p, float r) {
    return length(p) - r;
}

float boxSDF(vec3 p, vec3 size) {
    vec3 d = abs(p) - (size / 2.0);
    float insideDistance = min(max(d.x, max(d.y, d.z)), 0.0);

    float outsideDistance = length(max(d, 0.0));

    return insideDistance + outsideDistance;
}

float cubeSDF( vec3 p)
{
  vec3 q = abs(p) - 1.;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float cylinderSDF(vec3 p, float h, float r) {
    float inOutRadius = length(p.xy) - r;
    float inOutHeight = abs(p.z) - h/2.0;
    float insideDistance = min(max(inOutRadius, inOutHeight), 0.0);

    float outsideDistance = length(max(vec2(inOutRadius, inOutHeight), 0.0));

    return insideDistance + outsideDistance;
}

float ballOffset(float offset){
    return 0.4 + 1.0 + sin(1.7 * iTime+offset*3.1415926);
}

float sceneSDF(vec3 samplePoint) {
    samplePoint = rotateY(iTime / 2.0) * samplePoint;

    float cylinderRadius = 0.4 + (1.0 - 0.4) * (1.0 + sin(1.7 * iTime)) / 2.0;
    float cylinder1 = cylinderSDF(samplePoint, 2.0, cylinderRadius);
    float cylinder2 = cylinderSDF(rotateX(radians(90.0)) * samplePoint, 2.0, cylinderRadius);
    float cylinder3 = cylinderSDF(rotateY(radians(90.0)) * samplePoint, 2.0, cylinderRadius);

    float cube = boxSDF(samplePoint, vec3(1.8, 1.8, 1.8));

    float sphere = sphereSDF(samplePoint, 1.2);

    float ballRadius = 0.3;

    float balls = sphereSDF(samplePoint - vec3(ballOffset(1.), 0.0, 0.0), ballRadius);
    balls = unionSDF(balls, sphereSDF(samplePoint + vec3(ballOffset(1.), 0.0, 0.0), ballRadius));
    balls = unionSDF(balls, sphereSDF(samplePoint - vec3(0.0, ballOffset(2.), 0.0), ballRadius));
    balls = unionSDF(balls, sphereSDF(samplePoint + vec3(0.0, ballOffset(2.), 0.0), ballRadius));
    balls = unionSDF(balls, sphereSDF(samplePoint - vec3(0.0, 0.0, ballOffset(3.)), ballRadius));
    balls = unionSDF(balls, sphereSDF(samplePoint + vec3(0.0, 0.0, ballOffset(3.)), ballRadius));


    float csgNut = differenceSDF(intersectSDF(cube, sphere),
                         unionSDF(cylinder1, unionSDF(cylinder2, cylinder3)));

    return unionSDF(balls, csgNut);
}

float GetDist(vec3 p) {

    float planeDist = p.y+1.8;
    float fScene=sceneSDF(vec3(p.x,p.y-1.,p.z));

    float n=min(planeDist,fScene);
    return n;
}


float RayMarch(vec3 ro, vec3 rd) {
    float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 phongContribForLight(vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {

    vec3 k_a = vec3(0.2, 0.2, 0.2);
    vec3 k_d = vec3(1, 1, 1);
    vec3 k_s = vec3(1.0, 1.0, 1.0);
    float shininess = 10.0;
    vec3 N = GetNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = clamp(dot(L, N),0.,1.);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, shininess));
}

vec3 phongIllumination(vec3 p, vec3 eye) {
    vec3 k_a = vec3(0.2, 0.2, 0.2);
    const vec3 ambientLight = 0.6 * vec3(1.0, 0.8, 1.0);
    vec3 color = ambientLight * k_a*1.;

    vec3 light1Pos = vec3(2.0 * sin(iTime),
                          2.0,
                          2.0 * cos(iTime));
    vec3 light1Intensity = vec3(0.3, 0.4, 0.9);

    color += phongContribForLight(p, eye,
                                  light1Pos,
                                  light1Intensity);

    vec3 light2Pos = vec3(2.0 * sin(0.37 * iTime),
                          2.0 * cos(0.37 * iTime),
                          2.0);
    vec3 light2Intensity = vec3(0.7, 0.4, 0.3);

    color += phongContribForLight(p, eye,
                                  light2Pos,
                                  light2Intensity);  
    vec3 light3Pos = vec3(0.0 * sin(0.37 * iTime),
                          -2.0 * cos(0.37 * iTime),
                          -2.0);
    vec3 light3Intensity = vec3(0.7, 0.4, 0.3);

    color += phongContribForLight(p, eye,
                                  light3Pos,
                                  light3Intensity);    

    return color;
}


float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 3, 3);
    lightPos.xz += vec2(sin(iTime), cos(iTime))*2.;
    vec3 lightPos2 = vec3(0, 1, 3);
    lightPos2.xz += vec2(sin(iTime+1.), cos(iTime+1.))*2.;
    vec3 l = normalize(lightPos-p);
    vec3 l2 = normalize(lightPos2-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l);
    if(d<length(lightPos-p)) dif *= .1;
    if(d<length(lightPos2-p)) dif +=clamp(dot(n, l2), 0., 1.)*.1;
    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 1, -5);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float d = RayMarch(ro, rd);

    vec3 p = ro + rd * d;

    float dif = GetLight(p);
    col=phongIllumination(p,ro);
    //col = vec3(dif);

    fragColor = vec4(col,1.0);
}

////////////////////////////////////////////////////////////////////////////////
//
// Interactive Droste spiral
// Matthew Arcus, mla, 2020
//
// Derived from @FabriceNeyret2's reworking of @RadoKirov's original:
// https://www.shadertoy.com/view/3sjyWG
//
// Parameterized things, added mouse control etc.
//
// <mouse>: horizontal = scale factor, vertical = number of spirals
// t: use texture
// g,h: show major and minor grids
// z: autozoom
//
////////////////////////////////////////////////////////////////////////////////

int AA = 2;

float N = 1.0;
// There are M spirals
float M = 2.0;
// Scale factor for each turn
float X = 2.0, logX = log(2.0);

// Grid dimensions
float majorGrid = 2.0;
float minorGrid = 4.0;
const float speed = 3.0;
const float PI = 3.1415927;

float grid(vec2 uv, float g, float r) {
  uv *= g;
  vec2  a = abs(fract(uv+0.5)-0.5);
  // Antialiasing by Fabrice. Thanks.
  float m = min(a.x,a.y)-r;
  return smoothstep(-g,g,m*100.0*log(X*length(gl_FragCoord)));
}

vec2 cmul(vec2 z, vec2 w) {
  return z.x*w + vec2(-z.y*w.y,w.x*z.y);
}

vec2 polar(vec2 z) {
  return vec2(log(length(z)), atan(z.y, z.x));
}

vec2 cartesian(vec2 z) {
  return exp(z.x) * normalize(vec2(cos(z.y),sin(z.y)));
}

const int CHAR_D = 68;
const int CHAR_F = 70;
const int CHAR_G = 71;
const int CHAR_H = 72;
const int CHAR_M = 77;
const int CHAR_R = 82;
const int CHAR_S = 83;
const int CHAR_T = 84;
const int CHAR_U = 85;
const int CHAR_Z = 90;

bool key(int key) {
  return texelFetch(iChannel3,ivec2(key,2),0).x != 0.0;
}

// Smooth HSV to RGB conversion 
// Function by iq, from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb(in vec3 c) {
  vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
  rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	
  return c.z * mix( vec3(1.0), rgb, c.y);
}

vec3 getcolor(vec2 z) {
  vec3 col = vec3(1);
  col = hsv2rgb(vec3(0.5*z+0.5,1));
  if (key(CHAR_T)) col = texture(iChannel0,0.5*z+0.5).xyz;
  if (!key(CHAR_G)) col *= grid(z,majorGrid,0.06);
  if (!key(CHAR_H)) col *= grid(z,minorGrid,0.06);
  return col;
}

void mainImage(out vec4 fragColor, vec2 fragCoord ) {
  vec2 m = vec2(0);
  if (iMouse.x > 0.0) {
    m = iMouse.xy/iResolution.xy;
  }
  X = 2.0+floor(16.1*m.y);
  logX = log(X);
  majorGrid = X;
  minorGrid = 4.0*X;
  M = floor(16.1*(m.x-0.5));
  float t = iTime / speed;
  vec3 col = vec3(0);
  for (int i = 0; i < AA; i++) {
    for (int j = 0; j < AA; j++) {
      vec2 z = (2.0 * (fragCoord+vec2(i,j)/float(AA)) - iResolution.xy) / iResolution.y;
      if (key(CHAR_Z)) z *= exp(-t); // Zooming it
      vec2 e = vec2(N,M*logX/PI/2.0);
      z = cmul(polar(z),e);
      z = cartesian(z);
      z /= exp(mod(t,logX));
      // Multiply z to bring it into range, ie. find power of
      // X that brings max(x,y) into range 1..X
      float tt = ceil(log(max(abs(z.x),abs(z.y)))/logX);
      z *= pow(X,-tt);
      col += getcolor(z);
    }
  }
  fragColor = vec4(col/float(AA*AA),1);
}

//-------------------------------------------------------------------
// Trying out ShaderToy and trying to get to grips with ray marching.
// Inspired by the amazing creations of Evvvvil, Nusan and Shane
//-------------------------------------------------------------------

#define MD 30.		// max distance
#define SD 0.0001    // surface distance
#define MS 250      // max number of steps to march
#define EPS 0.0001   // distance used for sampling the gradient

float time;

// always handy to have PI available
const float PI = acos(-1.);
const float TWOPI = 2.*PI;

// fog color.
const vec3 fog = vec3(.05,.25, .05);

// always need some glow!
float glw = 0.;

// sphere SDF
float sph(vec3 p, float r){return length(p)-r/sin(p.y);}

// min and max which preserve material
vec2 mmin(vec2 a, vec2 b) {return a.x < b.x ? a:b;}
vec2 mmax(vec2 a, vec2 b) {return a.x > b.x ? a:b;}


// 2d rotation
void rot(inout vec2 p, float a) {
    float c=cos(a), s=sin(a);
    p *= mat2(c,-s,s,c);
}

// I was going to do something complicated here,
// but ended up doing a sphere!
vec2 prim(vec3 p) {
    return vec2(sph(p, .5), 5);
}

// the SDF for the scene
vec2 map(vec3 p) {

    // wiggle x
    p.x += sin(p.z + time*.2)*.5;
    // spiral
    rot(p.xy, sin(p.z / 3.));
    // scroll along z
    p.z -= time/3.;

    // repeat everything
    vec3 mp;
    mp.x = mod(p.x, 2.3)-1.15;
    mp.y = mod(p.y, 2.3)-1.15;
    mp.z = mod(p.z,.6)-.3;

    // fractal (ish)
    vec4 ap = vec4(mp,1.);
    // big purple sphere
    vec2 a=prim(mp);
    a.x *= .6;
    // smaller black spheres
    int n=4;
    for(int i=1; i<=n; ++i) {
    	ap *= 2.5; // scale
        ap.xyz = abs(ap.xyz)-vec3(.8); // symmetry
        rot(ap.xy, ap.z*.1 + time*.5); // movement
        vec2 b = prim(ap.xyz); // sphere
        b.x /= ap.w; // correct for scaling
        b.x *= .6;   // patch up the SDF
        b.y= 1.;     // paint it black
        a=mmin(a, b);

    }

    // Sparkles. Lots of them.
    rot(p.xy, time*.3);
    vec2 b = vec2(length(cos(p*1.+vec3(1.5+p.z*.05*sin(time),1.7+sin(time)*.2,time*2.)))-.001 ,6.);
    glw += .1/(.1*b.x*b.x*10000.);
    b.x *= .6;

    // final distance
    vec2 d=mmin(a,b);
    return d;
}

// determine the normal at point p by sampling the gradient of the SDF
vec3 normal(vec3 p) {
 	vec2 off = vec2(EPS, 0.);
    return normalize(map(p).x - vec3(map(p-off.xyy).x,map(p-off.yxy).x,map(p-off.yyx).x));
}

//-------------------------------------------------------------
// lighting and colour,
// heavily based on Evvvvil's Micro Lighting Engine Broski (TM)
//-------------------------------------------------------------

// shortcuts for calculating fake ambient occlusion and subsurface scattering
#define aoc(d) clamp(map(p + n * d).x/d, 0., 1.)
#define sss(d) smoothstep(0.,1.,map(p+ld*d).x/d)

vec3 surface(vec3 ro, vec3 rd, vec2 hit) {
	float d=hit.x;         // distance from ray origin
    float m = hit.y;       // materialID

    // albedo, or base color, based on material ID	
    vec3 al = m < 5. ? vec3(0) : 
    		m > 5. ? vec3(1) :
    		vec3(.1,.1,.4);

    vec3 p = ro + rd * d;  // the point in space
    vec3 n = normal(p);    // normal of the SDF at point p

    vec3 ld = normalize(vec3(-1));     // light direction
    float diff = max(0., dot(n, -ld)); // diffuse illumination

    vec3 lr = reflect(ld, n);          // reflected light ray
    float spec = pow(max(0., dot(lr,-rd)),32.); // specular component

    float frz = pow(max(0.1, 1. - dot(n,-rd)),4.)*.5; // freznel)

    float ao = aoc(.1); // fake AO
    float ss = sss(1.); // fake subsurface scattering

    vec3 col =  al * ao * (diff + ss) + spec; // combine it all together
    col = mix(col, fog, frz);         // fog the edges

    return col; // the final color
}

// ray marching loop
vec3 march(vec3 ro, vec3 rd) {
	float dd=0.1;		// start with a slight z offset
    vec3 col=vec3(0);   // no color initially
    vec3 p=ro + rd * dd;// current point

    for(int i=0; i<MS; ++i) {  // start marching
     	vec2 d = map(p);  // distance to SDF

        if(d.x < SD) { // close to a surface
            // get the color of the surface
            col += d.y > 0. ? surface(ro, rd, vec2(dd, d.y)) : vec3(0.);
            break; // we're done
        }

        if(dd > MD) { // we've reached the max draw distance,
            dd = MD;
            break;    // and we're done
        }

        dd += d.x;     // track the total distance
        p += rd * d.x; // and update the current position
    }

    // blend in some distance-based fog and return
    return mix(fog * pow(max(0., dot(rd, vec3(0,0,-1))),20.), col, exp(-0.003*dd*dd*dd));
}


// given the ray origin, lookat point and uv's, calculate the ray direction
vec3 raydir(vec3 ro, vec3 la, vec2 uv) {
    // x,y and z axes
    vec3 cz = normalize(la-ro);
    vec3 cx = normalize(cross(cz,vec3(0,1,0)));
    vec3 cy = normalize(cross(cx,cz));
    // project and normalize
    return normalize(cx*uv.x + cy*uv.y + cz);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    // make sure that the pixels are square
    uv /= vec2(iResolution.y / iResolution.x, 1);

    // get rid of some artifacts
    time = mod(iTime, 62.39);

    // setup the ray
    vec3 ro = vec3(0,0,1);
    vec3 rd = raydir(ro, vec3(0)-ro, uv);

    // march
    vec3 col = march(ro, rd);

    col += glw;
    // exposure
    col = vec3(1) - exp(-col * 1.1);
    // gamma
    col = pow(col, vec3(1./2.2));
    // final color
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 6.0 * fragCoord/iResolution.xx;

    for (int n = 1; n < 8; n++){
         float i = float(n);
         uv += vec2(0.743 / i * sin(i * uv.x + iTime + 0.322 * i) + 0.808, 0.4613 / i * sin(uv.x + iTime +0.322 * i) + 1.6);
    }
    uv += vec2(0.714 / cos(sin(uv.x + iTime + 0.333) * 3.22) + 0.8, 0.432 / sin(uv.x + iTime +0.322) + 1.43);

    // Time varying pixel color
    vec3 col = vec3(0.322 * sin(uv.x) + 0.909, 0.505 * sin(uv.x) + 0.505, sin(uv.x + uv.x));


    // Output to screen
    fragColor = vec4(sin(col),1.0);
}

#define M_PI 3.14159265358979323846264338

vec3 Rainbow(vec2 uv, float zoomAmnt, float speed, vec2 startFinishSteps){

    float ratioRnbow = uv.x * 20.;
    float angleRnbow = uv.y * 6. * cos(1.1 * speed);
    vec3 rgbRnbow = cos(speed + ratioRnbow + angleRnbow + vec3(0., 1., 2.));


    float ratioStartStep = startFinishSteps.x;
    float ratioFinishStep = startFinishSteps.y;

    vec2 sStep = smoothstep(0., ratioStartStep * zoomAmnt, abs(uv));
	vec2 fStep = smoothstep(ratioFinishStep * zoomAmnt, ratioStartStep * zoomAmnt, abs(uv)); 

    float stepper = sStep.x * fStep.x + sStep.y * fStep.y;

    return stepper * rgbRnbow; 
}

float Hash21(vec2 p) {
    p = fract(p*vec2(123.34, 456.21));
    p += dot(p, p+45.32);
    return fract(p.x*p.y);
}

mat2 Rot(float a){
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}
vec3 Star(vec2 uv, float flare, float zoomAmnt, float rotSpeed){
    float d = length(uv);
    vec3 m = vec3(.04/d);


    vec3 rnb = Rainbow(uv, zoomAmnt, iTime + uv.x * uv.y, vec2(0.3, 0.55));
    float ray = max(0., 1. - abs(uv.x * uv.y * 400.));
    m += ray * (1. + rnb) * (abs(flare) + .4);

    uv *= Rot(M_PI / 4. + iTime * rotSpeed);
    ray = max(0., 1. - abs(uv.x * uv.y * 800.));
    m += ray * (.6 + .5 * rnb) * (abs(flare) + .4);

    m *= smoothstep(1., .2, d);

    return m;
}

vec3 starLayer(vec2 uv, float zoomAmnt, float index){

    vec3 col = vec3(0);
    vec2 gv = fract(uv) -.5;
    vec2 id = floor(uv);

    int y,x;

    for(y = -1; y <=1; y++){
        for(x = -1; x <=1; x++){
            vec2 offs = vec2(x,y);
            float r = Hash21(id + offs + index);
            float size = 1. - .5 * fract(r * 345.223);

            vec3 star = Star(gv - offs - vec2(r, fract(r* 34.)) + .5, sin(iTime * size) * r, zoomAmnt, (-.4 + fract(r * 1723.2)));


    		star *= sin(iTime * (.4 + r)) * .5 + .5; 
            col += star * size * (sin(iTime + index * 32.1) * .5 + .5);
        }
    }

    return col ;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float zoomAmnt = 3.;
    uv *= zoomAmnt;

    vec3 col = vec3(0.);

    int k = 0;
    int AmnLayers = 6;
    for( k = 0; k < AmnLayers; k++){


    	col += starLayer(uv, zoomAmnt, float(k) / float(AmnLayers) * 3.);
    }



    //if(gv.x >.48 || gv.y > .48) col.r = 1.;

    // Output to screen
    fragColor = vec4(col,1.0);
    //fragColor = vec4(vec3(stepper), 1.0);
}

//	Simplex 3D Noise 
//	by Ian McEwan, Ashima Arts
//
vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}
vec4 taylorInvSqrt(vec4 r){return 1.79284291400159 - 0.85373472095314 * r;}

float snoise(vec3 v){ 
  const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
  const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
  vec3 i  = floor(v + dot(v, C.yyy) );
  vec3 x0 =   v - i + dot(i, C.xxx) ;

// Other corners
  vec3 g = step(x0.yzx, x0.xyz);
  vec3 l = 1.0 - g;
  vec3 i1 = min( g.xyz, l.zxy );
  vec3 i2 = max( g.xyz, l.zxy );

  //  x0 = x0 - 0. + 0.0 * C 
  vec3 x1 = x0 - i1 + 1.0 * C.xxx;
  vec3 x2 = x0 - i2 + 2.0 * C.xxx;
  vec3 x3 = x0 - 1. + 3.0 * C.xxx;

// Permutations
  i = mod(i, 289.0 ); 
  vec4 p = permute( permute( permute( 
             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

// Gradients
// ( N*N points uniformly over a square, mapped onto an octahedron.)
  float n_ = 1.0/7.0; // N=7
  vec3  ns = n_ * D.wyz - D.xzx;

  vec4 j = p - 49.0 * floor(p * ns.z *ns.z);  //  mod(p,N*N)

  vec4 x_ = floor(j * ns.z);
  vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

  vec4 x = x_ *ns.x + ns.yyyy;
  vec4 y = y_ *ns.x + ns.yyyy;
  vec4 h = 1.0 - abs(x) - abs(y);

  vec4 b0 = vec4( x.xy, y.xy );
  vec4 b1 = vec4( x.zw, y.zw );

  vec4 s0 = floor(b0)*2.0 + 1.0;
  vec4 s1 = floor(b1)*2.0 + 1.0;
  vec4 sh = -step(h, vec4(0.0));

  vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  vec3 p0 = vec3(a0.xy,h.x);
  vec3 p1 = vec3(a0.zw,h.y);
  vec3 p2 = vec3(a1.xy,h.z);
  vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

// Mix final noise value
  vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  m = m * m;
  return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                dot(p2,x2), dot(p3,x3) ) );
}

vec3 skyColor( vec3 cameraRay, vec3 sunDirection, bool nosun ){
    vec3 skyColor = vec3( 1.0, 1.2, 1.7 );

    float sunHeight = (sunDirection.y + 1.0 ) * 0.5;
    float hozHeight = clamp(smoothstep(-0.2,1.0,sunHeight),0.0,1.0);

    float hozHeightAbs = clamp(smoothstep(0.2,1.0,sunHeight), 0.0,1.0)+0.01;
    float eyeHeight = clamp(( cameraRay.y + 0.1 ),0.0,1.0 ) ;

    vec3 sunColor = mix( vec3(1.2,0.0,0.0), vec3(0.95,0.95,0.7), hozHeightAbs );

    float sunDot = pow( ( dot( cameraRay, sunDirection ) + 1.0 ) * 0.5, 2000.0*pow(eyeHeight,2.0)+1.0);

    vec3 sunGlow = sunColor * sunDot * hozHeightAbs;

    vec3 skyGlow = skyColor * hozHeightAbs;

    float stars = clamp(smoothstep(0.8,1.0,snoise(cameraRay*100.0)),0.0,1.0)*0.3 * clamp(smoothstep(0.1,0.5,cameraRay.y),0.0,1.0) * clamp(smoothstep(0.1,-0.1,sunDirection.y),0.0,1.0);

    float ground = pow(1.0-clamp(-cameraRay.y,0.0,1.0),20.0);


    float fog = pow( 1.0 - abs(cameraRay.y), 2.0 )*(hozHeight+0.2);

    vec3 col = skyGlow*0.4 + sunGlow*0.9 + stars + fog*sunColor*0.1*hozHeight;

    vec3 groundColor = vec3(0.09,0.1,0.08) * clamp( sunDirection.y, 0.0,1.0 ) * sunColor * 2.0;

    if(cameraRay.y<0.0){
    	col= mix(groundColor, col, ground); 
    }

    if(!nosun){
        float sun = clamp(smoothstep(0.9995,0.9996,dot( cameraRay, sunDirection )),0.0,1.0) * clamp(smoothstep(0.0,0.1,cameraRay.y),0.0,1.0)*0.3;
        col+=sun*sunColor;
    }

    return col;
}

float cloudValue( vec3 cloudCoord ){
    float time = iTime * 0.1;
    cloudCoord.z+=time;
	float cloud = snoise(cloudCoord)*0.55+snoise(cloudCoord*2.0+time*0.3)*0.2+snoise(cloudCoord*4.0+time*0.2)*0.2+snoise(cloudCoord*8.0+time*0.5)*0.05+snoise(cloudCoord*16.0)*0.05;
    cloud = ( cloud + 1.0 ) * 0.5;
    return cloud;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy - 0.5 ) * 2.0;

    vec2 mouse = 1.0-( iMouse.xy/iResolution.xy - 0.5 ) * 2.0;

    float aspect = iResolution.x/iResolution.y;

    uv.x*=aspect;

    vec3 sunDirection = normalize( vec3( 0.0,sin(iTime*0.2)*1.0,1.0) );

    vec3 cameraRay = normalize( vec3(uv, 2.0) );

    mat2 rotx = mat2( cos( mouse.y * 3.14159 ),-sin( mouse.y * 3.14159 ),sin( mouse.y * 3.14159 ),cos( mouse.y * 3.14159 ) );
    cameraRay.yz = rotx * cameraRay.yz;

    mat2 roty = mat2( cos( mouse.x * 3.14159 ),-sin( mouse.x * 3.14159 ),sin( mouse.x * 3.14159 ),cos( mouse.x * 3.14159 ) );
    cameraRay.xz = roty * cameraRay.xz;



    vec3 col = skyColor( cameraRay, sunDirection, false );

    vec3 cloudCoord = cameraRay / (cameraRay.y + 0.2 ) * 0.5;

    float cloud = cloudValue( cloudCoord );

    vec3 normal = vec3( cloudValue(cloudCoord + vec3(0.1,0.0,0.0)), cloudValue(cloudCoord + vec3(0.0,0.1,0.0)), cloudValue(cloudCoord + vec3(0.0,0.0,0.1))  );
    normal -= cloud;
    normalize(normal);
    normal.y = abs(normal.y);


    vec3 cloudColor = vec3(cloud);

    float cloudCover = 0.2;

    if( cameraRay.y > 0.0 ){
        cloud = clamp(cloud-pow(1.0-abs(cameraRay.y),50.0)*0.5,0.0,1.0);
        vec3 sunColor = skyColor( sunDirection, sunDirection, true );
        float sunLighting = pow( dot( -normal, sunDirection ) + 1.0, 3.0 );
        float cloudLuma = pow( clamp(sunDirection.y,0.0,1.0),0.8);
        vec3 cloudColor = sunLighting * sunColor * cloudLuma;
        float cloudmix = clamp(smoothstep((0.8 - cloudCover*2.0),1.0,cloud),0.0,1.0);
        col = mix(col, cloudColor,pow(cloudmix,(0.8 - cloudCover) * 2.0));

    }

    col = pow(col,vec3(0.5));
    fragColor = vec4(col,1.0);
}

//noise function from iq: https://www.shadertoy.com/view/Msf3WH
vec2 hash( vec2 p ) 
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

//quintique
vec2 fade5(vec2 f) {return f * f * f * (f * (f * 6.0 - 15.0) + 10.0);}

//cubique
vec2 fade3(vec2 f) {return f*f*(3.0-2.0*f);}

float noise(vec2 p)
{
 	vec2 i = floor( p );
    vec2 f = fract( p );

    vec2 u = fade5(f);

    return mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);

}


float fractal_noise(vec2 p)
{
    float f = 0.0;
	float amp = 1.;
    p = p*2.;
    for(int i = 0; i < 5 ; i++)
    {
     	f += amp/2.*noise(p);
        p = p*2.;
    }

    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
