// the equation of the star shape:
// 4(xÂ²+2yÂ²+zÂ²-1)Â²-(5xâ´z-10xÂ²zÂ³+zâµ)-1=0
// or: 4(xÂ²+2yÂ²+zÂ²-1)Â²-Im((x+zi)âµ)-1=0

float func(in vec3 p){
    vec3 u = p*p;
    float d = u.x+2.0*u.y+u.z-1.0;
    if (d>3.0) return d;  // clipping needed because its degree is odd
    return 4.0*d*d-p.z*(5.*u.x*u.x-10.*u.x*u.z+u.z*u.z)-1.0;
}

vec3 calcGrad(vec3 p){
    const float e = .0001;
	float a = func(p+vec3(e,e,e));
	float b = func(p+vec3(e,-e,-e));
	float c = func(p+vec3(-e,e,-e));
	float d = func(p+vec3(-e,-e,e));
	return vec3(a+b-c-d,a-b+c-d,a-b-c+d)*(.25/e);
}


const vec3 light = normalize(vec3(-0.3, 0.1, 1));

vec3 castRay(vec3 p, vec3 d) {
	float t = 1e-3, dt;
	if (func(p) < 0.0) return vec3(0.0, 0.0, 0.0);
	for (int i = 0; i < 1024; i++) {
		dt = func(p + t * d);
        dt /= length(calcGrad(p + t * d));
		t += 0.5*dt;
        if (dt < 1e-2) {
			p += t * d;
    		vec3 n = normalize(calcGrad(p));
    		if (dot(n, d) > 0.0) n = -n;
    		float dif = clamp(dot(n, light), 0.0, 1.0);
    		return (0.7*dif+0.2*pow(max(dot(d, light),0.0),4.0)+0.4)*vec3(1.0,0.6,0.1);
        }
		if (t > 20.0) break;
	}
    vec3 col = sin(30.0*d.x)+sin(30.0*d.y)+sin(30.0*d.z)>0.0 ?
        vec3(1.0,0.8,0.6) : vec3(0.9,0.6,0.8);
	t = max(dot(d,light), 0.0);
	return (0.3+0.7*t)*col;
}

#define AA 2
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float h = 1.5*cos(0.4*iTime)+1.0;
    float r = sqrt(20.0-h*h)+0.2*(cos(iTime)+1.0);
    vec3 pos = vec3(r*cos(iTime), r*sin(iTime), h);
    float Unit = 0.5*length(iResolution);

    vec3 w = normalize(pos);
    vec3 u=normalize(vec3(-w.y,w.x,0));
    vec3 v=cross(w,u);
    mat3 M=-mat3(u,v,w);

    vec3 col;
    for (int i=0;i<AA;i++) for (int j=0;j<AA;j++) {
        vec3 d=M*vec3(0.5*iResolution.xy-(fragCoord.xy+vec2(i,j)/float(AA)),Unit);
        col += castRay(pos,normalize(d));
    }
    col/=float(AA*AA);

    col.x=pow(col.x,0.75),col.y=pow(col.y,0.75),col.z=pow(col.z,0.75);

    fragColor = vec4(col,1.0);
}

#define Pi 3.141592
#define Pi2 6.2918
#define NumberOfParticles 64

float starline(vec2 relpos,float confradius,float filmsize)
{
	if(abs(relpos.y)>confradius) return 0.0;
	float y=relpos.y/confradius;
	float d=abs(relpos.x/filmsize);
	return sqrt(1.0-y*y)/(0.0001+d*d)*0.00001;
}

float star(vec2 relpos,float confradius,float filmsize)
{
	vec2 rotpos=mat2(cos(Pi/3.0),-sin(Pi/3.0),sin(Pi/3.0),cos(Pi/3.0))*relpos;
	vec2 rotpos2=mat2(cos(Pi/3.0),sin(Pi/3.0),-sin(Pi/3.0),cos(Pi/3.0))*relpos;

    return starline(relpos,confradius,filmsize)+
		starline(rotpos,confradius,filmsize)+
		starline(rotpos2,confradius,filmsize);
}
vec3 palette(float x)
{
	return vec3(
		sin(x*2.0*Pi)+1.5,
		sin((x+1.0/3.0)*2.0*Pi)+1.5,
		sin((x+2.0/3.0)*2.0*Pi)+1.5
	)/2.5;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 screenpos=(2.0*fragCoord.xy-iResolution.xy)/max(iResolution.x,iResolution.y);

	float focaldistance=0.5+sin(iTime*0.05)*0.013;
	float focallength=0.100;
	float filmsize=0.036;
	float minconf=filmsize/10000.0;
	float lensradius=focallength/(1.5 + (.5 * 1.0+cos(iTime )) ) ;

	float filmdistance=1.0/(1.0/focallength-1.0/focaldistance);

	vec3 c=vec3(0.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0.0,0.0,0.0);// 0.25*sin(iTime+uv.xyx+vec3(0,2,4));

    float r = iResolution.y * .2;
    float r2 = r * r;

    float x = iResolution.x * .5;
    float y = iResolution.y * .5;

    float xPos = fragCoord.x - x;
    float yPos = fragCoord.y - y;

    xPos = xPos * xPos;
    yPos = yPos * yPos;

    if( xPos + yPos < r2  ){
	    //col =  .5 + 0.5 * sin( 10.0 * iTime + uv.xyx + vec3(0, 2, 4));
    }

     //(x0 + r cos theta, y0 + r sin theta)

    for(int i=0;i<NumberOfParticles;i++)
	{
        float fi = float(i);
        float fNum = float(NumberOfParticles);
		float t= fi / fNum;
		float a= t * Pi2 + iTime*0.1;

		//vec3 pos = vec3( sin(2.0*a), -cos(2.0*a), -sin(3.0*a)) * 0.01;
		vec3 pos = vec3((.5 +cos(iTime)) + (r * sin(  a )) , (r * -sin( 2.0 * a )), 0) * .0002;

		pos.z+= 0.5 + (.01* (sin(sin(iTime))));


		float intensity=0.00005 + 0.00005 *(cos(iTime * 1.0)) ;

		vec2 filmpos=pos.xy/pos.z*filmdistance;
		float confradius=lensradius*filmdistance*abs(1.0/focaldistance-1.0/pos.z)+minconf;

		float diffusedintensity=intensity/(confradius*confradius);

		vec3 colour = palette(t);

		vec2 relpos=filmpos-screenpos/2.0*filmsize;
		if(length(relpos)<confradius) c+=colour*diffusedintensity;

		c+=colour*diffusedintensity*star(relpos,confradius,filmsize);
	}


    // Output to screen    
    fragColor = vec4(pow(c,vec3(1.0/1.0)),1.0) +  vec4( pow(col,vec3(1.0/2.2)),1.0);

}

