// displacement mapping alike don't take the code too serious

float pch(vec2);
#define t iTime

float f(vec3 p)
{
    float f = 0., 
          z = .05 * t + p.z, 
          v = 1. - smoothstep(0., 2., length(p));
    for(float i = 10.; i > 0.; --i)
    {
        float c = p.y * sin(i / 1.57 + z), 
              s = p.x * cos(i / 1.57 + z), 
              b = v * mod(1.57 * v, .636);
        f += min(v, .0125 / abs(b +(fract(.05*t)>.5?2.:1.) * (c + s)));
    }
    return (p + normalize(smoothstep(0., f, vec3(.15, 1, .5)))).z;
}

void mainImage(out vec4 O, in vec2 U)
{
    vec2 R = iResolution.xy, e = vec2(.001, 0);
    U = (U - .5 * R) / R.y;

    vec3 c, p, N;

    vec3 o = vec3(0, 0, .5+(sin(.2*t)*.2+.2)), 
         d = vec3(U, -1),
         dn = vec3(0, 0, 1.), 
         D = dot(dn, o) / dot(dn, d) * d - o;

    vec3 ld = normalize(vec3(.7 * sin(.5 * t), cos(.5 * t), 1));
    vec3 l = -normalize(ld + d);
    float lr = exp2(.1 * dot(l, l));

    float h; 
    for(int i = 10; i > 0; --i)
        	h += f(p = (D * h));

    N = normalize(f(p) - vec3(f(p - e.xyy), f(p - e.yxy), f(p - e.yyx)));

    c = mix(vec3(.2, .6, .95), 
            max(dot(N, l), .05) * lr * vec3(.8), 
            fract(.05 * t + .5 + h) + .5);
    c += vec3(.1, .2, .2) * max(0., .8 - dot(reflect(l, N), -d)) * fract(.5 * h);

    O = vec4(vec3(.4)* pch(U) + c , 1.);
}

