#define EPS .0001
#define NORMAL_EPS .0001
#define PI 3.14159265359
#define PI2 PI * 2.

#define MAT_SPHERE 1.
#define MAT_BOX 2.
#define MAT_FLOOR 3.

#define saturate(a) clamp(a, 0., 1.)
#define repeat(a, b) mod(a, b) - b * .5 

precision highp float;

struct Light {
	vec3 position;
  	float intensity;
  	vec3 color;
  	vec3 diffuse;
  	vec3 specular;
  	float attenuation;
};

struct Surface {
  	float depth;
  	vec3 position;
  	vec3 baseColor;
    vec3 specularColor;
    float specularPower;
  	vec3 normal;
  	vec3 emissiveColor;
  	float material;
};

const float stopThreshold = .001;
const int maxShadowIterations = 32;

vec3 glow = vec3(0.);

//---------------------------------------------------------------------------------------------
// utils
//---------------------------------------------------------------------------------------------

// ref: https://www.shadertoy.com/view/ldlcRf
vec2 minMat(vec2 d1, vec2 d2) {
	return (d1.x < d2.x) ? d1 : d2;
}

bool checkMat(float material, float check) {
	return material < (check + .5);
}

mat3 camera(vec3 o, vec3 t) {
	vec3 forward = normalize(t - o);
    vec3 right = cross(forward, vec3(0., 1., 0.));
    vec3 up = cross(right, forward);
    return mat3(right, up, forward);
}

mat2 rot2(float t) {
    return mat2(cos(t), -sin(t), sin(t), cos(t));
}

//---------------------------------------------------------------------------------------------
// sdf
//---------------------------------------------------------------------------------------------

// ra: radius
// rb: round
// h: height
float sdRoundedCylinder(vec3 p, float ra, float rb, float h) {
    vec2 d = vec2(length(p.xz) - 2. * ra + rb, abs(p.y) - h);
    return min(max(d.x, d.y), 0.) + length(max(d, 0.)) - rb;
}

// a: begin pos
// b: begin thin
// ra: end pos
// rb: end thin
float sdStick(in vec3 p, vec3 a, vec3 b, float ra, float rb) {
	vec3 ba = b - a;
    vec3 pa = p - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
    float r = mix(ra, rb, h);
	return length(pa - h * ba) - r;
}

float sdElipsoid(in vec3 pos, vec3 rad) {
	float k0 = length(pos / rad);
	float k1 = length(pos / rad / rad);
	return k0 * (k0 - 1.) / k1;
}

float sdPlane(vec3 p, vec4 n) {
  return dot(p, n.xyz) + n.w;
}

float sdBox(vec3 p, vec3 b) {
  vec3 d = abs(p) - b;
  return min(max(d.x, max(d.y, d.z)), 0.) + length(max(d, 0.));
}

float smin(in float a, in float b, float k) {
    float h = max(k - abs(a - b), 0.);
	return min(a, b) - h * h / (k * 4.);
}

float smax(in float a, in float b, float k) {
    float h = max(k - abs(a - b), 0.);
	return max(a, b) + h * h / (k * 4.);
}

//---------------------------------------------------------------------------------------------
// raymarch
//---------------------------------------------------------------------------------------------

vec2 scene(vec3 p) {
    // dummy float max and min
    vec2 res = vec2(10000., -10000.);
    float d = 0.;

    {
		float pl = sdPlane(p - vec3(0., 2., 0.), vec4(0., 1., 0., 2.));
        res = minMat(res, vec2(pl, MAT_FLOOR));
    }
    {
	    float s1 = sdElipsoid(p - vec3(0., .8, 0.), vec3(.5));        
        res = minMat(res, vec2(s1, MAT_SPHERE));
    }
    {
		float b1 = sdBox(p - vec3(1., .25, 0.), vec3(.2, .5, .2));
        res = minMat(res, vec2(b1, MAT_BOX));
    }
    {
		float b2 = sdBox(p - vec3(-1., .25, 0.), vec3(.2, .5, .2));
        res = minMat(res, vec2(b2, MAT_BOX));
    }

    return res;
}

vec3 getNormal(vec3 p) {
    vec2 e = vec2(NORMAL_EPS, 0);
    return normalize(
    	vec3(
            scene(p + e.xyy).x - scene(p - e.xyy).x,
            scene(p + e.yxy).x - scene(p - e.yxy).x,
            scene(p + e.yyx).x - scene(p - e.yyx).x
        )
    );
}

vec2 raymarch(vec3 ro, vec3 rd) {
	float tmin = .01;
    float tmax = 80.;
    float m = -1.;
    float t = tmin;
    for(int i = 0; i < 99; i++) {
		vec3 pos = ro + rd * t;
        vec2 h = scene(pos);
        m = h.y;
        if(abs(h.x) < (stopThreshold * t)) break;
        t += h.x;
        if(t > tmax) break;
    }
    if(t > tmax) m = -1.;
    return vec2(t, m);
}

//---------------------------------------------------------------------------------------------
// shading
//---------------------------------------------------------------------------------------------

float softShadow(vec3 ro, vec3 rd, float mint, float tmax, float power) {
  float res = 1.;
  float t = mint;
  float ph = 1e10;
  for(int i = 0; i < maxShadowIterations; i++) {
    float h = scene(ro + rd * t).x;

    // pattern 1
    // res = min(res, power * h / t);

    // pattern 2
    float y = h * h / (2. * ph);
    float d = sqrt(h * h - y * y);
    res = min(res, power * d / max(0., t - y));
    ph = h;

    t += h;

    float e = EPS;
    if(res < e || t > tmax) break;
  }
  return clamp(res, 0., 1.);
}


float getSpecular(Surface surface, vec3 lightDir, float diffuse, vec3 cameraPos) {
  	//vec3 lightDir = light.position - surface.position;
  	vec3 ref = reflect(-normalize(lightDir), surface.normal);
  	float specular = 0.;
  	if(diffuse > 0.) {
    	specular = max(0., dot(ref, normalize(cameraPos - surface.normal)));
    	float specularPower = surface.specularPower;
    	specular = pow(specular, specularPower);
  	}
  	return specular;
}

void calcDirectionalLight(inout Light light, Surface surface, vec3 cameraPos) {
    light.position = normalize(light.position);
    light.attenuation = 1.;

    // diffuse
  	float diffuseCoef = max(0., dot(surface.normal, normalize(light.position)));
  	vec3 diffuse = diffuseCoef * light.attenuation * light.color * light.intensity;
  	// specular
  	float specularCoef = getSpecular(
        //light,
        surface,
        light.position,
        diffuseCoef,
        cameraPos
    );
  	vec3 specular = vec3(specularCoef * light.attenuation * light.color * light.intensity);  

  	light.diffuse = diffuse * softShadow(surface.position, normalize(light.position), .1, 10., 10.);
    light.specular = specular;
}

void calcPointLight(inout Light light, Surface surface, vec3 cameraPos) {
  	float d = distance(light.position, surface.position);
  	vec3 k = vec3(.06, .08, .09);
  	light.attenuation = 1. / (k.x + (k.y * d) + (k.z * d * d));

  	// point light
  	vec3 lightDir = normalize(light.position - surface.position);
  	// diffuse
  	float diffuseCoef = max(0., dot(surface.normal, lightDir));
  	vec3 diffuse = diffuseCoef * light.color * light.intensity * light.attenuation;
  	// specular
  	float specularCoef = getSpecular(
        surface,
        lightDir,
        diffuseCoef,
        cameraPos
    );
  	vec3 specular = vec3(specularCoef * light.attenuation * light.color * light.intensity); 

  	light.diffuse = diffuse * softShadow(surface.position, normalize(light.position), .1, 10., 10.);
  	light.specular = specular;
}

float ambientOcculusion(vec3 pos, vec3 nor) {
	float occ = 0.;
    float sca = 1.;
    for(int i = 0; i < 5; i++) {
    	float h = .01 + .11 * float(i) / 4.;
        vec3 opos = pos + h * nor;
        float d = scene(opos).x;
        occ += (h - d) * sca;
        sca *= .95;
    }
    return clamp(1. - 2. * occ, 0., 1.);
}

vec3 fog(vec3 color, float dist, vec3 fogColor, float b) {
  float fogAmount = 1. - exp(-dist * b);
  return mix(color, fogColor, fogAmount);
}

vec3 lighting(Surface surface, vec3 cameraPos, vec3 cameraDir) {
  	vec3 position = surface.position;

  	vec3 color = vec3(0.);
  	vec3 normal = surface.normal;

  	Light directionalLight;
  	directionalLight.position = vec3(.5, 1., .5);
  	directionalLight.intensity = .7;
  	directionalLight.color = vec3(1., 1., 1.);
  	calcDirectionalLight(directionalLight, surface, cameraPos);

  	Light pointLight;
  	pointLight.position = vec3(sin(iTime) * 2., 2., 1.);
  	pointLight.intensity = .3;
  	pointLight.color = vec3(1., 1., 1.);
  	calcPointLight(pointLight, surface, cameraPos);

  	vec3 diffuse = directionalLight.diffuse + pointLight.diffuse;
  	vec3 specular = directionalLight.specular + pointLight.specular;

    // calc ambient
	float occ = ambientOcculusion(surface.position, surface.normal);
  	float amb = clamp(.5 + .5 * surface.normal.y, 0., 1.);
  	vec3 ambient = surface.baseColor * amb * occ * vec3(0., .08, .1);  

  	color =
        surface.emissiveColor +
        surface.baseColor * diffuse +
        surface.specularColor * specular +
        ambient;  

  	return color;
}

