void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = (fragCoord.xy - iResolution.xy * .5) / min(iResolution.x, iResolution.y);
	vec2 mouse = (iMouse.xy - iResolution.xy * .5) / min(iResolution.x, iResolution.y);

    vec3 ro = vec3(mouse * 3., 4.)  + vec3(0., 3., 0.);
    vec3 target = vec3(0., .5, 0.);
    float fov = 1.5;

    vec3 rd = camera(ro, target) * normalize(vec3(uv, fov));

	vec2 result = raymarch(ro, rd);
	vec3 color = vec3(0.);

    if(result.y > 0.) {
	    float depth = result.x;
    	float material = result.y;    

    	vec3 position = ro + rd * depth;
    	vec3 normal = getNormal(position);

	    Surface surface;
    	surface.depth = depth;
    	surface.position = position;
    	surface.normal = normal;
    	surface.material = result.y;       

    	vec3 objColor = vec3(0.);

        if(checkMat(material, MAT_SPHERE)) {
        	surface.baseColor = vec3(1., .1, .1);
            surface.specularColor = vec3(1.);
        	surface.specularPower = 16.;
        } else if(checkMat(material, MAT_BOX)) {
        	surface.baseColor = vec3(.1, .1, 1.);
            surface.specularColor = vec3(1.);
        	surface.specularPower = 32.;
        } else if(checkMat(material, MAT_FLOOR)) {
        	surface.baseColor = vec3(.1, 1., .1);
            surface.specularColor = vec3(1.);
        	surface.specularPower = 64.;
        }

		color = lighting(surface, ro, rd);
    }

    // fog
    color = fog(color, result.x, vec3(.02, .02, .04), .08);    

    // gamma
    color = pow(saturate(color), vec3(.4545));


    fragColor = vec4(color, 1.);
}

#define VEC3_MAX vec3(1e20,1e20,1e20)
#define VEC3_NULL vec3(0.,0.,0.) // Also works as black




struct CAMERA
{
    vec3 pos;
    vec3 lookat;
	float focal_length;

    vec3 Forward; // Same as Lookat but specifically normalized
    vec3 Right;
    vec3 Up;
};  

vec3 Plane = vec3(0.0, -1.0,0.0);
vec3 SphereCenter = vec3(1.0, 0.0, 0.0);
float SphereRadius = 0.5;

void SetupCamFromFocalLength(out CAMERA cam, in float focal_length, vec3 expected_pos)
{
    // at focal Length 1., at distance sqrt(3), object of size .2 is .6

    float ratio = 1. / 50.;
    cam.focal_length = focal_length*ratio;

    cam.pos = expected_pos*(focal_length/10.);

    // Camera is set to (0,0,0)
    cam.lookat = VEC3_NULL;
}

vec3 ex2(in vec2 fragCoord)
{    
   	CAMERA cam;

    // Color variable for use instead of ruining directly fragColor
	vec3 color = vec3(0.0,0.0,0.0);

    // 1 - Setup Camera    
    SetupCamFromFocalLength(cam, 50., vec3(1.,2.,1.));

    // 1.2 - Camera Animation
    // Camera rotates around center at the expected distance calculated with the previous fucntion
    cam.pos *= vec3(sin(iTime/10.),.1,cos(iTime/10.));
    cam.pos *= vec3(1., abs(sin(iTime/5.)), 1.);
    //cam.pos *= vec3(0., 1., 0.);


    // Imagine F to be parallel to z and x vector = Perpendicular to Y
    // We generate Right vector with this, then the real Up Vector
    // cross product between 2 vectors returns a perpendicular one always
    cam.Forward = normalize(cam.lookat - cam.pos);
    cam.Right 	= cross(vec3(0.,1.,0.), cam.Forward);
    cam.Up		= cross(cam.Forward, cam.Right);    

    // 2 - Calculate Center of the screen
    vec3 center = cam.pos + cam.Forward * cam.focal_length;

    // We calculate initial World Coordinate from screen
    vec3 world_coord = vec3((fragCoord - (iResolution.xy / 2.)) / iResolution.y, 0.);

    // Transform it according to the Camera
    vec3 coord = center + world_coord.x*cam.Right + world_coord.y * cam.Up;

    // 3 - Start raying
    vec3 raycastDir = normalize(coord - cam.pos);


    //Plane Grey
    float t = -cam.pos.y+Plane.y/raycastDir.y;

    Plane.x = cam.pos.x+t*raycastDir.x;
    Plane.z = cam.pos.z+t*raycastDir.z;

    vec3 blue = vec3(0.0,0.5,0.5);
    vec3 white = vec3(1.0,1.0,1.0);


    if (t > 0.0)
    {
        color = vec3(0.9,0.9,0.9);
    }
    else
    {
        color = mix(white,blue,coord.y);
    }

    //Sphere                
    // d = Ray Direction


    // Calculate OC
    vec3 OC = SphereCenter-cam.pos;

    //Get t_OCD with OC(L) projected in direction (D)   
    float t_OCD = dot(raycastDir,OC);
    if (t_OCD < 0.) return color;

    //Pythagorean theorem h^2 = co^2 + cc^2 -> co^2 = h^2 - cc^2 -> co = sqrt(h^2-cc^2) -> distance_oc_ocd = sqrt(OC^2-t_OCD^2)     
    float distance_OC_OCD = dot(OC,OC) - t_OCD*t_OCD;

    float t_OC_OCD_R = sqrt(pow(SphereRadius,2.) - pow(distance_OC_OCD,2.0));
    if (t_OC_OCD_R > SphereRadius) return color;

    vec3 intersection1 = (t_OCD - t_OC_OCD_R)*raycastDir + cam.pos;
    vec3 intersection2 = (t_OCD + t_OC_OCD_R)*raycastDir + cam.pos;

    if(length(intersection1 - cam.pos) < length(intersection2 - cam.pos) && (t_OCD - t_OC_OCD_R) > 0.)
    {

        return color = vec3(1.0,0.0,0.0);
    }

    if(length(intersection2 - cam.pos) < length(intersection1 - cam.pos) && (t_OCD + t_OC_OCD_R) > 0.)
    {
        return color = vec3(0.0,1.0,0.0);
    }    
    return color;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec3 color = ex2(fragCoord);

    // Output to screen
    fragColor = vec4(color,1.0);
}

// The MIT License

float sdHexagramVesica(vec2 p, float r, float d)
{ 
    //Hexagram stuff by iq
 	const vec4 k = vec4(-0.5,0.86602540378,0.57735026919,1.73205080757);

    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
 	p -= 2.0*min(dot(k.yx,p),0.0)*k.yx;
    p -= vec2(clamp(p.x,r*k.z,r*k.w),r);

   // p = abs(p) - r;//optional fun
    p = abs(p);//abs again to prevent wigging out on inside, and make it hollow

    //IQ Vesica code
    float b = sqrt(r*r-d*d);  // can delay this sqrt by rewriting the comparison
    return ((p.y-b)*d > p.x*b) ? length(p-vec2(0.0,b))*sign(d)
                               : length(p-vec2(-d,0.0))-r;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

	float d = sdHexagramVesica( p, 0.37, -0.25*abs(cos(iTime)) )- 0.2*(sin(iTime*1.3));

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-4.0*abs(d));
	col *= 0.8 + 0.2*cos(140.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.005,abs(d)) );


	fragColor = vec4(col,1.0);
}

