// Box with side lengths b
float box(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}


float plane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float sphere(vec3 p, float r, vec3 c)
{
    return distance(p, c) - r;
}

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}



void sceneMap3D(vec3 pos, out float t, out int hitObj, vec3 lightPos)
{
    // back wall
    t = plane(pos, vec4(0.0, 0.0, -1.0, 5.0));  
    hitObj = 0;  // 0 is the back wall

    float t2;

    if((t2 = plane(pos, vec4(-1.0, 0.0, 0.0, 5.0))) < t)  // right wall
    {
        t= t2;
        hitObj = 1;
    } 
    if((t2 = plane(pos, vec4(1.0, 0.0, 0.0, 5.0))) < t)  // left wall
    {
        t= t2;
        hitObj = 2;
    }
    if((t2 = plane(pos, vec4(0.0, -1.0, 0.0, 7.5))) < t)  // celling 
    {
        t= t2;
        hitObj = 3;
    }
    if((t2 = plane(pos, vec4(0.0, 1.0, 0.0, 2.5))) < t)  // floor
    {
        t= t2;
        hitObj = 4;
    }
    if((t2 = box(rotateY(pos + vec3(-2.2, 0.5, -3), -27.5 * 3.14159 / 180.0), vec3(1.5, 3.0, 1.5))) < t)  // large box
    {
        t= t2;
        hitObj = 5;
    }
    if((t2 = sphere(pos, 1.5, vec3(-1.5, 1.0, -1.0))) < t)  // sphere
    {
        t= t2;
        hitObj = 6;
    }
}

float sceneMap3D(vec3 pos)
{
    float t = plane(pos, vec4(0.0, 0.0, -1.0, 5.0));

    t = min(t, plane(pos, vec4(-1.0, 0.0, 0.0, 5.0)));
    t = min(t, plane(pos, vec4(1.0, 0.0, 0.0, 5.0)));
    t = min(t, plane(pos, vec4(0.0, -1.0, 0.0, 7.5)));
    t = min(t, plane(pos, vec4(0.0, 1.0, 0.0, 2.5)));
    t = min(t, box(rotateY(pos + vec3(-2.2, 0.5, -3), -27.5 * 3.14159 / 180.0), vec3(1.5, 3.0, 1.5)));
    t = min(t, sphere(pos, 1.5, vec3(-1.5, 1.0, -1.0)));

    return t;
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, hitObj, lightPos);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    // No intersection
    t = -1.0;
    hitObj = -1;
}

vec3 computeNormal(vec3 pos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                            sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                            sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

vec2 sphereUV(vec3 center, float r, vec3 p)
{
    vec3 pDir = normalize(p - center);
    vec3 worldUp = vec3(0.0, 1.0, 0.0);
    float theta = acos(dot(pDir, worldUp));
    // If p is located at the positive part of z axis, then phi is 0-180.
    // If p is located at the negative part of z axis, then phi is 180-360.
    vec3 xzDir = normalize(vec3(pDir.x, 0.0, pDir.z));
    float phi = acos(dot(xzDir, vec3(1.0, 0.0, 0.0)));

    if(pDir.z < 0.0)
    {
        phi = phi + 3.14159;
    }

    return vec2(theta / 3.14159, phi / (2.0 * 3.14159));
}

void planeUVAxis(in vec3 p, in vec3 n, out vec3 u, out vec3 v)
{
    vec3 worldUp = vec3(0.0, 1.0, 0.0);
    u = normalize(cross(worldUp, n));
    v = normalize(cross(n, u));
}

vec2 planeUV(vec3 origin, vec3 n, vec3 p)
{
    vec3 uAxis;
    vec3 vAxis;
    planeUVAxis(origin, n, uAxis, vAxis);
    vec3 diff = p - origin;
    float uVal = dot(diff, uAxis);
    float vVal = dot(diff, vAxis);
    return vec2(uVal, vVal);
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 lightDir, vec3 view)
{
    switch(hitObj)
    {
        case 0:  // back wall
        vec3 normal = vec3(0.0, 0.0, -1.0);
        vec2 uv0 = planeUV(normal * 5.0, normal, p);
        float worleyNoiseUV = WorleyNoise(uv0);
        vec3 color0 = cosinePalette(1.0 - worleyNoiseUV) * dot(lightDir, n);
        return color0;
        break;

        case 1:  // right wall 
        vec3 color1 = vec3(1.0, 0.0, 0.0) * dot(lightDir, n);
        return color1;
        break;

        case 2:  // left wall
        vec3 color2 = vec3(0.0, 1.0, 0.0) * dot(lightDir, n);
        return color2;
        break;

        case 3:  // celling
        vec3 color3 = vec3(1.0, 1.0, 1.0) * dot(lightDir, n);
        return color3;
        break;

        case 4:  // floor
        vec3 normal1 = vec3(0.0, 0.0, -1.0);
        vec2 uv4 = planeUV(normal1 * 5.0, normal1, p);      
        uv4.x = (uv4.x + sin(iTime * 0.3) + 1.0) / 3.0;
        uv4.y = (uv4.y + sin(iTime * 0.3) + 1.0) / 3.0;        
        float fbmUV4 = fbm(uv4);
        vec3 color4 = cosinePalette1(fbmUV4);        
        return color4 * dot(lightDir, n); 
        break;

        case 5:  // large box
        vec3 noisePosition = vec3(p.x + sin(iTime * 0.5), p.y + sin(iTime * 0.5), p.z + sin(iTime * 0.5));
        float perlinNoise = PerlinNoise3D(noisePosition);
        vec3 color5 = cosinePalette(perlinNoise);
        return color5 * dot(lightDir, n);
        break;

        case 6:  // sphere
        vec2 uv6 = sphereUV(vec3(2.0, 0.0, 1.0), 1.0, p);        
        uv6.x = (uv6.x + sin(iTime * 0.2) + 1.0) / 3.0;
        uv6.y = (uv6.y + sin(iTime * 0.2) + 1.0) / 3.0;        
        float fbmUV = fbm(uv6);
        vec3 color6 = cosinePalette1(fbmUV);        
        return color6 * dot(lightDir, n); 
        break;

        case -1:
        return vec3(0., 0., 0.);
        break;

    }
    return vec3(0.0);
}

Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos)
{
	float t;
    int hitObj;

    march(eye, dir, t, hitObj, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect);
    vec3 lightDir = normalize(lightPos - isect);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));
    return Intersection(t, surfaceColor, isect, hitObj);
}

void raycast(vec2 uv, out vec3 dir, out vec3 eye, out vec3 ref)
{

    eye = vec3(0.0, 3.0, -15.0);
    ref = vec3(0.0, 3, 0.0);

    vec3 look = normalize(ref - eye);
    vec3 right = normalize(cross(look, vec3(0.0, 1.0, 0.0)));
    vec3 up = cross(right, look);

    float tanFovV = tan(3.14159 * 0.125);
    float len = distance(ref, eye);
    float aspect = float(iResolution.x) / float(iResolution.y);

    vec3 V = up * len * tanFovV;
    vec3 H = right * len * aspect * tanFovV;

    vec3 p =ref + uv.x * H + uv.y * V;
    dir = normalize(p - eye);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Light
	vec3 lightPos = vec3(0.0, 5.3, -5.0);

    //Normalized pixel coordinate (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    //[-1, 1]
    vec2 uv2 = 2.0 * uv - vec2(1.0);

    vec3 dir, eye, ref;
    // Cast ray
    raycast(uv2, dir, eye, ref);

    // Intersection
    Intersection isect;

    isect = sdf3D(dir, eye, lightPos);



    //Output color
    fragColor = vec4(isect.color, 1.0);
}

#define S(a,b,t) smoothstep(a,b,t)


float N21 (vec2 p){
	float d = fract(sin(p.x*110.+(8.21-p.y)*331.)*1218.);
    return d;
}

float Noise2D(vec2 uv){
    vec2 st = fract(uv);
    vec2 id = floor(uv);
    st = st*st*(3.0-2.0*st);
    float c=mix(mix(N21(id),N21(id+vec2(1.0,0.0)),st.x),mix(N21(id+vec2(0.0,1.0)),N21(id+vec2(1.0,1.0)),st.x),st.y);
	return c;
}

float fbm (vec2 uv){

    float c=0.;
	c+=Noise2D(uv)/2.;
    c+=Noise2D(2.*uv)/4.;
    return c/(1.-1./16.);
}

vec3 fbm3(vec2 uv){
    vec3 color;
	float f1 = fbm(uv);
    color= mix(vec3(0.1,0.0,0.0),vec3(0.9,0.1,0.1),2.5*f1);

    float f2 = fbm(2.4*f1+uv+0.15*sin(iTime)*vec2(7.0,-8.0));
    color= mix(color,vec3(0.6,0.5,0.1),1.5*f2);
    float f3 = fbm(3.5*f2+uv-0.15*cos(1.5*iTime)*vec2(4.0,3.0));
    color= mix(color,vec3(0.1,0.35,0.45),f3);

    return color;

}

