float mandelbrot(vec2 c){
    const float N = 120.;
    vec2 z = vec2(0.);
    for(float i = 0.;i<1.0;i+=1./N)
    {
        z = vec2(1.*(z.x*z.x-z.y*z.y),2.0*z.x*z.y)+c;
        if(dot(z,z)>4.) return i/2.;
        float t = z.x;
    }
    return 1.;
}
