}
vec2 SDF(vec3 p) {
     float f = 100.;
     float id = 0.;
     for(float i=0.;i<=1.;i+=1./6.){
         float o = fig(p,i*acos(-1.)/(4.+sin(iTime+i)));

         f = min(f,o);
         if(f == o) id = i;

     }

     return vec2(id,f);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro =vec3(0.,0.,-3.),
    p = ro,
    rd = normalize(vec3(uv,1.)),
    col = vec3(0);

    float shad = 0.;
    bool hit = false;
    vec2 d = vec2(0.);
    for(float i=0.;i < ITER; i++) {
        d = SDF(p);
        if(d.y< 0.001) {
            hit = true;
            shad = i / ITER;
            break;
        }
        p += d.y*rd;
    }

    if(hit) {

        col = vec3(1.-shad*d.x,1.10*shad*(1./d.x),smoothstep(.1,0.9,d.x));


    }




    fragColor = vec4(col,1.0);
}

#define i_time iTime
#define i_resolution iResolution

float tt;
vec3 ap, bp;
float attr;

mat2 rot2(float r) {
  return mat2(cos(r), sin(r), -sin(r), cos(r));
}

float sd_cylinder(vec3 p, float r, float height) {
  float d = length(p.xz) - r;
  d = max(d, abs(p.y) - height);
  return d;
}

float sd_box(vec3 p, vec3 b) {
  vec3 q  = abs(p) - b;
  return max(max(q.x, q.y), q.z);
}

float spiral(vec3 p) {
  vec3 q = p - vec3(.0, 2.5, .0);
  q.y *= 0.04;
  q.xz *= rot2((sin(q.y + 3.0)-tt*.05)*20.0);
  q.yz *= rot2(radians(20.0));
  q.y *= 0.18;
  q = abs(q) - vec3(0.3, -.55, 3.0);
  q -= vec3(.5, .05, 0.);
  float d = length(q) - .7;
  return d*0.5;
}

vec2 rails(vec3 p) {
  vec3 q = p;

  q.y -= .4;
  q = abs(q) - vec3(1.2, .2, 0.);
  vec2 a = vec2(sd_box(q, vec3(.1, .3, 20.)), 1.0);

  q = p;
  q.z = abs(q.z) - 2.0;
  q.xy *= rot2(radians(90.0));
  vec2 b = vec2(sd_cylinder(q - vec3(.8, 0, 0), 0.2, 5.), 1.0);

  a = (a.x < b.x) ? a : b;

  a.x *= 0.8;
  return a;
}

vec2 map(vec3 p) {
  attr = max(0.0, (6.0 - (length(p + vec3(0., 0., 1.0-tt*2.5)) - 6.0)));

  vec3 q = p;
  vec2 a = vec2(length(q - vec3(0., 0., 10. + tt*2.5)) - 2.5, 2.0);

  q = p;
  q.z = mod(q.z - 1.0, 11.0);
  q = abs(q) - vec3(0., 6., 11.);
  for (int i=0; i<6; i++) {
    q = abs(q) - vec3(2.0, 0.0, 0.5);
    //q.xz *= rot2(radians(i*5));
    q.xy *= rot2(radians(4.+float(i)*1.5));
  }

  vec2 b = rails(q);

  a = (a.x < b.x) ? a : b;

  float s = sin(tt*.5)*2.0 +  + 1.5;
  float ss = cos(tt*.5)*2.0 +  + 1.5;
  q = abs(q) - vec3(s, ss*2.0, 0.);
  b = rails(q);

  a = (a.x < b.x) ? a : b;

  q = p;
  q.yz *= rot2(radians(90.0));
  b = vec2(spiral(q), 1.0);

  a = (a.x < b.x) ? a : b;

  return a;
}

vec2 trace(vec3 ro, vec3 rd) {
  vec2 h, t = vec2(0.1);
  for (int i=0; i<128; i++) {
    h = map(ro+rd*t.x);
    if (h.x < .0001 || t.x > 50.0) break;
    t.x += h.x;
    t.y = h.y;
  }
  if (t.x > 50.0) t.x = 0.0;
  return t;
}

