  // ro.xz *= rot2(tt*.5);

  vec3 cam_target = vec3(0, 5, 0);
  cam_target.z += tt*2.5;

  vec3 cw = normalize(cam_target - ro);
  vec3 cu = normalize(cross(cw, vec3(0,1,0)));
  vec3 cv = normalize(cross(cu, cw));

  vec3 rd = mat3(cu, cv, cw) * normalize(vec3(uv, 0.5));

  vec3 ld = normalize(vec3(.3, .5, -.5));

  // Fibonacci Blue #112358, R: 17, G: 35 B: 88

  vec3 fog = vec3(.066, .137, .345) * (1.0 - (length(uv)-0.2));
  vec3 col = fog;

  vec2 scene = trace(ro, rd);
  float t = scene.x;

  if (t > 0.0) {
    vec3 pp = ro+rd*t;

    vec2 e = vec2(.00035, -.00035);
    vec3 nor = normalize(e.xyy*map(pp+e.xyy).x +
                         e.yyx*map(pp+e.yyx).x +
                         e.xyx*map(pp+e.xyx).x +
                         e.xxx*map(pp+e.xxx).x);

    vec3 b0 = vec3(.1, .5, .8);
    vec3 b1 = vec3(0.7, .8, .8);
    vec3 base = mix(b0, b1, attr);

    if (scene.y == 1.0) {

      float diff = max(0., dot(nor, ld));

      float aor = t/30.;
      float ao = exp2(-2.*pow(max(0.,1.-map(pp+nor*aor).x/aor),2.));

      float fr = pow(1.+dot(nor,rd),2.);

      vec3 sss = vec3(0.5)*smoothstep(0.,1.,map(pp+ld*0.4).x/0.4);

      col = base*(0.9*ao+0.2)*(diff+sss);
      col = mix(col, b0, min(fr, 0.2));

    }

    col = mix(col, fog, 1.0 - exp(-.00005*t*t*t));

    if (scene.y == 2.0) {
      float fr = pow(1.+dot(nor,rd),5.);
      col = mix(vec3(0.), base, min(fr, 0.8));
    }

  }

  frag_color = vec4(col, 1.0);
}

float perlinNoise3D(float x , float y , float z );
float noise3D(float x , float y , float z );
float findNoise3D(int x, int y , int z);
float interpolate(float a , float b , float x);

ivec3 seed=ivec3(10,200,300);
int octaves=11;
float zoom=0.001;
float persistence=50.;

float interpolate(float a, float b, float x)
{
   //Quintic Interpolation Formula
	float f = 6.0 * pow(x , 5.0) - 15.0 * pow(x , 4.0) + 10.0 * pow(x , 3.0);
	return a * (1.0 - f) + b * f ; 
}

float findNoise3D(int x ,int y ,int z)
{
	int n = x*seed.x + y*seed.y + z*seed.z;
	n = (n << 13) ^ n;
	int nn = (60493 * (n * n * n) + 19990303 * n + 1376312589) & 0x7fffffff;
	return 1.0 * (float(nn) / 1073741824.0);
}

float perlinNoise3D(float x,float y, float z)
{
	float frequency, amplitude ,returnVal = 0.0, maxH= 0.0;
    for (int o = 0 ; o < octaves ; o++){
		frequency = pow(2.0,float(o));
		amplitude = pow(persistence, float(o) );
		maxH += amplitude ;
		returnVal += (noise3D(x*frequency / zoom, y * frequency / zoom, z * frequency/zoom)) * amplitude;
	}
	return (returnVal/maxH);
}   


float noise3D(float x ,float y ,float z)
{
	vec3 Pos = vec3(x , y , z );
	ivec3 Flr = ivec3 (floor(Pos));

	float s,t,u,v,a,b,c,d;

	s = findNoise3D(Flr.x, Flr.y, Flr.z);
	t = findNoise3D(Flr.x + 1, Flr.y, Flr.z);
	u = findNoise3D(Flr.x, Flr.y + 1, Flr.z);
	v = findNoise3D(Flr.x + 1, Flr.y + 1, Flr.z);
	a = findNoise3D(Flr.x, Flr.y, Flr.z + 1);
	b = findNoise3D(Flr.x + 1, Flr.y, Flr.z + 1);
	c = findNoise3D(Flr.x, Flr.y + 1, Flr.z + 1);
	d = findNoise3D(Flr.x + 1, Flr.y + 1, Flr.z + 1);

	float int1 = interpolate(s, t, fract(x));
	float int2 = interpolate(u, v, fract(x));
	float int3 = interpolate(a, b, fract(x));
	float int4 = interpolate(c, d, fract(x));
	float int5 = interpolate(int1, int2, fract(y));
	float int6 = interpolate(int3, int4, fract(y));

	return interpolate(int5, int6 , fract(z));
}


