float f(float x)
{
    float y = 0.0;
    float size = 0.25;
    float speed = 3.14;

    while(size > 0.001)
    {
        y += sin(x * speed + iTime) * size;
        speed *= 2.0;
        size *= 0.5;
    }

    return y + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    fragColor = vec4( abs( f(uv.x) - uv.y ) < 0.005 );
}

