    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);

        float cX = fragCoord.x/2.0;
    float resWalkX = iResolution.x / 0.20;

    float cY = fragCoord.y/2.0;
    float resWalkY = iResolution.y / 0.20;

    float flip = sin(iTime);
    fragColor.b *= 2.0 * fragColor.b; 
    fragColor.b *= 0.2;
    if(flip >= 0.0) {
            fragColor.r *= 0.8 + sin(cX + sin(resWalkX));
            fragColor.g *=0.2;
    }
    if(flip <= 0.0){
        fragColor.g *= 0.8 + sin(cY + sin(resWalkY));
        fragColor.r *= 0.2;
    }

}

