//--- some globals ---//

vec3 orbitTrap = vec3(10000.0);


//--- camera stuff ---//

mat3 setCamera(in vec3 ro, in vec3 ta)
{
	vec3 cw = normalize(ta - ro);
	vec3 cp = vec3(0.0, 1.0, 0.0);
	vec3 cu = normalize(cross(cw, cp));
	vec3 cv = normalize(cross(cu, cw));

    return mat3(cu, cv, cw);
}


//--- scene description ---//

vec2 rotate(vec2 v, float a)
{
    float ca = cos(a);
    float sa = sin(a);

	return vec2(ca*v.x + sa*v.y, -sa*v.x + ca*v.y);
}


float spring_cyl(vec3 p)
{
    bool b = mod(iTime, 5.0) > 2.5;

	p.xy  = rotate(b ? p.xy : p.yz, (1.5 + 0.05*sin(17.0*iTime - 2.1*p.z)) * p.z);
	p.xy -= vec2(-0.63564, -0.75968);

	return max(abs(p.z) - 10.0, length(p.xy) - 0.14414);
}


float c3(vec3 p)
{
	return min(spring_cyl(p), spring_cyl(p.zyx));
}


float distanceToScene(vec3 pos)
{
	float d = 10000.0;
	vec4  p = vec4(pos, 0.0);
	vec4 dp = vec4(1.0, 0.0, 0.0, 0.0);
	vec4  C = vec4(0.0303, -0.0303, 0.01516, 0.21);

	for (int i = 0; i < 6; i++)
    {
		dp = 2.0*vec4(p.x*dp.x - dot(p.yzw, dp.yzw), p.x*dp.yzw + dp.x*p.yzw + cross(p.yzw, dp.yzw));
		p = vec4(p.x*p.x - dot(p.yzw, p.yzw), vec3(2.0*p.x*p.yzw)) + C;

		float r = c3(p.yxz);
		d = min(d, 0.5 * r / length(dp));

        float p2 = dot(p, p);
		orbitTrap = min(orbitTrap, abs(p.zxy));
		if (p2 > 14.286) break;
	}

	return 0.45*min(d, length(pos) - 0.9771);
}


//--- cheap normal computing ---//

vec3 computeSurfaceNormal(vec3 p)
{
    float d = distanceToScene(p);

    return normalize(vec3(
        distanceToScene(p + vec3(0.001, 0.0, 0.0)) - d,
        distanceToScene(p + vec3(0.0, 0.001, 0.0)) - d,
        distanceToScene(p + vec3(0.0, 0.0, 0.001)) - d));
}


//--- output color ---//

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    //--- camera setup ---//

    float rtime = 0.32165*iTime;

    vec2 p   = (-iResolution.xy + 2.0*fragCoord - 1.0) / iResolution.y;
 	vec3 pos = vec3(25.0*cos(rtime), 25.0*sin(rtime), 10.0);
    vec3 tar = vec3(0.0);
    vec3 dir = setCamera(pos, tar) * normalize(vec3(p.xy, 11.6));  


    //--- distance to nearest object in the scene ---//

	float t = 0.0;
	for(int i = 0; i < 256; i++)
    {
		float d = distanceToScene(pos + t*dir);
		if(d < 0.0023) break;

		t += d;


		//--- early skip of background pixels ---//

        if (t > 35.0)
        {
            fragColor = vec4(1.0);
            return;
        }
	}


    //--- output color depends on few things ---//

    //bool   b = mod(iTime, 4.0) > 2.0;
    vec3  sn = computeSurfaceNormal(pos + t*dir);			// surface normal
    float dc = clamp(dot(sn, normalize(pos)), 0.0, 1.0);	// diffuse component
    float sr = pow(dc, 56.2);								// specular reflection

	fragColor     = 0.4*orbitTrap.xyzz;
    fragColor    *= 0.75 + 0.5*dc;
    fragColor    += sr;

}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

//for rotating, copied from http://jamie-wong.com/2016/07/15/ray-marching-signed-distance-functions/#rotation-and-translation
mat4 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);

    return mat4(
        vec4(c, 0, s, 0),
        vec4(0, 1, 0, 0),
        vec4(-s, 0, c, 0),
        vec4(0, 0, 0, 1)
    );
}


float sdCapsule(vec3 p, vec3 a, vec3 b, float r){

    vec3 ab = b-a; 
    vec3 ap = p-a; 

    float t = dot(ab, ap)/dot(ab, ab); 
    t = clamp(t, 0., 1.); 

    vec3 c = a+t*ab;
    return length(p-c)-r;   

}   

float sdThorus(vec3 p, vec3 o, vec2 r){ 
    p = p-o; 
    float x = length(p.xz)-r.x;
    return length(vec2(x, p.y))-r.y;


} 


float sdSphere(vec3 p, vec3 o, float r){
   return length(p-o)-r; 
}    

float sdBox(vec3 p, vec3 o, vec3 s, float angle){
   //box with added stuff for rotating the squares in clock face
   o.z = o.z+2.; 
   p = p-o;
   p = (inverse(rotateY(angle)) * vec4(p, 1.0)).xyz; 
   p = p-vec3(0, 0, -2); 		

   return length(max(abs(p)-s, 0.)); 
} 

float sdCylinder(vec3 p, vec3 a, vec3 b, float r){
    vec3 ab = b-a; 
    vec3 ap = p-a; 

    float t = dot(ab, ap)/dot(ab, ab);     
    vec3 c = a+t*ab;
    float x = length(p-c)-r; 
    float y = (abs(t-.5)-.5)*length(ab); 
    float e = length(max(vec2(x, y), 0.)); 
    float i = min(max(x, y), 0.); 
    return e+i;
}



float GetDist(vec3 p) {


    float sd = sdSphere(p, vec3(0, 0., 7), 1.);
    float planeDist = p.y;
    float cd = sdCapsule(p, vec3(0, 2.5, 7), vec3(0, .4, 7), .05); 
    float td = sdThorus(p, vec3(0, .4, 7), vec2(2, .3)); 
    float cyd = sdCylinder(p, vec3(0, -10, 7), vec3(0, .5, 7 ), 2.2);

    float d = min(sd, planeDist); 
    d = min(d, cd);
    d = min(d, td);

    d = min(d, cyd);
    //12 squares for sun dial face
    for (int i = 0; i <= 12; i++){
        float bd = sdBox(p, vec3(0, .61, 5), vec3(.05,.1,.2), .1+(2.*3.14*float(i)/12.)); //draw box
        d = min(d, bd);
    }        
    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.; 

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p); 
        dO += dS; 
        if(dO>MAX_DIST || dS<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p); 
    vec2 e = vec2(.01, 0); 

    vec3 n = d - vec3( 
        GetDist(p-e.xyy), 
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n); 
}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 3.5, 7); 
    lightPos.xz += 1.*vec2(sin(iTime), cos(iTime)); 
    vec3 l = normalize(lightPos-p); 
    vec3 n = GetNormal(p); 

    float dif = clamp(dot(n, l), 0., 1.); 
    float d = RayMarch(p+n*SURF_DIST*2., l); 
    if(d<length(lightPos-p)) dif *= .1; 

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y; 

    vec3 col = vec3(0); 

    //camera
    vec3 ro = vec3(0, 3, 0); 
    vec3 rd = normalize(vec3(uv.x, uv.y-.2, 1));

    //march
    float d = RayMarch(ro, rd); 
    vec3 p = ro + rd * d;

    //light
    float dif = GetLight(p); 
    col = vec3(dif); 

    //color
    fragColor = vec4(col,1.0);
}

