    vec3 col = vec3(0);

    vec3 ro = vec3 (0,1,0);
    vec3 rd = normalize(vec3(uv.x,uv.y,1));

    float d = RayMarch(ro,rd);

    vec3 p = ro + rd * d;

    float dif = GetLight(p);
    col = vec3(dif);



    fragColor = vec4(col, 1.);


}

