/***************** RAY MARCHING LOGIC *******************/

vec3 rayCast(vec3 eye, vec3 ref, vec2 uv) {

    float len = tan(FOVY) * distance(eye, ref);
    vec3 H = normalize(cross(vec3(0.0, 1.0, 0.0), ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * iResolution.x / iResolution.y;
    vec3 p = ref + uv.x * H + uv.y * V;
    return normalize(p - eye);
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, hitObj);
        if(m < 0.01) { return; }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

/***************** EFFECTS *******************/

vec2 random2(vec2 p) {
    return fract(sin(vec2(dot(p, vec2(127.1, 311.7)),
        dot(p, vec2(127.1, 311.7)))));
}



vec3 random3 ( vec3 p ) {
    return fract(sin(vec3(dot(p,vec3(127.1, 311.7, 191.999)),
                          dot(p,vec3(269.5, 183.3, 765.54)),
                          dot(p, vec3(420.69, 631.2,109.21))))
                 *43758.5453);
}

float random1(vec2 p) {
    return fract(dot(fract(cos(vec2(dot(p, vec2(127.1908, 311.7340)),
        dot(p, vec2(127.1, 311.7))))), vec2(0.1288971, 2403.7)));
}

vec2 random4 ( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1, 191.999)),
                          dot(p,vec2(269.5, 765.54)))) * 43758.5453);
}

float noise1D( vec2 p ) {
    return fract(sin(dot(p, vec2(127.1, 311.7))) *
                 43758.5453);
}



float WorleyNoise2D(vec2 p) {
    // Tile the space
    vec2 pointInt = floor(p);
    vec2 pointFract = fract(p);

    float minDist = 1.0; // Minimum distance initialized to max.

    // Search all neighboring cells and this cell for their point

    for (int y = -1; y <= 1; y++) {
        for (int x = -1; x <= 1; x++) {
            vec2 neighbor = vec2(float(x), float(y));

            // Random point inside current neighboring cell
            vec2 point = random4(pointInt + neighbor);

            // Animate the point
            point = 0.5 + 0.5 * sin(iTime *  point); // 0 to 1 range

            // Compute the distance b/t the point and the fragment
            // Store the min dist thus far
            vec2 diff = neighbor + point - pointFract;
            float dist = length(diff);
            minDist = min(minDist, dist);
        }
    }
    return minDist;
}

float interpNoise2D(float x, float y) {
    float intX = floor(x);
    float fractX = fract(x);
    float intY = floor(y);
    float fractY = fract(y);

    float v1 = random1(vec2(intX, intY));
    float v2 = random1(vec2(intX + 1., intY));
    float v3 = random1(vec2(intX, intY + 1.));
    float v4 = random1(vec2(intX + 1., intY + 1.));

    float i1 = mix(v1, v2, fractX);
    float i2 = mix(v3, v4, fractX);
    return mix(i1, i2, fractY);
}


float fbm(vec2 p) {
    float total = 0.;
    float persistence = 0.5f;
    float octaves = 4.;

    for(float i = 1.; i <= octaves; i++) {
        float freq = pow(2.f, i);
        float amp = pow(persistence, i);

        total += interpNoise2D(p.x * freq + cos(iTime),
                               p.y * freq + sin(iTime)) * amp;
    }
    return total;
}


float surflet(vec3  p, vec3 gridPoint) {
    // Compute the distance between p and the grid point along each axis, and warp it with a
    // quintic function so we can smooth our cells
    vec3 t2 = abs(p - gridPoint);
    vec3 t;
    t.x = 1.f - 6.f * pow(t2.x, 5.f) + 15.f * pow(t2.x, 4.f) - 10.f * pow(t2.x, 3.f);
    t.y = 1.f - 6.f * pow(t2.y, 5.f) + 15.f * pow(t2.y, 4.f) - 10.f * pow(t2.y, 3.f);
    t.z = 1.f - 6.f * pow(t2.z, 5.f) + 15.f * pow(t2.z, 4.f) - 10.f * pow(t2.z, 3.f);

    // Get the random vector for the grid point (assume we wrote a function random2
    // that returns a vec2 in the range [0, 1])
    vec3 gradient = random3(gridPoint) * 2. - vec3(1.);
    // Get the vector from the grid point to P
    vec3 diff = p - gridPoint;
    // Get the value of our height field by dotting grid->P with our gradient
    float height = dot(diff, gradient);
    // Scale our height field (i.e. reduce it) by our polynomial falloff function
    return height * t.x * t.y * t.z;
}

float perlin(vec3 p) {
    float surfletSum = 0.f;
    // Iterate over the four integer corners surrounding uv
    for(int dx = 0; dx <= 1; ++dx) {
        for(int dy = 0; dy <= 1; ++dy) {
             for(int dz = 0; dz <= 1; ++dz) {
                 surfletSum += surflet(p, floor(p) + vec3(dx, dy, dz));
             }
        }
    }
    return surfletSum + sin(iTime);
}

void CoordinateSystem(in vec3 v1, out vec3 v2, out vec3 v3) {
    if (abs(v1.x) > abs(v1.y))
            v2 = vec3(-v1.z, 0.0, v1.x) / sqrt(v1.x * v1.x + v1.z * v1.z);
        else
            v2 = vec3(0.0, v1.z, -v1.y) / sqrt(v1.y * v1.y + v1.z * v1.z);
        v3 = cross(v1, v2);
}

vec3 perlinTangentSpaceNormal(vec3 pos) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( perlin(pos + epsilon.yxx) - perlin(pos - epsilon.yxx),
                            perlin(pos + epsilon.xyx) - perlin(pos - epsilon.xyx),
                            perlin(pos + epsilon.xxy) - perlin(pos - epsilon.xxy)));
}


mat3 TangentToWorld(vec3 nor) {
    vec3 tangent, bitangent;
    CoordinateSystem(nor, tangent, bitangent);
    return mat3(tangent, bitangent, nor);
}


vec2 uvSphere(vec3 pos) {

    vec3 d =  normalize(center - pos); 
    float u = 0.5 + atan(d.z, d.x) / (2. * PI);
    float v = 0.5 - asin(d.y) / PI;
    return vec2(u, v);
}


/***************** MATERIALS *******************/

vec3 computeNormal(vec3 pos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                            sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                            sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 lightDir, vec3 view) {
    //disrupt normals 
    vec3 perlin_normal = perlinTangentSpaceNormal(p);
    perlin_normal = TangentToWorld(n) * perlin_normal;
    float perlin_lambert = max(0., dot(view, perlin_normal));

    float lambert = max(0., dot(lightDir, n));
    if (hitObj == 3) { return lambert *  colorPaletteWorley(WorleyNoise2D(vec2(p.x, p.y))); }
    if (hitObj == 5) { return lambert * colorPaletteFBM(fbm(uvSphere(p))); }
    if (hitObj == 6) { 
        if (AFFECT_NORMALS) { return colorPalettePerlin(perlin(p))* perlin_lambert; } 
        else { return colorPalettePerlin(perlin(p)) * lambert; }    
    } else { return vec3(0., 0., 1) * lambert; }
}

/***************** BASIC FUNCTIONALITY *******************/

Intersection sdf3D(vec3 dir, vec3 eye)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj);

    vec3 isect = eye + t * dir;
    vec3 nor = normalize(computeNormal(isect));
    vec3 lightDir = normalize(lightPos - isect);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));

    return Intersection(t, surfaceColor, isect, hitObj);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;

    //Convert to range [-1,1]
    uv = uv * 2.0 - vec2(1.);

    vec3 eye = eyePos;
    vec3 ref = refPos;

    vec3 dir = rayCast(eye, ref, uv);

    Intersection isect = sdf3D(dir, eye);

    // Output to screen
    fragColor = vec4(isect.color, 1.);

}

//---By Matej Vanco 2020 - educational purposes & for fun!
#define MAX_RENDER 64
#define MIN_RENDER 16.
#define EPSILON 0.001

struct ray
{
	vec3 o;
    vec3 d;
    float l;
};

//---Iq's smooth opu
vec4 opu(vec4 a, vec4 b)
{
    float k = 0.35;
    float d1 = a.w;
    float d2 = b.w;
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return vec4(mix(b.rgb,a.rgb,h),mix( d2, d1, h ) - k*h*(1.0-h));
}
//---'Geometry' calculation
vec4 geo(vec3 p)
{   
    float fsin = sin(iTime * 1. + p.y * 8.); //---'Chocolate effect'
	vec4 s = vec4(vec3(0.5,0.3,0.1)*0.5, pow(length(p - vec3(fsin*0.5,0.2,1.5)) - 0.2,2.)); //---'Sphere'

    vec4 pp = vec4(vec3(0.5,0.3,0.1)*0.2, p.y + 0.25); //---Ground

    vec4 g0 = opu(s,pp); //---Combine all objects
    return g0;
}
//---'Normals' calculation
vec3 normal(vec3 p)
{
	vec2 of = vec2(EPSILON,0.);
    float copy = geo(p).w;
    return normalize(copy - vec3(geo(p - of.xyy).w, geo(p - of.yxy).w, geo(p - of.yyx).w)); //---Subtract from copy
}
//---Traditional soft shadows
float shadow(vec3 ro, vec3 rd, float b)
{
	float t = EPSILON;
    float r = 1.;
    for(int i = 0; i < 16; i++)
    {
        float g = geo(ro + rd * t).w;
        r = min(r, b * g / t);
        t += g;
        if(g < EPSILON)
            break;

        if(g >= 4.)
            break;
    }
    return clamp(r,0.4,1.);
}
//---Traditional Lambert lighting model + Phong reflection specular
float lighting(vec3 p)
{
	vec3 lpos = vec3(2.5,2.5,-2.5);
    vec3 lposVec = normalize(p - lpos);
    vec3 n = normal(p);

    float spec = pow(max(dot(p, reflect(lposVec,n))*1.5,1.0),2.); //---Specular Phong theory

    float light = clamp(dot(lposVec, p),0.,1.) * shadow(p, lpos,0.4) * spec;
    return light;
}

//---Traditional ray-march
vec4 march(ray r)
{
	vec3 col = vec3(1.);
    for(int i = 0; i < MAX_RENDER; i++)
    {
        vec3 p = r.o + r.d * r.l;
        vec4 g = geo(p);
        r.l += g.w;
        col = g.rgb;
    	if(r.l > MIN_RENDER)
        	break;   
    }
    return vec4(col, r.l);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    ray r;
    r.o = vec3(0.);
    r.d = vec3(uv, 1.);
    vec4 m = march(r);
    vec3 col = m.rgb * lighting(r.o + r.d * m.w);
    col*=exp(-0.1 * m.w * m.w * m.w); //---Fog
    if(m.w > 2.5)
        col = mix(col,vec3(0.5,0.3,0.), uv.y+0.2); //---'Gradient' background

    fragColor = vec4(col,1.0);
}

precision highp float;

#define M_PI 3.1415926535897932384626433832795

// Parameters
float winding = 3.0;
float numSpirals = 1.0;

float u_f = 2.0; // scaling
float majorGrid = 2.0;
float minorGrid = 2.0 /* majorGrid */ * 3.0;

float speed = 3.0;

// convert from cartisian to polar.
// returns [r, theta] in x+iy = r e^i * theta
vec2 polarz(in vec2 c) {
  return vec2(length(c), atan(c.y, c.x));
}

// convert from polar to cartisian.
vec2 cart(in vec2 c) {
  return vec2(c.x * cos(c.y), c.x * sin(c.y));
}

vec2 powz(in vec2 c1, in vec2 c2) {
  vec2 polarC = polarz(c1);

  // (r * e ^ i theta) ^ (x + i y) = r ^ x * r ^ i y * e ^ i x theta * e ^ - y theta
  // r ^ x * e ^ - y theta * e ^ i (x theta + log (r) y)
  // |--------- r -------| * e ^ i |------- theta -----|

  float r = pow(polarC.x, c2.x) * exp(-polarC.y * c2.y);
  float theta = c2.x * polarC.y + log(polarC.x) * c2.y;

  return cart(vec2(r, theta));
}

vec4 grid(in vec2 uv) {
  float x = fract(uv.x * majorGrid + 0.07/2.0);
  float y = fract(uv.y * majorGrid + 0.07/2.0);
  if (abs(x) < 0.07 || abs(y) < 0.07) {
    float c = smoothstep(0.9 * 0.07, 0.07, min(abs(x), abs(y)));
    return vec4(c, c, c, 1.0);
  }
  x = fract(uv.x * minorGrid + 0.05/2.0);
  y = fract(uv.y * minorGrid + 0.05/2.0);
  if (abs(x) < 0.05 || abs(y) < 0.05){
    float c = smoothstep(0.0045, 0.05, min(abs(x), abs(y)));
    return vec4(c, c, c, 1.0);
  }
  return vec4(1.0, 1.0, 1.0, 1.0);
}

vec2 conformal(in vec2 uv) {
  float P = log(u_f) / M_PI / 2.0;
  vec2 alpha = vec2(winding / 4.0, numSpirals * P);
  return powz(uv, alpha);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 c = (2.0 * fragCoord - iResolution.xy) / iResolution.x;
    vec2 e = conformal(c);

    float t = iTime / speed;
    e /= exp(log(u_f) * fract(t / log(u_f)));
    while (abs(e.x) > 1.0 || abs(e.y) > 1.0) {
      e /= u_f;
    }
    while (abs(e.x) < 1.0 / u_f && abs(e.y) < 1.0 / u_f) {
      e *= u_f;
    }

    fragColor = grid(e + 1.0 / 2.0);
}

#define EPSILON 0.01
#define MAX_ITERATIONS 256.0
#define FAR_PLANE 100.0

vec4 mod289(vec4 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }

vec4 perm(vec4 x) { return mod289(((x * 34.0) + 1.0) * x); }

float noise(vec3 p)
{
	vec3 a = floor(p);
	vec3 d = p - a;
	d = d * d * (3.0 - 2.0 * d);
	vec4 b = a.xxyy + vec4(0.0, 1.0, 0.0, 1.0);
	vec4 k1 = perm(b.xyxy);
	vec4 k2 = perm(k1.xyxy + b.zzww);
	vec4 c = k2 + a.zzzz;
	vec4 k3 = perm(c);
	vec4 k4 = perm(c + 1.0);
	vec4 o1 = fract(k3 * (1.0 / 41.0));
	vec4 o2 = fract(k4 * (1.0 / 41.0));
	vec4 o3 = o2 * d.z + o1 * (1.0 - d.z);
	vec2 o4 = o3.yw * d.x + o3.xz * (1.0 - d.x);
	return o4.y * d.y + o4.x * (1.0 - d.y);
}

float NoiseSDF(vec3 ray)
{
	return noise(ray) - 1.0;
}

vec3 SceneColor(vec3 origin, vec3 ray, vec2 uv)
{
    vec3 color = vec3(1.0);
    float depth = 0.1;
    for (int i = 0; i < int(MAX_ITERATIONS); i++)
    {
        float d = NoiseSDF(ray * depth + origin) + clamp(1.0 - depth / 6.0, 0.0, 1.0);
        if (d < EPSILON)
        {
            float t = iTime / 10.0;
            color.r = cos(uv.x + t + sin(uv.y));
            color.g = sin(uv.y - t + cos(uv.x));
            color.b = 0.0;
            color = color * 0.5 + 0.5;
            float a = float(i) / MAX_ITERATIONS;
            return color * (1.0 - a) + a * vec3(1.0);
        }
        depth += d;
        if (depth > FAR_PLANE) { break; }
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
