    mat2 mat = mat2(vec2(cos(iTime*speed), sin(iTime*speed)), 		// first column (not row!)    
             		vec2(-sin(iTime*speed), cos(iTime*speed)));
    uv = mat*uv ;
	vec3 ro = vec3(0,0.5,iTime);
    vec3 rd = normalize(vec3(uv.x,uv.y,1));
    float d = rayMarch(ro,rd);
    //vec3 p = ro + rd * d ;
    float dif = 1.0/(1.0+d*d*0.1);
