#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float SpdDf (vec3 p, float dMin, int tpId)
{
  vec3 q;
  float d, s, len, szFac;
  szFac = 0.22;
  p /= szFac;
  dMin /= szFac; 
  p.y -= bdyHt + 0.8;
  q = p - vec3 (0., -0.15, 0.2);
  d = PrEllipsDf (q, vec3 (0.7, 0.5, 1.3));
  DMINQ (idBdy + tpId);
  q = p - vec3 (0., 0.1, 1.1);
  d = PrEllipsDf (q, vec3 (0.2, 0.4, 0.5));
  DMINQ (idHead + tpId);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.15, 0.25, 1.5);
  d = PrSphDf (q, 0.13);
  DMINQ (idEye + tpId);
  q -= vec3 (0., 0.15, -0.3);
  d = PrEETapCylDf (q, 1.3 * vec3 (0.3, 1.1, 0.4), vec3 (0.), 0.07, 0.7);
  DMINQ (idAnt + tpId);
  p.y += bdyHt;
  for (int j = VAR_ZERO; j < 8; j ++) {
    q = p - hipPos[j];
    d = 0.6 * PrEETapCylDf (q, kneePos[j], hipPos[j], 0.25, 0.3);
    DMINQ (idLegU + tpId);
    q = p - kneePos[j];
    d = 0.6 * PrEETapCylDf (q, footPos[j] - vec3 (0.3), kneePos[j] - vec3 (0.3), 0.2, 1.2);
    DMINQ (idLegD + tpId);
  }
  return dMin * szFac;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, widRmp, aRot, r, a, ai, s;
  dMin = dstFar;
  widRmp = 1.2;
  r = length (p.xz);
  a = (r > 0.) ? atan (p.z, - p.x) / (2. * pi) + 0.5 : 0.;
  q = p;
  q.y = mod (q.y + (0.5 - 2. * a) * vGap, vGap) - 0.5 * vGap;
  d = PrBox2Df (vec2 (r - radRmp, q.y), vec2 (widRmp, 0.1));
  DMIN (idRmp);
  d = max (PrBox2Df (vec2 (r - 0.5 * radRmp, q.y + 0.2), vec2 (0.1, 0.5)),
     - PrBox2Df (vec2 (abs (mod (7. * a, 1.) - 0.5), q.y + 0.2), vec2 (0.3, 0.2)));
  DMIN (idWalI);
  d = PrBox2Df (vec2 (abs (r - radRmp) - widRmp, q.y - 0.2), vec2 (0.07, 0.3));
  DMIN (idWalM);
  q = p;
  q.xz = Rot2D (q.xz, 2. * pi * (floor (7. * a + 0.5) / 7. - 0.5));
  q.x += 0.5 * radRmp;
  d = length (q.xz) - 0.2;
  DMIN (idCol);
  q.x += 0.25 * radRmp + 0.5 * widRmp;
  s = -0.03 * q.x;
  q.y = mod (q.y + 0.15 + (0.5 - 2. * a) * vGap, vGap) - 0.5 * vGap - s;
  d = PrBoxDf (q, vec3 (0.25 * radRmp + 0.5 * widRmp, 0.15 - s, 0.1));
  DMIN (idBm);
  q = p;
  d = max (abs (length (q.xz) - radExt - 0.2) - 0.2,
     - PrBox2Df (mod (vec2 (21. * (0.5 + a), 0.125 * q.y) + 0.5, 1.) - 0.5, vec2 (0.2, 0.3)));
  DMIN (idWalO);
  aRot = atan (vGap / (2. * pi * radRmp));
  for (float k = float (VAR_ZERO); k <= 1.; k ++) {
    s = sign (1. - 2. * k);
    q = p;
    a = s * spdVel * tCur / (2. * pi * radRmp);
    q.xz = Rot2D (q.xz, a);
    ai = 2. * pi * floor (nSpd * ((r > 0.) ? atan (q.z, - q.x) / (2. * pi) : 0.) + 0.5) / nSpd;
    q.xz = Rot2D (q.xz, ai);
    q.y = mod (q.y + 0.2 + k * vGap + (1. - (ai + a) / pi) * vGap, 2. * vGap) - vGap;
    q.yz = Rot2D (q.yz, aRot);
    q.x += radRmp;
    q.xz *= s;
    if (PrCylDf (q.xzy, 1., 0.7) < dMin) dMin = SpdDf (q, dMin, int (10. * k));
  }
  return 0.8 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.002, -0.002);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd, float dstLim)
{
  float sh, d, h;
  sh = 1.;
  d = 0.05;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.03 * d, h));
    d += clamp (2. * h, 0.05, 0.3);
    if (sh < 0.05 || d > dstLim) break;
  }
  return 0.6 + 0.4 * sh;
}

vec3 SpdCol ()
{
  vec3 col, c[2];
  vec2 cc;
  cc = vec2 (1., 0.2);
  if (idObj >= idBdy + 10) {
    idObj -= 10;
    c[0] = cc.xxy;
    c[1] = cc.xyy;
  } else {
    c[0] = cc.yyx;
    c[1] = cc.yxy;
  }
  if (idObj == idBdy) {
    col = mix (c[0], c[1], SmoothBump (0.2, 0.7, 0.05, mod (4. * qHit.z, 1.)));
  } else if (idObj == idHead) {
    col = c[1];
    if (qHit.z > 0.4) col *= 0.2 + 0.8 * smoothstep (0.02, 0.04, abs (qHit.x));
  } else if (idObj == idEye) {
    col = cc.xxx;
  } else if (idObj == idLegU || idObj == idLegD) {
    col = mix (c[1], c[0],  SmoothBump (0.4, 1., 0.2, fract (3.5 * length (qHit))));
  } else if (idObj == idAnt) {
    col = cc.xyx;
  }
  return col;
}

vec3 StarPat (vec3 rd, float scl)
{
  vec3 tm, qn, u;
  vec2 q;
  float f;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = Maxv3 (tm) * rd;
  q = atan (vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.)) / pi;
  f = 0.57 * (Fbm2 (11. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 131.13 * scl * q) +
      Fbm2 (13. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 171.13 * scl * q.yx));
  return 16. * vec3 (1., 1., 0.8) * pow (f, 16.);
}

vec3 ShStagGrid (vec2 p)
{
  vec2 q, sq, ss;
  q = p;
  if (2. * floor (0.5 * floor (q.y)) != floor (q.y)) q.x += 0.5;
  sq = smoothstep (0.05, 0.1, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.25 * smoothstep (0.3, 0.5, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.x, 0.8 + 0.2 * sq.x * sq.y, ss.y);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, bgCol, vn, rg;
  float dstObj, vDotL, sh, atten, a, s, sa, saa;
  bgCol = StarPat (rd, 12.);
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    a = atan (ro.z, - ro.x) / (2. * pi);
    vn = ObjNf (ro);
    if (idObj == idRmp) {
      col4 = vec4 (0.4, 0.4, 0.45, 0.1) * (0.7 + 0.3 * Fbm2 (64. * ro.xz));
    } else if (idObj == idWalI) {
      col4 = vec4 (0.7, 0.7, 0.2, 0.2);
    } else if (idObj == idCol) {
      col4 = vec4 (0.7, 0.7, 0.2, 0.2) * (0.7 + 0.3 * smoothstep (0.05, 0.1, mod (4. * ro.y, 1.)));
    } else if (idObj == idWalM) {
      col4 = (vn.y < 0.9) ? vec4 (0.8, 0.75, 0.75, 0.1) : vec4 (0.7, 0.7, 0.2, 0.2);
      col4.rgb *= (0.7 + 0.3 * smoothstep (0.05, 0.1, 0.5 * (1. + sin (256. * pi * a))));
    } else if (idObj == idBm) {
      col4 = vec4 (0.8, 0.75, 0.75, 0.);
    } else if (idObj == idWalO) {
      col4 = vec4 (0.7, 0.7, 0.6, 0.) * (0.7 + 0.3 * step (length (ro.xz), radExt + 0.01)); 
      s = mod (21. * (0.5 + a) + 0.5, 1.) - 0.5;
      if (abs (s) < 0.08) {
        sa = mod (1.5 * ro.y + 0.5, 1.) - 0.5;
        saa = smoothstep (0., 0.1, abs (sa));
        col4 *= 0.8 + 0.2 * saa;
        vn.y = -0.3 * (1. - saa) * sign (sa);
        vn.xz *= sqrt (1. - vn.y * vn.y);
        vn.xz = Rot2D (vn.xz, asin (pi * clamp (4. * s, -0.5, 0.5)));
      } else {
        rg = ShStagGrid (3. * vec2 (42. * a, ro.y));
        col4 *= rg.y;
        rg.xz *= sign ((abs (vn.x) > 0.5) ? vn.x : vn.z);
        if (abs (vn.x) > 0.5) {
          if (rg.x == 0.) vn.xy = Rot2D (vn.xy, rg.z);
          else vn.xz = Rot2D (vn.xz, rg.x);
        } else {
          if (rg.x == 0.) vn.zy = Rot2D (vn.zy, rg.z);
          else vn.zx = Rot2D (vn.zx, rg.x);
        }
      }
    } else {
      col4 = vec4 (SpdCol (), 0.3);
    } 
    vDotL = max (dot (vn, ltDir), 0.);
    if (idObj >= idBdy) vDotL *= vDotL;
    atten = min (1., 1.2 / (1. + 0.2 * dstObj * dstObj / (radExt * radExt)));
    sh = 1.;
    if (idObj != idWalO) sh = ObjSShadow (ro, ltDir, dstObj);
    if (col4.a >= 0.) col = atten * (col4.rgb * (0.2 + 0.8 * sh * vDotL) +
       step (0.95, sh) * col4.a * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.));
    else col = atten * col4.rgb;
    col = mix (col, bgCol, smoothstep (0.8, 1., abs (ro.y) / (2. * radExt)));
  } else {
    col = bgCol;
  }
  return clamp (col, 0., 1.);
}

void SpdSetup (float gDisp)
{
  vec3 v;
  float a, az, fz, d, ll;
  nSpd = float (N_SPD);
  for (int j = VAR_ZERO; j < 4; j ++) {
    a = 0.2 * (1. + float (j)) * pi;
    hipPos[j] = 0.5 * vec3 (- sin (a), 0., 1.5 * cos (a));
    hipPos[j + 4] = hipPos[j];
    hipPos[j + 4].x *= -1.;
  }
  bdyHt = 1.5;
  legLenU = 2.2;
  legLenD = 3.;
  ll = legLenD * legLenD - legLenU * legLenU;
  for (int j = VAR_ZERO; j < 8; j ++) {
    fz = fract ((gDisp + 0.93 + ((j < 4) ? -1. : 1.) + mod (7. - float (j), 4.)) / 3.);
    az = smoothstep (0.7, 1., fz);
    footPos[j] = 5. * hipPos[j];
    footPos[j].x *= 1.7;
    footPos[j].y += 0.7 * sin (pi * clamp (1.4 * az - 0.4, 0., 1.));
    footPos[j].z += ((j < 3) ? 0.5 : 1.) - 3. * (fz - az);
    hipPos[j] += vec3 (0., bdyHt - 0.3, 0.2);
    v = footPos[j] - hipPos[j];
    d = length (v);
    a = asin ((hipPos[j].y - footPos[j].y) / d);
    kneePos[j].y = footPos[j].y + legLenD *
       sin (acos ((d * d + ll) / (2. * d *  legLenD)) + a);
    kneePos[j].xz = hipPos[j].xz + legLenU * sin (acos ((d * d - ll) /
       (2. * d *  legLenU)) + 0.5 * pi - a) * normalize (v.xz);
  }
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvv, ut, mSize, mMid, msw;
  float el, az, zmFac, asp, sr, regId;
  int vuMode;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  mSize = (1./6.) * vec2 (asp, 1.);
  mMid = vec2 (asp * (1. - mSize.y), - 1. + mSize.y);
  ut = abs (uv - mMid) - mSize;
  vuMode = 0;
  if (max (ut.x, ut.y) < 0.) {
    uv = (uv - mMid) / mSize.y;
    vuMode = 1;
  }
  az = 0.05 * pi * (floor (0.2 * tCur) + smoothstep (0.9, 1., mod (0.2 * tCur, 1.)));
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    regId = 0.;
    msw = (mPtr.xy * vec2 (asp, 1.) - 0.5 * mMid) / mSize;
    if (max (abs (msw.x), abs (msw.y)) < 0.5) regId = 1.;
    else msw = mPtr.xy;
    if (vuMode == 0) {
      az += 2. * pi * msw.x;
      el += pi * msw.y;
    }
    if (regId == 1.) vuMode = 1 - vuMode;
  }
  if (length (uv * vec2 (1. / asp, 1.)) < 1.5 * SmoothBump (0.25, 0.75, 0.1,
     mod (0.03 * tCur, 1.))) vuMode = 1 - vuMode;
  if (vuMode == 0) el = clamp (el, -0.25 * pi, 0.25 * pi);
  vuMat = StdVuMat (el, az);
  spdVel = 2.5;
  SpdSetup (spdVel * tCur);
  radExt = 15.;
  radRmp = 6.;
  vGap = 3.;
  if (vuMode == 0) {
    ro = vuMat * vec3 (0., 0., - (radExt - 0.2));
    zmFac = 2.2;
  } else if (vuMode == 1) {
    ro = vec3 (0.);
    zmFac = 0.5;
  }
  dstFar = 4. * radExt;
  ltDir = vuMat * normalize (vec3 (0.5, 0.5, -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) * pi)) / zmFac;
    if (vuMode == 0) rd = normalize (vec3 (uvv, 1.));
    else rd = normalize (vec3 (sin (uvv.x + vec2 (0., 0.5 * pi)), uvv.y).xzy);
    rd = vuMat * rd;
    col += (1. / naa) * ShowScene (ro, rd);
  }
  if (vuMode == 1) col = pow (col, vec3 (0.9));
  if (max (ut.x, ut.y) < 0. && min (abs (ut.x), abs (ut.y)) * canvas.y < 2.) col = vec3 (0.2, 0.5, 0.2);
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrEETapCylDf (vec3 p, vec3 v1, vec3 v2, float r, float rf)
{
  vec3 v;
  float s;
  v = v1 - v2;
  s = clamp (dot (p, v) / dot (v, v), 0., 1.);
  return length (p - s * v) - r * (1. - rf * s * s);
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

#define EPSILON 0.001
#define PI 3.141592
#define MAX_DIST 256.0
#define MAX_STEPS 256
#define it 10

vec3 makeRay(vec2 origin)
{
    vec2 res;
    res.x = origin.x - iResolution.x * 0.5;
    res.y = origin.y - iResolution.y * 0.5;

    return normalize(vec3(res / iResolution.yy, 1));
}

mat2 rot(float ang)
{
    float s = sin(ang);
    float c = cos(ang);
    return mat2(c, -s, s, c);
}

vec3 rotVec(vec3 p, vec3 r)
{
    p.yz *= rot(r.x);
    p.xz *= rot(r.y);
    p.xy *= rot(r.z);
    return p;
}

float mandelBulb(vec3 p, vec3 fp, float power, vec3 ang)
{
    p -= fp;
    p = rotVec(p, ang);

	vec3 z = p;
	float r, theta, phi;
	float dr = 1.0;

	for(int i = 0; i < it; ++i)
    {
		r = length(z);

		if(r > 2.0)
            continue;

		theta = atan(z.y / z.x);
        phi = asin(z.z / r) + iTime;

		dr = pow(r, power - 1.0) * dr * power + 1.0;
		r = pow(r, power);

		theta = theta * power;
		phi = phi * power;

		z = r * vec3(cos(theta) * cos(phi),
                     sin(theta) * cos(phi), 
                     sin(phi)) + p;
	}

	return 0.5 * log(r) * r / dr;
}

float getDist(vec3 origin)
{
    vec3 fp = vec3(0);
    vec3 fr = vec3(0, PI + PI / 4.0, 0);
    float power = 8.0;

    return mandelBulb(origin, fp, power, fr);
}

vec2 rayMarch(vec3 origin, vec3 direct)
{
    float res = 0.0;

    for (int i = 0; i < MAX_STEPS; i++)
    {
        vec3 tmp = origin + direct * res;
        float d = getDist(tmp);
        res += d;

        if (res >= MAX_DIST || d < EPSILON)
        	return vec2(res, float(i));
    }

    return vec2(res, float(MAX_STEPS));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 origin = vec3(0, 0, -3);
    vec3 dir = makeRay(fragCoord);

    vec2 res = rayMarch(origin, dir);
    float d = res.x;
    vec3 col;

    vec3 startCol = vec3(cos(iTime) * 0.25 + 0.75, 0, 0);
    vec3 finCol = vec3(0, 0, sin(iTime) * 0.25 + 0.75);
    float delta = 0.5;

    if (d < MAX_DIST)
    {
    	vec3 p = origin + d * dir;
        delta = length(p) / 2.0;
    }

    col = mix(startCol, finCol, delta) * res.y / float(MAX_STEPS) * 5.0;
    fragColor = vec4(col, 1);
}

