vec3 map( vec2 p )
{   p *= 0.7;
    float f = dot( fbm2( 1.0*(0.24*iTime - p + fbm2(-0.05*iTime+2.0*(p / fbm2(5.0*p)))) ), vec2(1.0,-1.0) ); // condensa
