vec3 hash33(vec3 p3)
{
    p3 = fract(p3 * vec3(.1031,.11369,.13787));
    p3 += dot(p3, p3.yxz+19.19);
    return -1.0 + 2.0 * fract(vec3(p3.x+p3.y, p3.x+p3.z, p3.y+p3.z)*p3.zyx);
}
float snoise3(vec3 p)
{
    const float K1 = 0.3333333;
    const float K2 = 0.1666667;
    vec3 i = floor(p + (p.x + p.y + p.z) * K1);
    vec3 d0 = p - (i - (i.x + i.y + i.z) * K2);
    vec3 e = step(vec3(0.0), d0 - d0.yzx);
    vec3 i1 = e * (1.0 - e.zxy);
    vec3 i2 = 1.0 - e.zxy * (1.0 - e);
    vec3 d1 = d0 - (i1 - K2);
    vec3 d2 = d0 - (i2 - K1);
    vec3 d3 = d0 - (1.0 - 0.5);
    vec4 h = max(0.6 - vec4(dot(d0, d0), dot(d1, d1), dot(d2, d2), dot(d3, d3)), 0.0);
    vec4 n = h*h*h*h * vec4(dot(d0, hash33(i)), dot(d1, hash33(i+i1)), dot(d2, hash33(i+i2)), dot(d3, hash33(i+1.0)));
    return dot(vec4(31.316), n);
}


#define time (iTime * 0.9)
const vec3 color1 = vec3(1., 1., 0.1);
const vec3 color2 = vec3(1., 0.5, 0.1);
const float innerRadius = 0.25;
const float ringWidth = 0.03;

void draw(out vec4 FragColor, in vec2 vUv)
{
    vec2 uv = vUv;
    float len = length(uv);
    float ang = atan(uv.y, uv.x);
    float v0, v1, v2, v3;
    vec4 col = vec4(1.0);
    // ring
    v0 = sin(ang * 2.0) * 0.5 + 0.5;
    col.rgb = mix(color1, color2, v0);
    v1 = smoothstep(innerRadius + ringWidth + 0.02, innerRadius + ringWidth, len);
    // light
    float n = snoise3(vec3(normalize(uv), time)) * 0.5 + 0.5;
    n = n*n*n*n;
    float d = len - innerRadius;
    v2 = n * 1.0 / (1.0 + d);
    v2 += 0.1 / (0.1 + d);
    col.rgb = mix(vec3(1.0), col.rgb, v1);
    // mask
    v3 = smoothstep(innerRadius, innerRadius + 0.02, len);
    v3 *= smoothstep(1.0, innerRadius + ringWidth, len);
    // color
    col.a = max(v1, v2);
    col.a *= v3;
    col = clamp(col, 0.0, 1.0);
    //gl_FragColor = col;
    FragColor = col;
}

