    fragColor.rgb = mix(bg, col.rgb, col.a);
}

float bias( in float t, in float b) {
    return (t / ((((1.0 / b) - 2.0) * (1.0 - t)) + 1.0));
}

float gain( in float t, in float g ) {
	return t < 0.5 ? (bias(t * 2.0, g) * 0.5) : (0.5 + bias(t * 2.0 - 1.0, 1.0 - g) * 0.5);
}

