vec3 palette(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d)
{
    return a + b * cos(6.28318 * (c * t + d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	vec2 pt = 2.0 * uv - 1.0;
    float aspect = iResolution.x/iResolution.y;
    pt.x *= aspect;

    vec3 accumCol = vec3(0.0);
    float numSteps = 30.0;

    vec3 ac = vec3(0.5);
    vec3 bc = vec3(0.5);
    vec3 cc = vec3(1.0);
    vec3 dc = vec3(0.0, 0.333, 0.667);

    // 30 samples of motion blur
    for (int i = 0; i < 30; ++i){
    	float delta = (float(i) / (numSteps - 1.0) - 0.5) * .18;
        float time = 2.75 * iTime - delta;

        vec2 pos = pt;

        // rectangular motion from circular motion
        float sx = 0.5 + 0.5 * sin(3.14159 * time);
    	//sx = gain(sx, 0.1);
    	//sx = smoothstep(0.0, 1.0, sx);
        float sy = 0.5 + 0.5 * cos(3.14159 * time);
        //sy = gain(sy, 0.1);
        //sy = smoothstep(0.0, 1.0, sy);

         // base pauses then animates, used for color and rotation
        float rotationBase = floor(0.5 * time) + max(0.0, 8.0 * fract(0.5 * time) - 7.0); 
        float rotationTime = 0.875 * rotationBase * 2.0 * 3.1415962;
        //rotationTime = 0.0;
        float ct = cos(rotationTime);
        float st = sin(rotationTime);

        float scaleX = abs(sin(2.0 * 3.1415962 * time));
        //scaleX = smoothstep(0.0, 1.0, scaleX);

        // apply all transformations
        //pos = vec2(ct * pt.x - st * pt.y, ct * pt.y + st * pt.x);
        //pos.x /= 2.0;
        pos -= 0.5 * vec2(sx, sy) - 0.25;
        //pos.x *= 2.0;
        //pos /= 0.75 + 0.5 * scaleX;


        // pulse effect at end of rotation
        float pulse = 0.5 + 0.5 * sin((time + 0.4) * 3.14159);
        pulse = bias(pulse, 0.009);
        //pos /= 1.0 + 1.0 * pulse;
        float cp = cos(-0.25 * pulse * 3.1415962);
        float sp = sin(-0.25 * pulse * 3.1415962);
        //pos = vec2(cp * pos.x - sp * pos.y, cp * pos.y + sp * pos.x);

        accumCol +=  2.0 * step(length(pos) - 0.01, 0.05) / numSteps;
    }

    vec3 col = vec3(0.0);

    col = accumCol;
    // gamma correct to improve visibility
    col = pow(col, vec3(0.454545454545454545));

    // Output to screen
    fragColor = vec4(col,1.0);
}

