vec3 draw_star(vec2 pos, vec3 star_col) {
	pos -= FragCoord.xy / iResolution.x; 
	float d = length(pos) * 50.0;
	vec3 col, spectrum = star_col;
	col = spectrum / (d * d * d);

	// produce spikes
	d = length(pos * vec2(10., .2)) * 50.0;
	col += spectrum / (d * d * d);
	d = length(pos * vec2(.2, 10.)) * 50.0;
	col += spectrum / (d * d * d);

	return col;
}

