float circle(vec2 uv) {
    //uv *= 2.;
    if (length(uv)< 0.9) {
        if (abs(uv.y) < .1) return .8;
        return 0.3;
    }
    if (length(uv)< 1.) {
        return 1.;
    }
    return 0.;
    return clamp(length(uv)-8., 0., 1.);
    return bias(1.-(abs(length(uv) - 1. +.018) / .02), .8);
}
// Returns a unit-length vector at a given rotation.
// Analogous to e^(-i*theta)
// i.e. rotate (1,0) anticlockwise for theta radians. (+y means facing up)
vec2 e(float theta) {
    return vec2(cos(theta), sin(theta));
}
float monochrome(vec2 uv) {
    float height = 4.;

    const float cell_wd = 1.;
    vec2 inner_uv = mod(uv+cell_wd, 2.*cell_wd) - cell_wd;  // position within grid cell.
	vec2 outer_uv = floor((uv+cell_wd)/ 2.*cell_wd);      // index of grid cell.


    //vec2 angular_vel = vec2(outer_uv.yx);
    vec2 outer_hash_pos = outer_uv + vec2(1030., 0);
    float yaw = TAU * hash13(vec3(outer_hash_pos, 2.));
    float angular_speed = 3. + hash13(vec3(outer_hash_pos, 3.));
    vec2 angular_vel = angular_speed* vec2(cos(yaw), sin(yaw));
    //if ( outer_uv.y < -1.1) debug = cornflowerblue;
    float pitch = length(angular_vel) * (iTime+100.)*0.1;
    vec2 a = normalize(angular_vel);  // axis of rotation
    mat2 rot = mat2(a.x, a.y,
                    -a.y, a.x);
    inner_uv = rot * inner_uv;
    inner_uv.y /= cos(pitch);
    inner_uv = transpose(rot) * inner_uv;  // note: could be removed when our object is rotationally symmetric..
    inner_uv *= 1.5; // don't make the circles take up the entire inner square

    vec3 normal = vec3(0.0, sin(pitch), cos(pitch));
    mat3 rot3 = mat3(a.x, a.y, 0.,
                     -a.y, a.x, 0.,
                     0., 0., 1.);
    normal = transpose(rot3) * normal;
    const vec3 LIGHT_DIR = normalize(vec3(1.5, -1.0, 2.0));
    float light = 
        .2 + // ambient
        .8 * smoothstep(.98, .99, abs(dot(normal, LIGHT_DIR)));  // cheap (wrong-ish) specular

    float acc = 0.;
    acc += light * circle(inner_uv);

	return clamp(acc, 0., 1.);
}

float f(vec2 fragCoord) {
    vec2 uv = project(fragCoord);
    uv /= project(domsMouse.xy).xx / 3.;
    return monochrome(uv);
}

// antialiasing
float sampleSubpixel(vec2 pixels) {
	const int size = 1; 
	float disp = 1.0 / (float(size) + 2.0);
	float contrb = 0.0;
	float maxContrb = 0.0;
	for (int j = -size; j <= size; j++) {
		for (int i = -size; i <= size; i++) {
			contrb += f(pixels + vec2(float(i) * (disp / 3.0), float(j) * disp));
			maxContrb += 1.0;
		}
	}
	return contrb / maxContrb;
}

