const float Pi = 3.141592653589793;
const float OneThird = 2.0 * Pi/3.0;

vec4 drawLine(vec2 fragCoord, vec2 from, vec2 to, vec4 clrFrom, vec4 clrTo) {
    vec2 diff = to - from;
    float len = length(diff);
    vec2 localSpace = fragCoord - from;

    float dist = determinant(mat2(diff, localSpace)) / len;

    if (abs(dist) < 0.005) {
        float proj = dot(diff, localSpace) / len / len;

        if (proj >= 0.0 && proj <= 1.0)
	        return mix(clrFrom, clrTo, proj);
    }

    return vec4(0.0, 0.0, 0.0, 1.0);
}

